const initialInvestment = inputs.initialInvestment;
const discountRate = inputs.discountRate / 100; // Convert percentage to decimal
const annualCashFlows = inputs.annualCashFlows;
const numPeriods = inputs.numPeriods;
const salvageValue = inputs.salvageValue || 0;

// Basic input validation
if (initialInvestment < 0 || discountRate <= -1 || numPeriods < 1) {
    return {
        npv: null,
        irr: null,
        paybackPeriod: null
    };
}

// --- NPV Calculation ---
let npv = -initialInvestment;
for (let t = 1; t <= numPeriods; t++) {
    let cashFlowAtT = annualCashFlows;
    if (t === numPeriods) {
        cashFlowAtT += salvageValue;
    }
    if (1 + discountRate <= 0) { // Avoid division by zero or negative base for power
        npv = Infinity; // Or NaN, depending on desired error representation
        break;
    }
    npv += cashFlowAtT / Math.pow(1 + discountRate, t);
}

// --- IRR Calculation (Bisection Method) ---
let irrResult = null;
const calculateNPVForRate = (rate) => {
    let currentNPV = -initialInvestment;
    for (let t = 1; t <= numPeriods; t++) {
        let cashFlowAtT = annualCashFlows;
        if (t === numPeriods) {
            cashFlowAtT += salvageValue;
        }
        if (1 + rate <= 0) return Infinity; // Prevent division by zero or negative base for power
        currentNPV += cashFlowAtT / Math.pow(1 + rate, t);
    }
    return currentNPV;
};

let lowerBound = -0.99; // -99%
let upperBound = 5.0; // 500%
let foundSignChange = false;

// Check initial bounds for sign change
const npvAtLower = calculateNPVForRate(lowerBound);
const npvAtUpper = calculateNPVForRate(upperBound);

if (npvAtLower * npvAtUpper < 0) {
    foundSignChange = true;
} else {
    // Try to extend bounds if no sign change initially (e.g., all positive or all negative NPVs)
    if (npvAtLower > 0) { // NPV is positive, so IRR must be lower (more negative discount rate)
        let tempLower = lowerBound;
        for (let i = 0; i < 100; i++) {
            tempLower = Math.max(-0.999, tempLower - 0.01);
            if (calculateNPVForRate(tempLower) < 0) {
                lowerBound = tempLower;
                foundSignChange = true;
                break;
            }
        }
    } else if (npvAtUpper < 0) { // NPV is negative, so IRR must be higher
        let tempUpper = upperBound;
        for (let i = 0; i < 100; i++) {
            tempUpper = tempUpper + 0.05;
            if (tempUpper > 10.0) {break;}
            if (calculateNPVForRate(tempUpper) > 0) {
                upperBound = tempUpper;
                foundSignChange = true;
                break;
            }
        }
    }
}

if (foundSignChange) {
    const tolerance = 0.000001; // 0.0001%
    const maxIterations = 1000;
    for (let i = 0; i < maxIterations; i++) {
        if (upperBound - lowerBound < tolerance) {
            irrResult = (lowerBound + upperBound) / 2;
            break;
        }
        const midPoint = (lowerBound + upperBound) / 2;
        const npvAtMid = calculateNPVForRate(midPoint);

        if (npvAtMid > 0) {
            lowerBound = midPoint;
        } else {
            upperBound = midPoint;
        }
    }
    if (typeof irrResult === 'number') {
        irrResult = irrResult * 100; // Convert to percentage
    } else {
        irrResult = null;
    }
}

// --- Payback Period Calculation ---
let paybackPeriodResult = null;
if (annualCashFlows > 0) {
    let remainingInvestment = initialInvestment;
    let yearsToPayback = 0;

    for (let t = 1; t <= numPeriods; t++) {
        let currentCF = annualCashFlows;
        if (t === numPeriods) {
            currentCF += salvageValue;
        }

        if (remainingInvestment <= 0) {
            break;
        }

        if (currentCF <= 0) { // If cash flow becomes non-positive, payback is stalled or impossible
            paybackPeriodResult = null;
            break;
        }

        if (remainingInvestment <= currentCF) {
            yearsToPayback = t - 1 + (remainingInvestment / currentCF);
            remainingInvestment = 0;
            break;
        } else {
            remainingInvestment -= currentCF;
            yearsToPayback = t; // Full year passed
        }
    }

    if (remainingInvestment <= 0) {
        paybackPeriodResult = yearsToPayback;
    } else {
        paybackPeriodResult = null; // Indicate 'Never' or not paid back within project life
    }
}

// Ensure all results are numbers or null for consistency with precision
if (isNaN(npv)) npv = null;
if (isNaN(irrResult)) irrResult = null;
if (isNaN(paybackPeriodResult)) paybackPeriodResult = null;

return {
    npv: npv,
    irr: irrResult,
    paybackPeriod: paybackPeriodResult
};
