function calculate({ gender, age, weight, height, activityLevel, goal, proteinTargetGramPerKg, fatTargetPercentage }) {
  let bmr;
  // Mifflin-St Jeor Equation for BMR
  if (gender.toLowerCase() === 'male') {
    bmr = (10 * weight) + (6.25 * height) - (5 * age) + 5;
  } else if (gender.toLowerCase() === 'female') {
    bmr = (10 * weight) + (6.25 * height) - (5 * age) - 161;
  } else {
    throw new Error('Invalid gender. Please use "male" or "female".');
  }

  let activityFactor;
  switch (activityLevel.toLowerCase()) {
    case 'sedentary':
      activityFactor = 1.2;
      break;
    case 'lightly active':
      activityFactor = 1.375;
      break;
    case 'moderately active':
      activityFactor = 1.55;
      break;
    case 'very active':
      activityFactor = 1.725;
      break;
    case 'extra active':
      activityFactor = 1.9;
      break;
    default:
      throw new Error('Invalid activity level. Please choose from specified options.');
  }

  const tdee = bmr * activityFactor;

  let calorieAdjustment;
  switch (goal.toLowerCase()) {
    case 'maintain':
      calorieAdjustment = 0;
      break;
    case 'mild deficit':
      calorieAdjustment = -250;
      break;
    case 'moderate deficit':
      calorieAdjustment = -500;
      break;
    case 'aggressive deficit':
      calorieAdjustment = -750;
      break;
    case 'mild surplus':
      calorieAdjustment = 250;
      break;
    case 'moderate surplus':
      calorieAdjustment = 500;
      break;
    default:
      throw new Error('Invalid weight goal. Please choose from specified options.');
  }

  const targetCalories = Math.max(tdee + calorieAdjustment, 1200); // Cap target calories at 1200 for health reasons

  // Macronutrient calculation
  const proteinGrams = proteinTargetGramPerKg * weight;
  const proteinCalories = proteinGrams * 4;

  const fatCalories = targetCalories * (fatTargetPercentage / 100);
  const fatGrams = fatCalories / 9;

  let carbsCalories = targetCalories - proteinCalories - fatCalories;
  carbsCalories = Math.max(carbsCalories, 0); // Ensure carb calories are not negative
  const carbsGrams = carbsCalories / 4;

  // Input validation for percentages
  if (fatTargetPercentage < 0 || fatTargetPercentage > 100) {
    throw new Error('Fat target percentage must be between 0 and 100.');
  }

  return {
    tdee: tdee,
    targetCalories: targetCalories,
    proteinGrams: proteinGrams,
    carbsGrams: carbsGrams,
    fatGrams: fatGrams,
    proteinCalories: proteinCalories,
    carbsCalories: carbsCalories,
    fatCalories: fatCalories
  };
}
