function calculate({ ratedPower, numTurbines, capacityFactor, arrayLosses, gridLosses, availability, capitalCostPerMW, opexPerMWh, avgElectricityPrice, projectLifeYears }) {
    // Constants
    const hoursPerYear = 8760;
    const assumedDiscountRate = 0.07; // 7% for LCOE calculation, a reasonable industry average

    // Input validation and clamping
    ratedPower = Math.max(0.1, ratedPower || 0.1);
    numTurbines = Math.max(1, Math.floor(numTurbines || 1));
    capacityFactor = Math.min(100, Math.max(0, capacityFactor || 0));
    arrayLosses = Math.min(100, Math.max(0, arrayLosses || 0));
    gridLosses = Math.min(100, Math.max(0, gridLosses || 0));
    availability = Math.min(100, Math.max(0, availability || 0));
    capitalCostPerMW = Math.max(0, capitalCostPerMW || 0);
    opexPerMWh = Math.max(0, opexPerMWh || 0);
    avgElectricityPrice = Math.max(0, avgElectricityPrice || 0);
    projectLifeYears = Math.max(1, Math.floor(projectLifeYears || 1));

    // 1. Calculate Total Installed Capacity
    const totalRatedPowerMW = ratedPower * numTurbines;

    // 2. Calculate Gross Annual Energy Production
    const grossAnnualEnergyMWh = totalRatedPowerMW * hoursPerYear * (capacityFactor / 100);

    // 3. Apply Losses for Net Annual Energy Production
    // Ensure combined losses do not exceed 100%
    const combinedLossFactor = (1 - arrayLosses / 100) * (1 - gridLosses / 100) * (availability / 100);
    const netAnnualEnergyMWh = grossAnnualEnergyMWh * Math.max(0, combinedLossFactor);

    // Handle edge case: if net energy is negative or zero due to extreme losses/availability, cap it at 0.
    const finalNetAnnualEnergyMWh = Math.max(0, netAnnualEnergyMWh);

    // 4. Calculate Economic Metrics
    const totalCapitalCost = capitalCostPerMW * totalRatedPowerMW;
    const annualOpEx = finalNetAnnualEnergyMWh * opexPerMWh;
    const annualRevenue = finalNetAnnualEnergyMWh * avgElectricityPrice;

    // 5. Calculate Levelized Cost of Energy (LCOE) using Capital Recovery Factor
    let levelizedCostOfEnergy;
    if (assumedDiscountRate > 0 && projectLifeYears > 0) {
        const i = assumedDiscountRate;
        const n = projectLifeYears;
        const crf = (i * Math.pow(1 + i, n)) / (Math.pow(1 + i, n) - 1); // Capital Recovery Factor
        const annualFixedCosts = totalCapitalCost * crf;
        if (finalNetAnnualEnergyMWh > 0) {
            levelizedCostOfEnergy = (annualFixedCosts + annualOpEx) / finalNetAnnualEnergyMWh;
        } else {
            levelizedCostOfEnergy = Infinity; // Cannot produce energy, LCOE is infinite
        }
    } else if (finalNetAnnualEnergyMWh > 0) { // If discount rate is 0 or project life is 0, simple average
        levelizedCostOfEnergy = (totalCapitalCost / (finalNetAnnualEnergyMWh * projectLifeYears)) + opexPerMWh;
    } else {
        levelizedCostOfEnergy = Infinity; // Cannot produce energy, LCOE is infinite
    }
    if (totalCapitalCost === 0 && annualOpEx === 0) levelizedCostOfEnergy = 0; // No cost, LCOE is zero

    // 6. Calculate Simple Payback Period
    let simplePaybackYears = Infinity;
    const annualNetCashFlow = annualRevenue - annualOpEx;
    if (annualNetCashFlow > 0 && totalCapitalCost > 0) {
        simplePaybackYears = totalCapitalCost / annualNetCashFlow;
    } else if (totalCapitalCost === 0) {
        simplePaybackYears = 0; // If no initial cost, payback is immediate
    }

    // Cap payback period to a reasonable max if it's excessively long
    simplePaybackYears = Math.min(simplePaybackYears, 500);

    return {
        annualEnergyProduction: finalNetAnnualEnergyMWh,
        annualRevenue: annualRevenue,
        totalCapitalCost: totalCapitalCost,
        annualOperatingExpense: annualOpEx,
        levelizedCostOfEnergy: levelizedCostOfEnergy,
        simplePaybackPeriod: simplePaybackYears
    };
}
