const { waterTempC, currentSpeedMPS, marineLifeActivity, swimmerExperience, visibilityMeters, distanceToShoreM } = inputs;

// Input validation and clamping
const validatedWaterTempC = Math.max(0, Math.min(35, waterTempC));
const validatedCurrentSpeedMPS = Math.max(0, Math.min(2, currentSpeedMPS));
const validatedMarineLifeActivity = Math.max(1, Math.min(5, marineLifeActivity));
const validatedSwimmerExperience = Math.max(1, Math.min(5, swimmerExperience));
const validatedVisibilityMeters = Math.max(0.1, Math.min(10, visibilityMeters));
const validatedDistanceToShoreM = Math.max(10, Math.min(5000, distanceToShoreM));

// Calculate individual risk factors (each 1-5)
let tempRiskFactor;
if (validatedWaterTempC < 10) tempRiskFactor = 5; // Very cold
else if (validatedWaterTempC < 14) tempRiskFactor = 4; // Cold
else if (validatedWaterTempC < 18) tempRiskFactor = 3; // Cool
else if (validatedWaterTempC <= 28) tempRiskFactor = 1; // Optimal
else tempRiskFactor = 2; // Warm, risk of overheating/dehydration

const currentRiskFactor = Math.min(5, Math.max(1, validatedCurrentSpeedMPS * 10)); // 0m/s -> 1, 0.5m/s -> 5, 1m/s -> 5

const marineRiskFactor = validatedMarineLifeActivity;

const visibilityRiskFactor = Math.min(5, Math.max(1, 6 - validatedVisibilityMeters * 0.5)); // 10m -> 1, 1m -> 5, 0.1m -> 5

const distanceRiskFactor = Math.min(5, Math.max(1, validatedDistanceToShoreM / 1000 * 2.5)); // 10m -> 1, 2000m -> 5

// Sum base risks
const baseRisk = tempRiskFactor + currentRiskFactor + marineRiskFactor + visibilityRiskFactor + distanceRiskFactor;

// Apply swimmer experience as a mitigation factor (1=novice, 5=expert)
const experienceMitigation = (6 - validatedSwimmerExperience) / 5; // 1 (for exp 1) to 0.2 (for exp 5)

// Calculate weighted risk
const weightedRisk = baseRisk * experienceMitigation;

// Scale overall risk score to 1-10, clamping values
const overallRiskScore = Math.round(Math.min(10, Math.max(1, weightedRisk * 0.4)) * 10) / 10; // Max baseRisk=25, Max weightedRisk=25, 25*0.4=10

// Determine hypothermia risk
let hypothermiaRisk;
if (validatedWaterTempC < 14) hypothermiaRisk = 'High';
else if (validatedWaterTempC < 18) hypothermiaRisk = 'Medium';
else hypothermiaRisk = 'Low';

// Determine current danger level
let currentDangerLevel;
if (validatedCurrentSpeedMPS > 0.5) currentDangerLevel = 'High';
else if (validatedCurrentSpeedMPS > 0.2) currentDangerLevel = 'Medium';
else currentDangerLevel = 'Low';

// Generate safety recommendation
let recommendation;
if (overallRiskScore <= 3) {
  recommendation = 'Very Low Risk. Conditions are generally favorable for swimming, but always remain vigilant and aware of your surroundings.';
} else if (overallRiskScore <= 5) {
  recommendation = 'Low to Moderate Risk. Conditions are generally good, but be aware of potential factors. Consider swimming with a buddy and informing others of your plans.';
} else if (overallRiskScore <= 7) {
  recommendation = 'Moderate Risk. Exercise caution. Carefully consider your experience level and potential hazards. Swimming with a buddy is strongly recommended, and ensure someone on shore knows your estimated return time.';
} else if (overallRiskScore <= 9) {
  recommendation = 'High Risk. Significant hazards may be present. Strongly reconsider swimming or take extreme precautions. Only expert swimmers with full safety support should consider entering the water.';
} else {
  recommendation = 'Extreme Risk. Swimming is highly discouraged. Conditions are dangerous and pose a serious threat to safety. It is strongly advised to postpone your swim until conditions improve.';
}

return {
  overallRiskScore,
  hypothermiaRisk,
  currentDangerLevel,
  recommendation
};
