const netGainA = returnA - costA;
const netGainB = returnB - costB;

// Opportunity cost is the net gain of the best forgone option.
// If the chosen option is better, the opportunity cost of that specific choice is effectively zero, 
// as you didn't miss out on a better outcome.
const opportunityCost = Math.max(0, netGainB - netGainA);

return { netGainA, netGainB, opportunityCost };
