let netPremiumPerShare = inputs.buyPremium - inputs.sellPremium;
let totalNetPremium = netPremiumPerShare * inputs.contracts * inputs.multiplier;
let spreadWidth = Math.abs(inputs.buyStrike - inputs.sellStrike);
let P_L_at_Expiration_per_share = 0;
let maxProfit_per_share = 0;
let maxLoss_per_share = 0;
let breakevenPoint = 0;
let capitalRequired_val = 0;

const lowStrike = Math.min(inputs.buyStrike, inputs.sellStrike);
const highStrike = Math.max(inputs.buyStrike, inputs.sellStrike);
const isDebitStrategy = netPremiumPerShare > 0.0001; // Using a small epsilon to handle floating point comparisons

if (inputs.optionType === "Call") {
    if (isDebitStrategy) { // Call Debit Spread (e.g., Buy 100C, Sell 105C)
        P_L_at_Expiration_per_share = Math.min(
            Math.max(-netPremiumPerShare, inputs.underlyingAtExpiration - lowStrike - netPremiumPerShare),
            spreadWidth - netPremiumPerShare
        );
        maxProfit_per_share = spreadWidth - netPremiumPerShare;
        maxLoss_per_share = -netPremiumPerShare;
        breakevenPoint = lowStrike + netPremiumPerShare;
        capitalRequired_val = totalNetPremium;
    } else { // Call Credit Spread (e.g., Sell 105C, Buy 110C)
        const creditReceived = Math.abs(netPremiumPerShare);
        P_L_at_Expiration_per_share = Math.min(
            Math.max(-(spreadWidth - creditReceived), creditReceived - Math.max(0, inputs.underlyingAtExpiration - lowStrike)),
            creditReceived
        );
        maxProfit_per_share = creditReceived;
        maxLoss_per_share = -(spreadWidth - creditReceived);
        breakevenPoint = lowStrike + spreadWidth - creditReceived; 
        capitalRequired_val = (spreadWidth - creditReceived) * inputs.contracts * inputs.multiplier;
    }
} else { // Put
    if (isDebitStrategy) { // Put Debit Spread (e.g., Buy 105P, Sell 100P)
        P_L_at_Expiration_per_share = Math.min(
            Math.max(-netPremiumPerShare, highStrike - inputs.underlyingAtExpiration - netPremiumPerShare),
            spreadWidth - netPremiumPerShare
        );
        maxProfit_per_share = spreadWidth - netPremiumPerShare;
        maxLoss_per_share = -netPremiumPerShare;
        breakevenPoint = highStrike - netPremiumPerShare;
        capitalRequired_val = totalNetPremium;
    } else { // Put Credit Spread (e.g., Sell 100P, Buy 95P)
        const creditReceived = Math.abs(netPremiumPerShare);
        P_L_at_Expiration_per_share = Math.min(
            Math.max(-(spreadWidth - creditReceived), creditReceived - Math.max(0, highStrike - inputs.underlyingAtExpiration)),
            creditReceived
        );
        maxProfit_per_share = creditReceived;
        maxLoss_per_share = -(spreadWidth - creditReceived);
        breakevenPoint = highStrike - spreadWidth + creditReceived;
        capitalRequired_val = (spreadWidth - creditReceived) * inputs.contracts * inputs.multiplier;
    }
}

let profitOrLossAtExpiration = P_L_at_Expiration_per_share * inputs.contracts * inputs.multiplier;
let maxProfit = maxProfit_per_share * inputs.contracts * inputs.multiplier;
let maxLoss = maxLoss_per_share * inputs.contracts * inputs.multiplier;

return {
  netPremiumCost: totalNetPremium,
  maxProfit: maxProfit,
  maxLoss: maxLoss,
  breakevenPoint: breakevenPoint,
  profitOrLossAtExpiration: profitOrLossAtExpiration,
  capitalRequired: capitalRequired_val
};
