function calculate() {
  let score = 100;
  let specificWarnings = [];

  const { 
    forecastedHighTempC, forecastedLowTempC, precipitationProbability,
    windSpeedKmh, uvIndex, activityType, durationHours,
    remotenessLevel, experienceLevel, gearPreparedness
  } = inputs;

  // --- Input Validation and Defaulting ---
  if (isNaN(forecastedHighTempC) || isNaN(forecastedLowTempC) || isNaN(precipitationProbability) || 
      isNaN(windSpeedKmh) || isNaN(uvIndex) || isNaN(durationHours) ||
      forecastedHighTempC === null || forecastedLowTempC === null || precipitationProbability === null ||
      windSpeedKmh === null || uvIndex === null || durationHours === null) {
    return { safetyScore: null, riskCategory: 'Invalid Input', recommendation: 'Please ensure all numerical fields are filled correctly.' };
  }

  // Cap precipitation probability between 0 and 100
  const precipProb = Math.max(0, Math.min(100, precipitationProbability));

  // --- 1. Weather Condition Deductions ---

  // Temperature (Ideal 10-25°C, tolerates 5-30°C with minor deductions)
  if (forecastedHighTempC > 30) {
    score -= (forecastedHighTempC - 30) * 1.5; // >30 gets penalized more heavily
    specificWarnings.push('High temperatures increase dehydration and heat stroke risk.');
  } else if (forecastedHighTempC < 10) {
    score -= (10 - forecastedHighTempC) * 1; // <10 penalized
  }

  if (forecastedLowTempC < 5) {
    score -= (5 - forecastedLowTempC) * 2; // <5 penalized more heavily due to hypothermia risk
    if (forecastedLowTempC <= 0) specificWarnings.push('Freezing temperatures pose hypothermia and frostbite risks, and complicate water sources.');
  }

  const tempRange = forecastedHighTempC - forecastedLowTempC;
  if (tempRange > 15) {
    score -= (tempRange - 15) * 0.5;
    specificWarnings.push('Large temperature swings require diverse clothing layers.');
  }

  // Precipitation Probability
  if (precipProb > 20) {
    score -= (precipProb - 20) * 0.4; // up to 32 points for 100% precip
    if (precipProb >= 50) specificWarnings.push('High chance of precipitation significantly increases discomfort and risk of hypothermia.');
  }

  // Wind Speed (km/h) - Converts to approximate Beaufort scale for impact
  // 0-15 (Light): 0 deduction
  // 16-30 (Moderate): 0.5 deduction per km/h over 15
  // 31-50 (Strong): 1.0 deduction per km/h over 30
  // >50 (Gale): 2.0 deduction per km/h over 50
  if (windSpeedKmh > 15) {
    let windDeduction = 0;
    if (windSpeedKmh > 50) {
      windDeduction += (windSpeedKmh - 50) * 2.0;
      specificWarnings.push('Strong winds (gale force or higher) drastically increase perceived cold and physical effort, and can cause instability.');
    }
    if (windSpeedKmh > 30) {
      windDeduction += (Math.min(windSpeedKmh, 50) - 30) * 1.0;
      specificWarnings.push('Strong winds can make movement difficult and increase wind chill.');
    }
    if (windSpeedKmh > 15) {
      windDeduction += (Math.min(windSpeedKmh, 30) - 15) * 0.5;
    }
    score -= windDeduction;
  }

  // UV Index
  if (uvIndex >= 3) {
    score -= (uvIndex - 2) * 1.5; // up to 13.5 points for UV 11
    if (uvIndex >= 6) specificWarnings.push('High UV index requires stringent sun protection.');
  }

  // --- 2. Activity-Specific Risk Deductions ---
  let activityRiskFactor = 0;
  let activityRisks = new Set();

  const activityMap = {
    'leisure walk': 0,
    'day hike (moderate)': 8,
    'strenuous hike': 15,
    'mountain biking': 20,
    'rock climbing': 30,
    'water sports': 12,
    'backcountry skiing': 35
  };
  activityRiskFactor = activityMap[activityType.toLowerCase()] || 10; // Default to moderate risk if not found
  score -= activityRiskFactor;
  if (activityRiskFactor > 15) activityRisks.add('The chosen activity inherently carries significant risks.');

  // Duration
  if (durationHours > 3) {
    score -= (durationHours - 3) * 1.5;
    if (durationHours >= 8) activityRisks.add('Long duration activities increase exposure to changing conditions and fatigue.');
  }

  // Remoteness
  const remotenessMap = {
    'urban park': 0,
    'developed trailhead': 7,
    'backcountry (day)': 18,
    'remote wilderness (multi-day)': 30
  };
  score -= remotenessMap[remotenessLevel.toLowerCase()] || 15; // Default to backcountry (day) risk
  if (remotenessMap[remotenessLevel.toLowerCase()] >= 18) activityRisks.add('High remoteness significantly complicates rescue efforts and requires self-sufficiency.');

  // --- 3. Participant Preparation Adjustments ---

  // Experience Level
  const experienceMap = {
    'novice': -15,
    'intermediate': 0,
    'experienced': 5,
    'expert': 10
  };
  score += experienceMap[experienceLevel.toLowerCase()] || 0;
  if (experienceMap[experienceLevel.toLowerCase()] === -15) specificWarnings.push('Novice participants should consider easier routes or guided trips.');

  // Gear Preparedness
  const gearMap = {
    'inadequate': -25,
    'basic': -5,
    'standard': 5,
    'advanced': 10
  };
  score += gearMap[gearPreparedness.toLowerCase()] || 0;
  if (gearMap[gearPreparedness.toLowerCase()] <= -5) specificWarnings.push('Inadequate or basic gear significantly increases risks in adverse conditions.');

  // --- Final Score Calculation and Category Assignment ---

  score = Math.max(0, Math.min(100, Math.round(score)));

  let riskCategory;
  let recommendation = '';

  if (score >= 80) {
    riskCategory = 'Low Risk';
    recommendation = 'Conditions appear highly favorable. Enjoy your activity, but always stay vigilant for changes.';
  } else if (score >= 60) {
    riskCategory = 'Moderate Risk';
    recommendation = 'Exercise caution. Review weather updates, ensure adequate gear and experience for the conditions. Be prepared to adjust plans.';
  } else if (score >= 40) {
    riskCategory = 'High Risk';
    recommendation = 'Significant risks are present. Strongly reconsider your activity, or postpone. If proceeding, ensure expert-level preparation, gear, and a solid emergency plan. Avoid solo excursions.';
  } else {
    riskCategory = 'Extreme Risk';
    recommendation = 'Dangerous conditions. It is strongly advised to cancel or postpone your activity. Proceeding would be extremely risky, even for highly experienced individuals.';
  }

  // Append specific warnings if any
  if (specificWarnings.length > 0 || activityRisks.size > 0) {
      recommendation += ' Specific considerations: ' + [...specificWarnings, ...activityRisks].join(' ');
  }

  return {
    safetyScore: score,
    riskCategory: riskCategory,
    recommendation: recommendation
  };
}
return calculate();
