function calculate(inputs) {
  // Input validation and default values
  const eventName = inputs.event_name;
  const eventStartDate = new Date(inputs.event_start_date);
  const eventDurationHours = Math.max(1, parseFloat(inputs.event_duration_hours) || 0);
  const totalEventBudget = Math.max(1000, parseFloat(inputs.total_event_budget_usd) || 0);
  const expectedAttendees = Math.max(1, parseInt(inputs.expected_attendees) || 0);
  const avgRevenuePerAttendee = Math.max(0, parseFloat(inputs.avg_revenue_per_attendee_usd) || 0);
  const criticalRainThreshold = Math.max(0.1, parseFloat(inputs.critical_rain_mm_hr) || 0);
  const criticalWindThreshold = Math.max(10, parseFloat(inputs.critical_wind_km_hr) || 0);
  const riskTolerance = inputs.risk_tolerance_level || 'Medium';

  // --- Step 1: Simulate Baseline Weather Risk --- (Simplified for this demo)
  // In a real application, this would involve API calls to fetch historical data and forecasts
  // based on location (city, country, lat/lon) and date.

  const month = eventStartDate.getMonth(); // 0 for Jan, 11 for Dec
  let baseRiskProbability = 0.05; // Starting base risk

  // Adjust base risk by month (e.g., higher in peak storm seasons, lower in stable months)
  // This is a very generalized, illustrative adjustment
  if (month >= 5 && month <= 9) { // June-October (summer/fall in Northern Hemisphere)
    baseRiskProbability += 0.08; // Higher risk for heat, thunderstorms, some rain/wind
  } else if (month >= 11 || month <= 2) { // Dec-March (winter)
    baseRiskProbability += 0.04; // Higher risk for cold, snow, specific wind patterns
  } else { // Spring/Autumn shoulders
    baseRiskProbability += 0.03;
  }

  // Adjust risk based on critical thresholds (lower threshold implies higher risk)
  const rainSensitivityFactor = (10 - Math.min(10, criticalRainThreshold)) / 10; // 0-1 range
  const windSensitivityFactor = (60 - Math.min(60, criticalWindThreshold)) / 60; // 0-1 range, up to 60 km/hr

  baseRiskProbability += (rainSensitivityFactor * 0.05) + (windSensitivityFactor * 0.04);

  // Adjust for event duration (longer events have higher cumulative risk)
  const durationFactor = Math.min(2, eventDurationHours / 24); // Cap at 2x for multi-day
  baseRiskProbability *= (1 + durationFactor * 0.1);

  // Cap base risk probability
  baseRiskProbability = Math.min(0.75, Math.max(0.05, baseRiskProbability));
  const baselineRiskScore = Math.round(baseRiskProbability * 100);

  // --- Step 2: Calculate Estimated Unmitigated Loss --- 
  const totalPotentialRevenue = expectedAttendees * avgRevenuePerAttendee;
  const potentialAttendeeLossPercentage = baseRiskProbability * 0.8; // 80% of risk leads to attendee loss factor
  const lostAttendees = Math.round(expectedAttendees * potentialAttendeeLossPercentage * 0.6); // 60% of at-risk attendees actually lost
  const lostRevenueFromAttendees = lostAttendees * avgRevenuePerAttendee;

  // Estimate disruption costs as a percentage of budget
  const eventDisruptionCost = totalEventBudget * baseRiskProbability * 0.3; // 30% of risk translates to budget disruption

  let estimatedUnmitigatedLoss = lostRevenueFromAttendees + eventDisruptionCost;
  // Cap unmitigated loss at total budget + total potential revenue for realism
  estimatedUnmitigatedLoss = Math.min(estimatedUnmitigatedLoss, totalEventBudget + totalPotentialRevenue);

  // --- Step 3: Determine Mitigation Strategy and Costs --- 
  const mitigationCostsAndReductions = {
    'Low': { cost_factor: 0.15, reduction_factor: 0.70 },   // Robust: 15% of budget, 70% risk reduction
    'Medium': { cost_factor: 0.08, reduction_factor: 0.45 }, // Balanced: 8% of budget, 45% risk reduction
    'High': { cost_factor: 0.03, reduction_factor: 0.20 }    // Minimal: 3% of budget, 20% risk reduction
  };

  const selectedStrategy = mitigationCostsAndReductions[riskTolerance];
  const suggestedMitigationCost = totalEventBudget * selectedStrategy.cost_factor;

  // --- Step 4: Calculate Residual Risk, Potential Savings, and ROI --- 
  const residualRiskProbability = baseRiskProbability * (1 - selectedStrategy.reduction_factor);
  const residualRiskScoreMitigated = Math.round(Math.max(0, residualRiskProbability * 100)); // Cannot be negative

  const mitigatedLoss = estimatedUnmitigatedLoss * (1 - selectedStrategy.reduction_factor);
  const potentialSavingsFromMitigation = estimatedUnmitigatedLoss - mitigatedLoss;

  let roiOfMitigation = 0;
  if (suggestedMitigationCost > 0) {
    roiOfMitigation = ((potentialSavingsFromMitigation - suggestedMitigationCost) / suggestedMitigationCost) * 100;
  }

  return {
    baseline_risk_score: baselineRiskScore,
    estimated_unmitigated_loss_usd: Math.round(estimatedUnmitigatedLoss),
    suggested_mitigation_cost_usd: Math.round(suggestedMitigationCost),
    residual_risk_score_mitigated: residualRiskScoreMitigated,
    potential_savings_from_mitigation_usd: Math.round(potentialSavingsFromMitigation),
    roi_of_mitigation_percent: parseFloat(roiOfMitigation.toFixed(1))
  };
}
