const curativeMedicalCostTotal = inputs.curativeTreatmentDurationMonths * Math.max(0, inputs.curativeMonthlyMedicalSpend);
const palliativeMedicalCostTotal = inputs.palliativeCareDurationMonths * Math.max(0, inputs.palliativeMonthlyMedicalSpend);

const oopShare = Math.max(0, Math.min(1, inputs.outOfPocketInsuranceShare / 100)); // Ensure between 0 and 1, convert %

const patientCurativeMedicalCost = curativeMedicalCostTotal * oopShare;
const patientPalliativeMedicalCost = palliativeMedicalCostTotal * oopShare;

const totalPatientCurativeCost = patientCurativeMedicalCost + (inputs.curativeTreatmentDurationMonths * Math.max(0, inputs.monthlySupportServicesCostCurative));
const totalPatientPalliativeCost = patientPalliativeMedicalCost + (inputs.palliativeCareDurationMonths * Math.max(0, inputs.monthlySupportServicesCostPalliative));

const costDifference = totalPatientCurativeCost - totalPatientPalliativeCost;

return {
  totalCurativeCost: totalPatientCurativeCost,
  totalPalliativeCost: totalPatientPalliativeCost,
  costDifference: costDifference
};
