const sex = inputs.sex;
const age_months = parseFloat(inputs.age_months);
const weight_kg = parseFloat(inputs.weight_kg);
const height_cm = parseFloat(inputs.height_cm);
const head_circ_cm = parseFloat(inputs.head_circ_cm);

// Simplified Growth Data - THIS IS ILLUSTRATIVE AND NOT MEDICALLY ACCURATE.
// Real growth charts use complex datasets (e.g., CDC, WHO) that vary by age, sex, and often include LMS parameters.
const growth_data = {
    'male': {
        'weight': [
            { age_max: 12, median: 8.5, sd: 1.5 }, // 0-12 months
            { age_max: 60, median: 18, sd: 3 },    // 13-60 months
            { age_max: 120, median: 30, sd: 6 },   // 61-120 months
            { age_max: 240, median: 60, sd: 12 }   // 121-240 months
        ],
        'height': [
            { age_max: 12, median: 74, sd: 3 },
            { age_max: 60, median: 110, sd: 5 },
            { age_max: 120, median: 138, sd: 7 },
            { age_max: 240, median: 175, sd: 8 }
        ],
        'head_circ': [
            { age_max: 12, median: 47, sd: 1.5 },
            { age_max: 60, median: 50, sd: 1.2 },
            { age_max: 120, median: 53, sd: 1 },
            { age_max: 240, median: 55, sd: 1 }
        ]
    },
    'female': {
        'weight': [
            { age_max: 12, median: 7.8, sd: 1.3 },
            { age_max: 60, median: 17, sd: 2.8 },
            { age_max: 120, median: 28, sd: 5.5 },
            { age_max: 240, median: 50, sd: 10 }
        ],
        'height': [
            { age_max: 12, median: 72, sd: 2.8 },
            { age_max: 60, median: 108, sd: 4.8 },
            { age_max: 120, median: 136, sd: 6.5 },
            { age_max: 240, median: 163, sd: 7 }
        ],
        'head_circ': [
            { age_max: 12, median: 46, sd: 1.4 },
            { age_max: 60, median: 49, sd: 1.1 },
            { age_max: 120, median: 52, sd: 0.9 },
            { age_max: 240, median: 54, sd: 0.9 }
        ]
    }
};

// Helper function to get relevant growth data for a given age and sex
function getGrowthRef(sex, age_months, measurement_type) {
    const sex_key = sex.toLowerCase();
    if (sex_key !== 'male' && sex_key !== 'female') return null;

    const sex_data = growth_data[sex_key];
    if (!sex_data) return null;

    const type_data = sex_data[measurement_type];
    if (!type_data) return null;

    for (const range of type_data) {
        if (age_months <= range.age_max) {
            return range;
        }
    }
    return null; // Age out of range or no data
}

// Helper function to convert Z-score to approximate percentile
// This is a simplified approximation for general purposes and not a precise statistical function.
function zScoreToPercentile(z) {
    if (z <= -2.5) return 0.5;
    if (z <= -2.0) return 2;
    if (z <= -1.5) return 7;
    if (z <= -1.0) return 16;
    if (z <= -0.5) return 31;
    if (z <= 0) return 50;
    if (z <= 0.5) return 69;
    if (z <= 1.0) return 84;
    if (z <= 1.5) return 93;
    if (z <= 2.0) return 98;
    if (z <= 2.5) return 99.5;
    return 99.9;
}

let weight_percentile = 'N/A';
let height_percentile = 'N/A';
let head_circ_percentile = 'N/A';

if (sex && age_months >= 0 && weight_kg > 0 && height_cm > 0 && head_circ_cm > 0) {
    // Calculate Weight Percentile
    const weight_ref = getGrowthRef(sex, age_months, 'weight');
    if (weight_ref) {
        const z_weight = (weight_kg - weight_ref.median) / weight_ref.sd;
        weight_percentile = zScoreToPercentile(z_weight);
    }

    // Calculate Height Percentile
    const height_ref = getGrowthRef(sex, age_months, 'height');
    if (height_ref) {
        const z_height = (height_cm - height_ref.median) / height_ref.sd;
        height_percentile = zScoreToPercentile(z_height);
    }

    // Calculate Head Circumference Percentile
    const head_circ_ref = getGrowthRef(sex, age_months, 'head_circ');
    if (head_circ_ref) {
        const z_head_circ = (head_circ_cm - head_circ_ref.median) / head_circ_ref.sd;
        head_circ_percentile = zScoreToPercentile(z_head_circ);
    }
}

return {
    weight_percentile,
    height_percentile,
    head_circ_percentile
};
