const P = inputs.payment_amount;
const r_percent = inputs.discount_rate;
const g_percent = inputs.growth_rate !== undefined ? inputs.growth_rate : 0; 

const r = r_percent / 100;
const g = g_percent / 100;

let present_value;
let effective_discount_rate_used;

if (r <= g) {
    present_value = Infinity; // Mathematically, an infinite value or undefined in this scenario
    effective_discount_rate_used = r_percent; // Still show the discount rate for context
} else if (g === 0) {
    // Constant perpetuity
    present_value = P / r;
    effective_discount_rate_used = r_percent;
} else {
    // Growing perpetuity
    present_value = P / (r - g);
    effective_discount_rate_used = (r - g) * 100; 
}

return {
    present_value: present_value,
    effective_discount_rate_used: effective_discount_rate_used
};
