formula:
const monthly_net_income = inputs.monthly_net_income || 0;
const total_monthly_expenses = inputs.total_monthly_expenses || 0;
const savings_goal_amount = inputs.savings_goal_amount || 0;
const savings_goal_target_months = inputs.savings_goal_target_months || 1; // Avoid division by zero if not handled carefully
const initial_savings_balance = inputs.initial_savings_balance || 0;
const annual_interest_rate = inputs.annual_interest_rate || 0;

// Basic calculations
const disposable_income = monthly_net_income - total_monthly_expenses;
const potential_monthly_savings = Math.max(0, disposable_income); // Cannot save negative money

let required_monthly_contribution_for_target = 0;
let savings_target_met_in_target_time = 'No';
const remaining_goal_initial = savings_goal_amount - initial_savings_balance;

// Calculate required monthly contribution for target duration (without interest)
if (remaining_goal_initial <= 0) {
    required_monthly_contribution_for_target = 0; // Goal already met or exceeded
    savings_target_met_in_target_time = 'Yes';
} else if (savings_goal_target_months <= 0) {
    required_monthly_contribution_for_target = remaining_goal_initial; // Must save all now if target months is 0 or less
} else {
    required_monthly_contribution_for_target = remaining_goal_initial / savings_goal_target_months;
}

const monthly_shortfall_or_surplus = disposable_income - required_monthly_contribution_for_target;

// Determine if target can be met in target time with current budget
if (remaining_goal_initial <= 0) {
    savings_target_met_in_target_time = 'Yes';
} else if (monthly_shortfall_or_surplus >= 0) {
    savings_target_met_in_target_time = 'Yes';
} else {
    savings_target_met_in_target_time = 'No';
}

let projected_months_to_goal;
let total_interest_earned = 0;

if (savings_goal_amount <= initial_savings_balance) {
    // Goal already met or exceeded
    projected_months_to_goal = 0;
    total_interest_earned = 0;
} else if (potential_monthly_savings <= 0) {
    // Cannot save anything, goal will never be met
    projected_months_to_goal = Infinity;
    total_interest_earned = 0;
} else {
    const monthly_rate = annual_interest_rate / 100 / 12;
    if (monthly_rate === 0) {
        // No interest, simple calculation
        projected_months_to_goal = (savings_goal_amount - initial_savings_balance) / potential_monthly_savings;
        total_interest_earned = 0; // No interest earned
    } else {
        // With interest, solve for n using the future value of annuity formula adjusted for initial balance
        // FV = PV * (1 + i)^n + PMT * (((1 + i)^n - 1) / i)
        // Rearranging to solve for n: n = log((FV * i + PMT) / (PV * i + PMT)) / log(1 + i)

        const log_num_arg = (savings_goal_amount * monthly_rate + potential_monthly_savings);
        const log_den_arg = (initial_savings_balance * monthly_rate + potential_monthly_savings);

        if (log_den_arg <= 0) {
            // This implies a scenario where initial_savings_balance and potential_monthly_savings are zero or negative relative to interest
            // which would mean the goal is likely unreachable if savings_goal_amount is positive.
            projected_months_to_goal = Infinity;
            total_interest_earned = 0;
        } else if (log_num_arg <= 0) {
            // This case implies goal amount is zero or negative, which is already handled above by savings_goal_amount <= initial_savings_balance
            // Or it implies other parameters leading to an impossible log argument, so it's likely unreachable.
            projected_months_to_goal = Infinity;
            total_interest_earned = 0;
        } else {
            projected_months_to_goal = Math.log(log_num_arg / log_den_arg) / Math.log(1 + monthly_rate);

            // Calculate total interest earned if goal is reached
            if (isFinite(projected_months_to_goal) && projected_months_to_goal > 0) {
                const total_contributed_principal = initial_savings_balance + (potential_monthly_savings * projected_months_to_goal);
                // The actual future value will be precisely savings_goal_amount if projected_months_to_goal is accurate.
                total_interest_earned = Math.max(0, savings_goal_amount - total_contributed_principal);
            } else {
                 total_interest_earned = 0;
            }
        }
    }
}

return {
    disposable_income: disposable_income,
    potential_monthly_savings: potential_monthly_savings,
    required_monthly_contribution_for_target: required_monthly_contribution_for_target,
    monthly_shortfall_or_surplus: monthly_shortfall_or_surplus,
    projected_months_to_goal: projected_months_to_goal,
    total_interest_earned: total_interest_earned,
    savings_target_met_in_target_time: savings_target_met_in_target_time
};
