const RED_MEAT_EMISSION_FACTOR_KG_CO2E_PER_SERVING = 2.8; // Approx. 200g serving * 14 kgCO2e/kg
const GASOLINE_EMISSION_FACTOR_KG_CO2E_PER_LITER = 2.3; // Standard factor for gasoline
const ELECTRICITY_EMISSION_FACTOR_KG_CO2E_PER_KWH = 0.233; // EU 2022 average, varies by grid

let dietCarbonSavings = 0;
let transportCarbonSavings = 0;
let transportFinancialSavings = 0;
let energyCarbonSavings = 0;
let energyFinancialSavings = 0;

// Input validation and clamping
const currentWeeklyRedMeatServings = Math.max(0, parseFloat(inputs.currentWeeklyRedMeatServings) || 0);
const targetWeeklyRedMeatServings = Math.max(0, parseFloat(inputs.targetWeeklyRedMeatServings) || 0);
const currentAnnualCarKm = Math.max(0, parseFloat(inputs.currentAnnualCarKm) || 0);
const targetAnnualCarKm = Math.max(0, parseFloat(inputs.targetAnnualCarKm) || 0);
const carFuelEfficiencyLitersPer100Km = Math.max(0.1, parseFloat(inputs.carFuelEfficiencyLitersPer100Km) || 0.1); // Avoid division by zero
const fuelCostPerLiter = Math.max(0, parseFloat(inputs.fuelCostPerLiter) || 0);
const currentAnnualElectricityKWh = Math.max(0, parseFloat(inputs.currentAnnualElectricityKWh) || 0);
const targetAnnualElectricityKWh = Math.max(0, parseFloat(inputs.targetAnnualElectricityKWh) || 0);
const electricityCostPerKWh = Math.max(0, parseFloat(inputs.electricityCostPerKWh) || 0);

// 1. Dietary Impact Calculation
if (targetWeeklyRedMeatServings < currentWeeklyRedMeatServings) {
  const redMeatReductionServingsPerYear = (currentWeeklyRedMeatServings - targetWeeklyRedMeatServings) * 52;
  dietCarbonSavings = redMeatReductionServingsPerYear * RED_MEAT_EMISSION_FACTOR_KG_CO2E_PER_SERVING;
}

// 2. Transportation Impact Calculation
if (targetAnnualCarKm < currentAnnualCarKm) {
  const carKmReduction = currentAnnualCarKm - targetAnnualCarKm;
  const fuelSavedLiters = (carKmReduction / 100) * carFuelEfficiencyLitersPer100Km;
  transportCarbonSavings = fuelSavedLiters * GASOLINE_EMISSION_FACTOR_KG_CO2E_PER_LITER;
  transportFinancialSavings = fuelSavedLiters * fuelCostPerLiter;
}

// 3. Home Energy Impact Calculation
if (targetAnnualElectricityKWh < currentAnnualElectricityKWh) {
  const electricityKWhReduction = currentAnnualElectricityKWh - targetAnnualElectricityKWh;
  energyCarbonSavings = electricityKWhReduction * ELECTRICITY_EMISSION_FACTOR_KG_CO2E_PER_KWH;
  energyFinancialSavings = electricityKWhReduction * electricityCostPerKWh;
}

// Total Calculations
const totalCarbonSavingsKgCo2e = dietCarbonSavings + transportCarbonSavings + energyCarbonSavings;
const totalFinancialSavings = transportFinancialSavings + energyFinancialSavings;

return {
  dietCarbonSavingsKgCo2e: dietCarbonSavings,
  transportCarbonSavingsKgCo2e: transportCarbonSavings,
  energyCarbonSavingsKgCo2e: energyCarbonSavings,
  transportFinancialSavings: transportFinancialSavings,
  energyFinancialSavings: energyFinancialSavings,
  totalCarbonSavingsKgCo2e: totalCarbonSavingsKgCo2e,
  totalFinancialSavings: totalFinancialSavings
};
