/**
 * Calculates the personal data breach risk based on user inputs.
 * @param {object} inputs - The input values from the calculator form.
 * @param {string} inputs.digital_footprint_level - User's online activity level.
 * @param {string} inputs.security_awareness_level - User's security knowledge level.
 * @param {number} inputs.num_online_accounts - Approximate number of active online accounts.
 * @param {number} inputs.mfa_coverage_percent - Percentage of accounts with MFA.
 * @param {string} inputs.software_update_frequency - Frequency of software updates.
 * @param {string} inputs.phishing_training_status - Phishing training status.
 * @param {string} inputs.data_sensitivity_level - Highest sensitivity of data handled.
 * @param {string} inputs.past_breach_exposure_checked - Status of past breach exposure check.
 * @param {string} inputs.public_wifi_usage_frequency - Frequency of public Wi-Fi usage.
 * @returns {object} An object containing breach_likelihood_score, potential_impact_score, and overall_risk_level.
 */
function calculateRisk(inputs) {
  const footprint_map = {
    "Minimal": 1,
    "Moderate": 3,
    "Extensive": 7,
    "Very Extensive": 10
  };
  const awareness_map = {
    "Basic": 10,
    "Intermediate": 7,
    "Advanced": 4,
    "Expert": 1
  };
  const software_map = {
    "Daily/Automatic": 1,
    "Weekly": 3,
    "Monthly": 5,
    "Quarterly": 8,
    "Rarely/Never": 10
  };
  const phishing_map = {
    "Yes": 1,
    "No": 8,
    "Not Applicable": 3
  };
  const sensitivity_map = {
    "General Public Data": 1,
    "Personal Identifiable Information (PII)": 4,
    "Financial Records": 7,
    "Health Records (PHI)": 9,
    "Confidential Business Data": 10
  };
  const past_breach_map = {
    "Yes, and remediated": 2,
    "Yes, but not fully remediated": 9,
    "No, not checked": 6,
    "Unsure": 7
  };
  const public_wifi_map = {
    "Never": 1,
    "Rarely": 3,
    "Sometimes": 6,
    "Often": 8,
    "Very Often": 10
  };

  // Convert inputs to numerical values, with defaults for robustness
  const footprint_val = footprint_map[inputs.digital_footprint_level] || 5;
  const awareness_val = awareness_map[inputs.security_awareness_level] || 5;
  const num_online_accounts = Number(inputs.num_online_accounts) || 0;
  const mfa_coverage_percent = Number(inputs.mfa_coverage_percent) || 0;
  const software_val = software_map[inputs.software_update_frequency] || 5;
  const phishing_val = phishing_map[inputs.phishing_training_status] || 4;
  const sensitivity_val = sensitivity_map[inputs.data_sensitivity_level] || 5;
  const past_breach_val = past_breach_map[inputs.past_breach_exposure_checked] || 5;
  const public_wifi_val = public_wifi_map[inputs.public_wifi_usage_frequency] || 5;

  // --- Likelihood Score Calculation ---
  let raw_likelihood_score = 0;
  raw_likelihood_score += footprint_val * 3; // Weight 3
  raw_likelihood_score += awareness_val * 2; // Weight 2 (higher value means less awareness = more risk)
  raw_likelihood_score += Math.min(num_online_accounts / 5, 20); // Caps at 20 for 100+ accounts. Weight 1
  raw_likelihood_score += (100 - mfa_coverage_percent) / 5; // Lower MFA coverage = higher risk. Weight 1
  raw_likelihood_score += software_val * 2; // Weight 2
  raw_likelihood_score += phishing_val * 3; // Weight 3
  raw_likelihood_score += public_wifi_val * 2; // Weight 2
  raw_likelihood_score += past_breach_val * 4; // Weight 4 (significant risk factor)

  // Min/Max for raw_likelihood_score (derived from input ranges and weights)
  const MIN_RAW_LIKELIHOOD = (1 * 3) + (1 * 2) + Math.min(5 / 5, 20) + ((100 - 100) / 5) + (1 * 2) + (1 * 3) + (1 * 2) + (2 * 4); // 3 + 2 + 1 + 0 + 2 + 3 + 2 + 8 = 21
  const MAX_RAW_LIKELIHOOD = (10 * 3) + (10 * 2) + Math.min(200 / 5, 20) + ((100 - 0) / 5) + (10 * 2) + (8 * 3) + (10 * 2) + (9 * 4); // 30 + 20 + 20 + 20 + 20 + 24 + 20 + 36 = 190

  let breach_likelihood_score = ((raw_likelihood_score - MIN_RAW_LIKELIHOOD) / (MAX_RAW_LIKELIHOOD - MIN_RAW_LIKELIHOOD)) * 100;
  breach_likelihood_score = Math.max(0, Math.min(100, Math.round(breach_likelihood_score)));

  // --- Potential Impact Score Calculation ---
  let raw_impact_score = 0;
  raw_impact_score += sensitivity_val * 5; // Weight 5 (very important)
  raw_impact_score += Math.min(num_online_accounts / 10, 10); // More accounts mean more data potentially exposed. Weight 1, caps at 10 for 100+ accounts
  raw_impact_score += past_breach_val * 3; // Weight 3 (prior exposure increases potential impact if not fully remediated)

  // Min/Max for raw_impact_score (derived from input ranges and weights)
  const MIN_RAW_IMPACT = (1 * 5) + Math.min(5 / 10, 10) + (2 * 3); // 5 + 0.5 + 6 = 11.5
  const MAX_RAW_IMPACT = (10 * 5) + Math.min(200 / 10, 10) + (9 * 3); // 50 + 10 + 27 = 87

  let potential_impact_score = ((raw_impact_score - MIN_RAW_IMPACT) / (MAX_RAW_IMPACT - MIN_RAW_IMPACT)) * 100;
  potential_impact_score = Math.max(0, Math.min(100, Math.round(potential_impact_score)));

  // --- Overall Risk Level Determination ---
  let overall_risk_level;
  const high_threshold = 70;
  const medium_threshold = 40;

  if ((
      breach_likelihood_score > high_threshold && potential_impact_score > high_threshold
    ) || (
      breach_likelihood_score > (high_threshold * 1.2) && potential_impact_score > medium_threshold
    )) {
    overall_risk_level = "Critical";
  } else if (
    (breach_likelihood_score > high_threshold || potential_impact_score > high_threshold) ||
    (breach_likelihood_score > medium_threshold && potential_impact_score > medium_threshold)
  ) {
    overall_risk_level = "High";
  } else if (
    breach_likelihood_score > medium_threshold || potential_impact_score > medium_threshold
  ) {
    overall_risk_level = "Medium";
  } else {
    overall_risk_level = "Low";
  }

  return {
    breach_likelihood_score,
    potential_impact_score,
    overall_risk_level
  };
}
