const initial_data_gb = inputs.initial_data_gb;
const data_growth_rate_percent = inputs.data_growth_rate_percent;
const compression_ratio_percent = inputs.compression_ratio_percent;
const storage_duration_years = inputs.storage_duration_years;
const cloud_storage_cost_gb_month = inputs.cloud_storage_cost_gb_month;
const local_drive_capacity_tb = inputs.local_drive_capacity_tb;
const local_drive_cost_per_unit = inputs.local_drive_cost_per_unit;
const local_drive_lifespan_years = inputs.local_drive_lifespan_years;

const compression_factor = 1 - (compression_ratio_percent / 100);
const growth_factor = 1 + (data_growth_rate_percent / 100);

let total_cloud_cost = 0;

// Cloud cost calculation - sum of data volume for each year
for (let year = 1; year <= storage_duration_years; year++) {
    // Data volume at the *start* of the current year, compressed
    const data_volume_start_of_year_gb = (initial_data_gb * compression_factor) * Math.pow(growth_factor, year - 1);
    total_cloud_cost += data_volume_start_of_year_gb * cloud_storage_cost_gb_month * 12;
}

// Final data volume calculation (at the *end* of the storage duration, compressed)
const final_data_volume_gb_compressed = (initial_data_gb * compression_factor) * Math.pow(growth_factor, storage_duration_years);

// Local storage cost calculation
const final_data_volume_tb_compressed = final_data_volume_gb_compressed / 1024;

let total_local_cost = 0;

if (storage_duration_years > 0 && final_data_volume_gb_compressed > 0) {
    // Calculate the number of drives needed to store the final projected data volume
    // Ensure local_drive_capacity_tb is not zero to prevent division by zero
    const effective_local_drive_capacity_tb = Math.max(0.001, local_drive_capacity_tb); // Min capacity to avoid error
    const drives_needed_for_final_volume = Math.ceil(final_data_volume_tb_compressed / effective_local_drive_capacity_tb);

    // Initial purchase cost for drives to cover the final volume
    total_local_cost = drives_needed_for_final_volume * local_drive_cost_per_unit;

    // Calculate replacement costs over the duration
    // Ensure local_drive_lifespan_years is at least 1 to avoid division by zero or extremely frequent replacements
    const effective_lifespan = Math.max(1, local_drive_lifespan_years);
    const num_replacements = Math.floor(storage_duration_years / effective_lifespan);

    // Add cost for each full replacement cycle (initial purchase is handled above)
    total_local_cost += drives_needed_for_final_volume * local_drive_cost_per_unit * num_replacements;
} else if (storage_duration_years === 0) {
    // If duration is 0, cost is 0, but final data volume is initial compressed data
    total_cloud_cost = 0;
    total_local_cost = 0;
}

return {
    total_cloud_cost: total_cloud_cost,
    total_local_cost: total_local_cost,
    final_data_volume_gb: final_data_volume_gb_compressed
};
