formula:
let score = 50; // Base neutral score

// Additive factors (increase exposure)
score += Math.min(inputs.socialMediaPlatforms * 2.5, 25); // Max 25 points (for 10 platforms)
score += (5 - inputs.socialMediaPrivacyLevel) * 3; // Max 12 points (for level 1)
score += Math.min(inputs.connectedAppsCount * 0.5, 15); // Max 15 points (for 30+ apps)
score += Math.min(inputs.weakPasswordsCount * 2.5, 25); // Max 25 points (for 10+ weak passwords)
score += inputs.locationTrackingEnabled * 10; // 10 points if enabled

// Subtractive factors (decrease exposure, improve privacy)
score -= (inputs.twoFactorAuthCoverage / 100) * 15; // Max 15 points (for 100% coverage)
score -= (inputs.dataRetentionAwareness - 1) * 2.5; // Max 10 points (for level 5)
score -= (inputs.vpnUsageFrequency - 1) * 3; // Max 12 points (for level 5)

// Ensure score is within a reasonable range (0-100)
score = Math.max(0, Math.min(100, score));

let riskCategory;
let steps = [];

if (score >= 80) {
    riskCategory = "Very High";
    steps.push("Immediately address weak or reused passwords using a robust password manager.");
    steps.push("Review and tighten privacy settings on ALL social media platforms, limiting data sharing.");
    steps.push("Disable location tracking on your primary devices unless absolutely essential for a specific app.");
    steps.push("Audit and revoke access for any unnecessary third-party apps connected to your social media.");
    steps.push("Implement 2FA on all critical online accounts (email, banking, social media logins).");
    steps.push("Consider reducing your active presence on multiple social media platforms.");
} else if (score >= 60) {
    riskCategory = "High";
    steps.push("Prioritize securing accounts with weak passwords; use strong, unique passwords for each service.");
    steps.push("Regularly review your social media privacy settings and connected apps.");
    steps.push("Ensure 2FA is enabled for sensitive accounts like email and financial services.");
    steps.push("Be mindful of location data sharing; review app permissions and device settings.");
    steps.push("Start using a VPN or privacy-focused browser for general internet usage.");
} else if (score >= 40) {
    riskCategory = "Moderate";
    steps.push("Conduct a periodic privacy audit of your online accounts and device settings (e.g., quarterly).");
    steps.push("Strengthen privacy settings on social media and limit who can see your posts.");
    steps.push("Explore using a password manager to simplify strong, unique password creation.");
    steps.push("Improve your understanding of data retention policies for services you use frequently.");
    steps.push("Consider using a VPN more frequently, especially on public Wi-Fi.");
} else if (score >= 20) {
    riskCategory = "Low";
    steps.push("Maintain vigilance with your privacy practices; digital threats evolve constantly.");
    steps.push("Keep abreast of privacy news and best practices to adapt your strategies.");
    steps.push("Regularly review app permissions and third-party access to your data.");
    steps.push("Ensure your devices' operating systems and apps are always up to date to patch vulnerabilities.");
    steps.push("Educate yourself and others on the importance of digital privacy.");
} else { // score < 20
    riskCategory = "Very Low";
    steps.push("Congratulations! You demonstrate excellent digital privacy hygiene.");
    steps.push("Continue your proactive approach to digital privacy management.");
    steps.push("Consider becoming an advocate for digital privacy best practices among your peers.");
    steps.push("Stay informed about emerging privacy threats and technologies.");
    steps.push("Regularly re-evaluate your privacy settings as new services or features emerge.");
}

return {
    exposureScore: Math.round(score),
    riskCategory: riskCategory,
    actionableSteps: steps
};
