function calculateDiscretionarySpending(inputs) {
  const netMonthlyIncome = inputs.netMonthlyIncome || 0;
  const fixedHousingCosts = inputs.fixedHousingCosts || 0;
  const fixedTransportationCosts = inputs.fixedTransportationCosts || 0;
  const otherFixedExpenses = inputs.otherFixedExpenses || 0;
  const essentialUtilities = inputs.essentialUtilities || 0;
  const groceriesFood = inputs.groceriesFood || 0;
  const minimumDebtPayments = inputs.minimumDebtPayments || 0;
  const monthlySavingsGoals = inputs.monthlySavingsGoals || 0;

  // Ensure all inputs are non-negative
  const validatedNetMonthlyIncome = Math.max(0, netMonthlyIncome);
  const validatedFixedHousingCosts = Math.max(0, fixedHousingCosts);
  const validatedFixedTransportationCosts = Math.max(0, fixedTransportationCosts);
  const validatedOtherFixedExpenses = Math.max(0, otherFixedExpenses);
  const validatedEssentialUtilities = Math.max(0, essentialUtilities);
  const validatedGroceriesFood = Math.max(0, groceriesFood);
  const validatedMinimumDebtPayments = Math.max(0, minimumDebtPayments);
  const validatedMonthlySavingsGoals = Math.max(0, monthlySavingsGoals);

  // Step 1 & 2: Calculate total essential and fixed monthly expenses, including savings goals.
  const totalMonthlyOutflows = 
    validatedFixedHousingCosts +
    validatedFixedTransportationCosts +
    validatedOtherFixedExpenses +
    validatedEssentialUtilities +
    validatedGroceriesFood +
    validatedMinimumDebtPayments +
    validatedMonthlySavingsGoals;

  // Step 3: Subtract total outflows from net monthly income.
  const rawDiscretionary = validatedNetMonthlyIncome - totalMonthlyOutflows;

  // Step 4: Determine discretionary capacity (non-negative) and any potential shortfall.
  const discretionarySpending = Math.max(0, rawDiscretionary);
  const monthlyShortfall = Math.abs(Math.min(0, rawDiscretionary)); // If raw is negative, this is the shortfall

  // Step 5: Calculate the percentage of net income represented by the discretionary capacity.
  let percentageOfNetIncome = 0;
  if (validatedNetMonthlyIncome > 0) {
    percentageOfNetIncome = (discretionarySpending / validatedNetMonthlyIncome) * 100;
  }

  return {
    discretionarySpending: discretionarySpending,
    percentageOfNetIncome: percentageOfNetIncome,
    monthlyShortfall: monthlyShortfall,
    totalMonthlyExpenses: totalMonthlyOutflows
  };
}
