function calculate({
    grossMonthlyIncome,
    emergencyFundAmount,
    monthlyLivingExpenses,
    totalNonMortgageDebt,
    incomeStabilityCategory,
    assetDiversificationCategory,
    secondaryIncomePresent,
    jobMarketOutlook
}) {
    let score = 0;
    const maxScore = 100;

    // --- Input Validation and Edge Cases ---
    if (grossMonthlyIncome <= 0 || monthlyLivingExpenses <= 0) {
        return {
            resilienceScore: 0,
            resilienceRating: "Critical (Insufficient Data)",
            emergencyFundMonthsCoverage: 0,
            debtToIncomeRatio: 0
        };
    }

    // --- Component Scoring ---

    // 1. Emergency Fund Score (Max 25 points)
    let emergencyFundMonthsCoverage = 0;
    if (monthlyLivingExpenses > 0) {
        emergencyFundMonthsCoverage = emergencyFundAmount / monthlyLivingExpenses;
    }
    let emergencyFundScore = 0;
    if (emergencyFundMonthsCoverage >= 12) {
        emergencyFundScore = 25;
    } else if (emergencyFundMonthsCoverage >= 6) {
        emergencyFundScore = 20;
    } else if (emergencyFundMonthsCoverage >= 3) {
        emergencyFundScore = 10;
    } else if (emergencyFundMonthsCoverage > 0) {
        emergencyFundScore = 5;
    }
    score += emergencyFundScore;

    // 2. Debt Score (Max 25 points) - Lower debt = higher score
    const annualGrossIncome = grossMonthlyIncome * 12;
    let debtToAnnualIncomeRatio = 0;
    if (annualGrossIncome > 0) {
        debtToAnnualIncomeRatio = (totalNonMortgageDebt / annualGrossIncome) * 100;
    }
    let debtScore = 0;
    if (totalNonMortgageDebt === 0) {
        debtScore = 25;
    } else if (debtToAnnualIncomeRatio < 20) {
        debtScore = 20;
    } else if (debtToAnnualIncomeRatio < 50) {
        debtScore = 15;
    } else if (debtToAnnualIncomeRatio < 100) {
        debtScore = 10;
    } else {
        debtScore = 5;
    }
    score += debtScore;

    // 3. Income Stability Score (Max 20 points)
    let incomeStabilityScore = 0;
    switch (incomeStabilityCategory) {
        case 'highly_stable': incomeStabilityScore = 20; break;
        case 'stable': incomeStabilityScore = 15; break;
        case 'moderate': incomeStabilityScore = 10; break;
        case 'volatile': incomeStabilityScore = 5; break;
        default: incomeStabilityScore = 0; // Should not happen with validation
    }
    score += incomeStabilityScore;

    // 4. Asset Diversification Score (Max 15 points)
    let assetDiversificationScore = 0;
    switch (assetDiversificationCategory) {
        case 'highly_diversified': assetDiversificationScore = 15; break;
        case 'diversified': assetDiversificationScore = 10; break;
        case 'moderately_diversified': assetDiversificationScore = 7; break;
        case 'concentrated': assetDiversificationScore = 3; break;
        default: assetDiversificationScore = 0;
    }
    score += assetDiversificationScore;

    // 5. Secondary Income Modifier (Max 5 points)
    if (secondaryIncomePresent) {
        score += 5;
    }

    // 6. Job Market Outlook Modifier (Max 10 points)
    let jobMarketScore = 0;
    switch (jobMarketOutlook) {
        case 'strong_growth': jobMarketScore = 10; break;
        case 'stable': jobMarketScore = 7; break;
        case 'uncertain': jobMarketScore = 3; break;
        case 'declining': jobMarketScore = 1; break;
        default: jobMarketScore = 0;
    }
    score += jobMarketScore;

    // --- Final Score and Rating ---
    score = Math.max(0, Math.min(score, maxScore)); // Cap score between 0 and 100

    let resilienceRating;
    if (score >= 90) {
        resilienceRating = "Excellent - Highly Resilient";
    } else if (score >= 75) {
        resilienceRating = "Strong - Very Resilient";
    } else if (score >= 50) {
        resilienceRating = "Moderate - Developing Resilience";
    } else if (score >= 25) {
        resilienceRating = "Low - Vulnerable";
    } else {
        resilienceRating = "Critical - Extremely Vulnerable";
    }

    return {
        resilienceScore: Math.round(score),
        resilienceRating: resilienceRating,
        emergencyFundMonthsCoverage: emergencyFundMonthsCoverage,
        debtToIncomeRatio: debtToAnnualIncomeRatio
    };
}
