function calculate() {
    const householdMembers = inputs.householdMembers > 0 ? inputs.householdMembers : 1; // Prevent division by zero
    const locationRiskScore = inputs.locationRiskScore;
    const waterSupplyDays = inputs.waterSupplyDays;
    const foodSupplyDays = inputs.foodSupplyDays;
    const firstAidKitLevel = inputs.firstAidKitLevel;
    const emergencyPlan = inputs.emergencyPlan;
    const financialResilienceMonths = inputs.financialResilienceMonths;
    const evacuationKit = inputs.evacuationKit;
    const specialNeedsPreparedness = inputs.specialNeedsPreparedness;
    const utilityShutoffKnowledge = inputs.utilityShutoffKnowledge;

    let totalScore = 0;
    const recommendations = [];

    // 1. Water Supply (Max 20 points)
    const waterPerPersonDays = waterSupplyDays / householdMembers;
    let waterScore = 0;
    if (waterPerPersonDays >= 14) {
        waterScore = 20;
    } else if (waterPerPersonDays >= 7) {
        waterScore = 15;
    } else if (waterPerPersonDays >= 3) {
        waterScore = 8;
    } else if (waterPerPersonDays > 0) {
        waterScore = 3;
    }
    if (waterScore < 15) recommendations.push("Increase your household's potable water supply to at least 7-14 days per person.");
    totalScore += waterScore;

    // 2. Food Supply (Max 20 points)
    const foodPerPersonDays = foodSupplyDays / householdMembers;
    let foodScore = 0;
    if (foodPerPersonDays >= 14) {
        foodScore = 20;
    } else if (foodPerPersonDays >= 7) {
        foodScore = 15;
    } else if (foodPerPersonDays >= 3) {
        foodScore = 8;
    } else if (foodPerPersonDays > 0) {
        foodScore = 3;
    }
    if (foodScore < 15) recommendations.push("Build up your non-perishable food supply to last 7-14 days per person.");
    totalScore += foodScore;

    // 3. First Aid Kit (Max 10 points)
    let firstAidScore = 0;
    if (firstAidKitLevel === 1) {
        firstAidScore = 3;
        recommendations.push("Upgrade your first aid kit to at least an intermediate level, ensuring it's well-stocked and accessible.");
    } else if (firstAidKitLevel === 2) {
        firstAidScore = 7;
        recommendations.push("Consider upgrading to a comprehensive first aid kit and refreshing your first aid knowledge/training.");
    } else if (firstAidKitLevel === 3) {
        firstAidScore = 10;
    }
    totalScore += firstAidScore;

    // 4. Emergency Plan (Max 15 points)
    let planScore = 0;
    if (emergencyPlan === 0) {
        planScore = 0;
        recommendations.push("Develop a comprehensive written emergency plan including communication strategies, evacuation routes, and meeting points.");
    } else if (emergencyPlan === 1) {
        planScore = 7;
        recommendations.push("Flesh out your basic emergency plan into a detailed one, practice it, and share it with all household members.");
    } else if (emergencyPlan === 2) {
        planScore = 15;
    }
    totalScore += planScore;

    // 5. Financial Resilience (Max 15 points)
    let financeScore = 0;
    if (financialResilienceMonths >= 6) {
        financeScore = 15;
    } else if (financialResilienceMonths >= 3) {
        financeScore = 10;
    } else if (financialResilienceMonths >= 1) {
        financeScore = 5;
    }
    if (financeScore < 10) recommendations.push("Work towards saving 3-6 months of essential living expenses to enhance financial resilience.");
    totalScore += financeScore;

    // 6. Evacuation Kit (Max 10 points)
    let evacKitScore = 0;
    if (evacuationKit === 0) {
        evacKitScore = 0;
        recommendations.push("Prepare a 'Go-Bag' or 'Bug-Out Bag' for each household member with essentials for 3 days.");
    } else if (evacuationKit === 1) {
        evacKitScore = 5;
        recommendations.push("Ensure your 'Go-Bags' are fully stocked, easily accessible, and reviewed regularly for each household member.");
    } else if (evacuationKit === 2) {
        evacKitScore = 10;
    }
    totalScore += evacKitScore;

    // 7. Special Needs Preparedness (Max 5 points)
    let specialNeedsScore = 0;
    if (specialNeedsPreparedness === 0) {
        specialNeedsScore = 0;
        if (householdMembers > 1) { // Only suggest if multiple people or explicit special needs implied
            recommendations.push("Develop specific plans and supplies for any household members with special needs (medical, accessibility, pets).");
        } else {
             // If single person and no explicit special needs, it's not a deficiency. Max out score for this component.
            specialNeedsScore = 5; 
        }
    } else if (specialNeedsPreparedness === 1) {
        specialNeedsScore = 2;
        recommendations.push("Review and enhance your special needs preparedness plan and supplies to ensure they are fully comprehensive.");
    } else if (specialNeedsPreparedness === 2) {
        specialNeedsScore = 5;
    }
    totalScore += specialNeedsScore;

    // 8. Utility Shut-off Knowledge (Max 5 points)
    let utilityScore = 0;
    if (utilityShutoffKnowledge === 0) {
        utilityScore = 0;
        recommendations.push("Learn how to safely shut off main utilities (water, gas, electricity) at your home and practice if possible.");
    } else if (utilityShutoffKnowledge === 1) {
        utilityScore = 5;
    }
    totalScore += utilityScore;

    // Cap the total score at 100
    totalScore = Math.min(totalScore, 100);

    let preparednessLevel = "Critical Risk";
    if (totalScore >= 75) {
        preparednessLevel = "Highly Resilient";
    } else if (totalScore >= 50) {
        preparednessLevel = "Prepared";
    } else if (totalScore >= 30) {
        preparednessLevel = "Developing Preparedness";
    }

    // Add general advice based on location risk if applicable
    if (locationRiskScore >= 4) {
        recommendations.push("Given your high local risk factor, it's crucial to review all aspects of your plan and consider additional specialized training (e.g., advanced first aid, disaster-specific drills).");
    } else if (locationRiskScore >= 3 && totalScore < 75) {
        recommendations.push("Your moderate local risk factor means continuous improvement of your preparedness is advisable, focusing on areas identified as weaker.");
    }

    // Ensure unique recommendations
    const uniqueRecommendations = [...new Set(recommendations)];
    if (uniqueRecommendations.length === 0) {
        uniqueRecommendations.push("Congratulations! You are highly prepared. Continue to regularly review and update your plans and supplies.");
    }

    return {
        preparednessScore: totalScore,
        preparednessLevel: preparednessLevel,
        recommendation: uniqueRecommendations
    };
}
