function calculateEthicalFootprint(inputs) {
    let {
        monthly_discretionary_spending,
        fast_fashion_purchases_per_year,
        weekly_meat_consumption_servings,
        daily_commute_type,
        charitable_donations_percentage,
        volunteer_hours_per_month,
        voted_in_last_election,
        civic_engagement_frequency
    } = inputs;

    // --- Consumption Score (Max 25 points) ---
    let consumptionScore = 0;

    // monthly_discretionary_spending (penalty)
    // Score decreases by 1 point for every 500 units spent, max penalty 15 points (at 7500 units)
    let discretionarySpendingPenalty = Math.min(15, monthly_discretionary_spending / 500);
    consumptionScore += Math.max(0, 15 - discretionarySpendingPenalty); // max 15 points

    // fast_fashion_purchases_per_year (penalty)
    // Score decreases by 1 point for every item, max penalty 10 points (at 10 items)
    let fastFashionPenalty = Math.min(10, fast_fashion_purchases_per_year * 1);
    consumptionScore += Math.max(0, 10 - fastFashionPenalty); // max 10 points

    // Ensure consumptionScore is within 0-25 range
    consumptionScore = Math.max(0, Math.min(25, consumptionScore));

    // --- Environmental Score (Max 25 points) ---
    let environmentalScore = 0;

    // weekly_meat_consumption_servings (penalty)
    // Score decreases by 1.5 points for every serving, max penalty 15 points (at 10 servings)
    let meatConsumptionPenalty = Math.min(15, weekly_meat_consumption_servings * 1.5);
    environmentalScore += Math.max(0, 15 - meatConsumptionPenalty); // max 15 points

    // daily_commute_type (positive contribution)
    switch (daily_commute_type) {
        case "Cycling/Walking":
            environmentalScore += 10;
            break;
        case "Public Transport":
            environmentalScore += 7;
            break;
        case "Car (carpool)":
            environmentalScore += 4;
            break;
        case "Car (solo)":
        default:
            environmentalScore += 0;
            break;
    }

    // Ensure environmentalScore is within 0-25 range
    environmentalScore = Math.max(0, Math.min(25, environmentalScore));


    // --- Social Engagement Score (Max 25 points) ---
    let socialScore = 0;

    // charitable_donations_percentage (positive contribution)
    // Score increases by 1.5 points for every % donated, max 15 points (at 10%)
    socialScore += Math.min(15, charitable_donations_percentage * 1.5); // max 15 points

    // volunteer_hours_per_month (positive contribution)
    // Score increases by 1 point for every hour, max 10 points (at 10 hours)
    socialScore += Math.min(10, volunteer_hours_per_month * 1); // max 10 points

    // Ensure socialScore is within 0-25 range
    socialScore = Math.max(0, Math.min(25, socialScore));


    // --- Civic Participation Score (Max 25 points) ---
    let civicScore = 0;

    // voted_in_last_election (binary)
    if (voted_in_last_election === "Yes") {
        civicScore += 15;
    } else {
        civicScore += 0;
    }

    // civic_engagement_frequency (graded contribution)
    switch (civic_engagement_frequency) {
        case "Often (6+ times/year)":
            civicScore += 10;
            break;
        case "Sometimes (3-5 times/year)":
            civicScore += 7;
            break;
        case "Rarely (1-2 times/year)":
            civicScore += 4;
            break;
        case "Never":
        default:
            civicScore += 0;
            break;
    }

    // Ensure civicScore is within 0-25 range
    civicScore = Math.max(0, Math.min(25, civicScore));


    // --- Total Ethical Footprint Score (Max 100 points) ---
    let ethicalFootprintScore = consumptionScore + environmentalScore + socialScore + civicScore;

    // Ensure total score is within 0-100 range
    ethicalFootprintScore = Math.max(0, Math.min(100, ethicalFootprintScore));

    // --- Footprint Classification ---
    let footprintCategory;
    if (ethicalFootprintScore >= 80) {
        footprintCategory = "Highly Conscious Ethos";
    } else if (ethicalFootprintScore >= 60) {
        footprintCategory = "Developing Awareness & Impact";
    } else if (ethicalFootprintScore >= 40) {
        footprintCategory = "Emerging Ethical Contributor";
    } else if (ethicalFootprintScore >= 20) {
        footprintCategory = "Needs Significant Reflection";
    } else {
        footprintCategory = "Undertaking Initial Steps";
    }

    return {
        ethical_footprint_score: Math.round(ethicalFootprintScore),
        footprint_category: footprintCategory,
        consumption_score: Math.round(consumptionScore),
        environmental_score: Math.round(environmentalScore),
        social_score: Math.round(socialScore),
        civic_score: Math.round(civicScore)
    };
}
