function calculateDosage(inputs) {
    const {
        patientWeightKg, patientAgeYears, biologicalSex, serumCreatinineMgDl,
        drugHalfLifeHours, drugBioavailabilityPercent, targetPlasmaConcentrationMgL,
        drugVolumeOfDistributionLKg, hepaticImpairmentFactor, fractionRenalElimination
    } = inputs;

    // --- Input Validation and Defaulting (Robustness) ---
    const errors = [];
    if (isNaN(patientWeightKg) || patientWeightKg <= 0) errors.push("Invalid Patient Weight. Must be a positive number.");
    if (isNaN(patientAgeYears) || patientAgeYears < 0) errors.push("Invalid Patient Age. Must be a non-negative number.");
    if (isNaN(serumCreatinineMgDl) || serumCreatinineMgDl <= 0) errors.push("Invalid Serum Creatinine. Must be a positive number.");
    if (isNaN(drugHalfLifeHours) || drugHalfLifeHours <= 0) errors.push("Invalid Drug Half-Life. Must be a positive number.");
    if (isNaN(drugBioavailabilityPercent) || drugBioavailabilityPercent <= 0 || drugBioavailabilityPercent > 100) errors.push("Invalid Drug Bioavailability. Must be between 1 and 100%.");
    if (isNaN(targetPlasmaConcentrationMgL) || targetPlasmaConcentrationMgL <= 0) errors.push("Invalid Target Plasma Concentration. Must be a positive number.");
    if (isNaN(drugVolumeOfDistributionLKg) || drugVolumeOfDistributionLKg <= 0) errors.push("Invalid Drug Volume of Distribution. Must be a positive number.");
    if (isNaN(hepaticImpairmentFactor) || hepaticImpairmentFactor <= 0.1 || hepaticImpairmentFactor > 1.0) errors.push("Invalid Hepatic Impairment Factor. Must be between 0.1 and 1.0.");
    if (isNaN(fractionRenalElimination) || fractionRenalElimination < 0 || fractionRenalElimination > 1) errors.push("Invalid Fraction Renal Elimination. Must be between 0 and 1.");

    if (errors.length > 0) {
        throw new Error(errors.join('\n'));
    }

    const bioavailabilityDecimal = drugBioavailabilityPercent / 100;

    // 1. Estimate Creatinine Clearance (CrCl) using Cockcroft-Gault Equation
    let crcl = ((140 - patientAgeYears) * patientWeightKg) / (72 * serumCreatinineMgDl);
    if (biologicalSex && biologicalSex.toLowerCase() === 'female') {
        crcl *= 0.85;
    }
    // Cap CrCl to prevent overestimation in very healthy individuals and ensure a minimum
    crcl = Math.max(5, Math.min(crcl, 120)); // Capped between 5 and 120 mL/min

    // 2. Calculate Total Volume of Distribution (Vd)
    const totalVd = drugVolumeOfDistributionLKg * patientWeightKg; // Liters

    // 3. Calculate Elimination Rate Constant (Ke) for a healthy individual
    const ke_healthy = 0.693 / drugHalfLifeHours; // per hour

    // 4. Calculate Healthy Clearance (CL_healthy)
    const cl_healthy = ke_healthy * totalVd; // L/hour

    // 5. Adjust Clearance based on Renal and Hepatic Function
    // CrCl_ratio reflects the patient's renal function relative to a 'normal' CrCl (e.g., 100 mL/min)
    const crcl_adj_factor = Math.min(1, crcl / 100); // Caps at 1, meaning normal/high CrCl gets full renal clearance

    // Total adjusted clearance, accounting for renal and hepatic components
    // CL_patient = CL_healthy * [ (1 - fractionRenalElimination) * hepaticImpairmentFactor + fractionRenalElimination * crcl_adj_factor ]
    const cl_patient = cl_healthy * (
        (1 - fractionRenalElimination) * hepaticImpairmentFactor +
        fractionRenalElimination * crcl_adj_factor
    ); // L/hour

    // Ensure CL_patient is not absurdly low or high (safety caps)
    const MIN_CL = 0.001; // L/hr (very low clearance)
    const MAX_CL = 1000; // L/hr (very high clearance)
    const clamped_cl_patient = Math.max(MIN_CL, Math.min(cl_patient, MAX_CL));

    // 6. Calculate Effective Elimination Rate Constant and Half-Life for the Patient
    const effectiveKe_patient = clamped_cl_patient / totalVd; // /hour
    const effectiveHalfLifeHours = 0.693 / effectiveKe_patient; // hours

    // 7. Determine Optimal Dosing Interval
    // Dosing Interval: typically 1 to 1.5 times the effective half-life, rounded to common intervals.
    // Aims to maintain steady-state concentrations without excessive fluctuation.
    let dosingIntervalHours = effectiveHalfLifeHours * 1.5;

    // Round to common dosing intervals for practical application
    if (dosingIntervalHours <= 4) dosingIntervalHours = 4;
    else if (dosingIntervalHours <= 6) dosingIntervalHours = 6;
    else if (dosingIntervalHours <= 8) dosingIntervalHours = 8;
    else if (dosingIntervalHours <= 12) dosingIntervalHours = 12;
    else if (dosingIntervalHours <= 18) dosingIntervalHours = 18;
    else if (dosingIntervalHours <= 24) dosingIntervalHours = 24;
    else if (dosingIntervalHours <= 36) dosingIntervalHours = 36;
    else dosingIntervalHours = 48; // Max common interval for practical reasons

    // 8. Calculate Maintenance Dose (MD)
    // MD = (Target Plasma Concentration * CL_patient * Dosing Interval) / Bioavailability
    const suggestedMaintenanceDoseMg = (targetPlasmaConcentrationMgL * clamped_cl_patient * dosingIntervalHours) / bioavailabilityDecimal;

    // 9. Calculate Loading Dose (LD)
    // LD = (Target Plasma Concentration * Total Vd) / Bioavailability
    const loadingDoseMg = (targetPlasmaConcentrationMgL * totalVd) / bioavailabilityDecimal;

    // --- Output Caps/Floors for Safety and Realism ---
    const minDose = 0.01; // mg
    const maxDose = 10000; // mg (realistic upper bound for many drugs)

    return {
        suggestedMaintenanceDoseMg: Math.max(minDose, Math.min(suggestedMaintenanceDoseMg, maxDose)),
        loadingDoseMg: Math.max(minDose, Math.min(loadingDoseMg, maxDose)),
        dosingIntervalHours: dosingIntervalHours,
    };
}
