formula:
let baseRisk = 10;

// Age factor: Risk increases after 20, up to a cap
let ageFactor = Math.max(0, (age - 20) * 0.5);

// Dietary Variety factor: Lower variety (1) adds more risk, higher (5) adds less
let varietyFactor = (6 - dietary_variety) * 6;

// Processed Food Intake factor: Higher intake (5) adds more risk
let processedFactor = processed_food_intake * 5;

// Stress Level factor: Higher stress (5) adds more risk
let stressFactor = stress_level * 4;

// Physical Activity factor: Very low or very high activity can add risk
let activityFactor = 0;
if (physical_activity_hours < 2) {
    activityFactor = (2 - physical_activity_hours) * 3; // Inactivity risk
} else if (physical_activity_hours > 10) {
    activityFactor = (physical_activity_hours - 10) * 2; // Excessive activity/needs risk
}

// Chronic Condition factor: Presence significantly increases risk
let chronicFactor = chronic_condition_present * 20;

// Dietary Restriction factor: Vegan/Vegetarian diets can increase risk for specific nutrients
let restrictionFactor = dietary_restriction * 10;

// Sum all factors to get the total raw risk
let totalRisk = baseRisk + ageFactor + varietyFactor + processedFactor + stressFactor + activityFactor + chronicFactor + restrictionFactor;

// Cap the total risk score between 0 and 100
totalRisk = Math.min(Math.max(0, totalRisk), 100);

let riskCategory = "Low Risk";
let recommendationFocus = "Maintain good habits and continue to prioritize nutrient-rich foods.";

if (totalRisk >= 40 && totalRisk < 70) {
    riskCategory = "Moderate Risk";
    recommendationFocus = "Focus on increasing dietary diversity, managing stress, and reviewing intake of key micronutrients. Consider a general multi-vitamin/mineral supplement if diet is insufficient.";
} else if (totalRisk >= 70) {
    riskCategory = "High Risk";
    recommendationFocus = "Strongly consider consulting a healthcare professional or registered dietitian for a comprehensive assessment. Immediate attention to dietary intake, stress reduction, and targeted supplementation is advised.";
}

// Add more specific recommendations based on individual factors
if (dietary_restriction === 2) { // Vegan
    recommendationFocus += " Particular attention to B12, Iron, Zinc, Calcium, Iodine, and Omega-3s is crucial.";
} else if (dietary_restriction === 1) { // Vegetarian
    recommendationFocus += " Focus on Iron, B12, and Zinc sources. Ensure adequate protein intake from plant sources.";
}
if (processed_food_intake >= 4) {
    recommendationFocus += " Prioritize significantly reducing intake of ultra-processed foods and increasing whole foods.";
}
if (stress_level >= 4) {
    recommendationFocus += " Implement stress-reduction techniques like mindfulness, yoga, or sufficient sleep, as chronic stress impacts nutrient utilization.";
}
if (chronic_condition_present === 1) {
    recommendationFocus += " Work closely with your doctor or a specialized dietitian to manage nutritional needs and absorption issues related to your health condition.";
}
if (physical_activity_hours < 2) {
    recommendationFocus += " Increase moderate physical activity gradually to improve overall health and nutrient absorption.";
} else if (physical_activity_hours > 10) {
    recommendationFocus += " Ensure adequate caloric and micronutrient intake to support high activity levels, potentially increasing needs for B vitamins, electrolytes, and antioxidants.";
}

return {
    overall_risk_score: Math.round(totalRisk),
    risk_category: riskCategory,
    recommendation_focus: recommendationFocus
};
