function calculateRisk(inputs) {
    let riskScore = 0;
    let recommendations = [];
    const {
        age,
        sex,
        standardDrinksPerWeek,
        bingeDrinkingFrequency,
        hasLiverCondition,
        hasHeartCondition,
        hasMentalHealthCondition,
        familyHistoryAlcoholism,
        onMedications,
        isPregnant
    } = inputs;

    // --- Critical Overrides ---
    if (isPregnant === 'yes') {
        riskScore = 100; // Absolute highest risk
        recommendations.push("Immediate and complete cessation of alcohol is strongly advised due to severe risks to fetal development and infant health.");
        recommendations.push("Consult your healthcare provider for urgent guidance.");
        return {
            riskScore: Math.min(riskScore, 100),
            riskCategory: "Extremely High Risk - Pregnancy/Breastfeeding",
            recommendations: recommendations.join(" ")
        };
    }

    // --- Base Risk Factors ---
    // Age (minor influence, slightly higher for very young/very old)
    if (age < 21) riskScore += 5; // Legal age consideration, brain development
    if (age >= 65) riskScore += 5; // Increased sensitivity, medication interactions

    // Sex (women generally have lower tolerance and higher risk for equivalent consumption)
    const weeklyGuidelineLimit = sex === 'female' ? 7 : 14; // Standard drinks per week based on updated guidelines (approx)
    const bingeThreshold = sex === 'female' ? 4 : 5;

    // --- Alcohol Consumption Risk ---
    if (standardDrinksPerWeek > 0) {
        recommendations.push("Consider reducing alcohol intake to align with updated health guidelines.");
    }

    if (standardDrinksPerWeek > weeklyGuidelineLimit) {
        riskScore += (standardDrinksPerWeek - weeklyGuidelineLimit) * 3; // 3 points per drink over limit
        if (sex === 'female') riskScore += 5; // Additional risk for women exceeding limits
        recommendations.push(`Your weekly consumption (${standardDrinksPerWeek} drinks) exceeds recommended guidelines for your biological sex (${weeklyGuidelineLimit} drinks/week).`);
    } else if (standardDrinksPerWeek > 0 && standardDrinksPerWeek <= weeklyGuidelineLimit) {
        recommendations.push(`Your weekly consumption (${standardDrinksPerWeek} drinks) is within or close to recommended guidelines. Even within guidelines, some risks remain.`);
    }

    if (bingeDrinkingFrequency > 0) {
        riskScore += bingeDrinkingFrequency * 10; // 10 points per binge occasion per month
        recommendations.push(`Binge drinking ${bingeDrinkingFrequency} times per month significantly increases acute and long-term health risks.`);
        recommendations.push("Consider strategies to avoid binge drinking, such as setting limits, alternating alcoholic with non-alcoholic drinks, or avoiding high-risk situations.");
    }

    // --- Medical History and Predisposition Risk ---
    if (hasLiverCondition === 'yes') {
        riskScore += 25;
        recommendations.push("Your existing liver condition makes any alcohol consumption highly risky. Abstinence is strongly recommended and urgent consultation with your specialist is advised.");
    }
    if (hasHeartCondition === 'yes') {
        riskScore += 15;
        recommendations.push("Your existing heart condition warrants extremely careful consideration of alcohol intake. Consult your cardiologist or GP.");
    }
    if (hasMentalHealthCondition === 'yes') {
        riskScore += 15;
        recommendations.push("Alcohol can exacerbate mental health conditions and interfere with medication effectiveness. Seek professional advice from a mental health expert or GP.");
    }
    if (familyHistoryAlcoholism === 'yes') {
        riskScore += 10;
        recommendations.push("A family history of alcohol use disorder indicates increased genetic predisposition. Be extra mindful of your consumption patterns and potential for developing dependence.");
    }
    if (onMedications === 'yes') {
        riskScore += 20;
        recommendations.push("Alcohol can have dangerous interactions with many prescription and over-the-counter medications. Review your prescriptions with a pharmacist or doctor.");
    }

    // --- Caps and Floors ---
    riskScore = Math.max(0, Math.min(riskScore, 100)); // Ensure score is between 0 and 100

    // --- Determine Risk Category ---
    let riskCategory;
    if (riskScore >= 75) {
        riskCategory = "Very High Risk";
        if (!recommendations.includes("Consult your healthcare provider for urgent guidance.")) {
            recommendations.unshift("URGENT: Your estimated risk is Very High. It is strongly recommended to consult a healthcare professional immediately to discuss your alcohol consumption and health.");
        }
    } else if (riskScore >= 50) {
        riskCategory = "High Risk";
        if (!recommendations.includes("Consult your healthcare provider for urgent guidance.")) {
            recommendations.unshift("Your estimated risk is High. Discuss your alcohol consumption and potential health impacts with your doctor.");
        }
    } else if (riskScore >= 25) {
        riskCategory = "Moderate Risk";
        recommendations.unshift("Your estimated risk is Moderate. Consider adjusting your alcohol consumption to reduce potential long-term risks.");
    } else {
        riskCategory = "Low Risk";
        if (standardDrinksPerWeek === 0 && bingeDrinkingFrequency === 0) {
             recommendations.unshift("Your estimated risk is Low. Abstinence or very low consumption is generally associated with the lowest health risks.");
        } else {
             recommendations.unshift("Your estimated risk is Low. Continue to monitor your consumption and health, as even low levels carry some risk.");
        }
    }

    // Add general advice if specific recommendations are few
    if (recommendations.length < 3) {
        if (standardDrinksPerWeek === 0 && bingeDrinkingFrequency === 0) {
             recommendations.push("Maintaining abstinence or very low consumption is generally associated with the lowest overall health risks.");
        } else {
             recommendations.push("Regularly review your alcohol consumption patterns and stay informed about updated health guidelines. Prioritize your overall health and well-being.");
        }
    }


    return {
        riskScore: riskScore,
        riskCategory: riskCategory,
        recommendations: recommendations.join(" ")
    };
}
