const currentBMI = Number(inputs.currentBMI);
const targetBMI = Number(inputs.targetBMI);
const healthConditionsSeverity = Number(inputs.healthConditionsSeverity); // 0-10
const dietaryPreferenceImpact = Number(inputs.dietaryPreferenceImpact); // 1.0-2.5
const localIngredientCostIndex = Number(inputs.localIngredientCostIndex); // 0.5-2.0
const supplementNeedsScore = Number(inputs.supplementNeedsScore); // 0-5
const mealPrepTimeCommitment = Number(inputs.mealPrepTimeCommitment); // 0-20
const professionalGuidanceMonths = Number(inputs.professionalGuidanceMonths); // 0-24

// --- Health Impact Score Calculation --- (Max 100 points)
let healthScore = 0;

// 1. BMI Improvement Component (max 30 points)
// A significant drop in BMI yields higher points. If target is higher or equal, minimal points.
let bmiImprovementPoints = 0;
if (currentBMI > targetBMI) {
    const bmiDifference = currentBMI - targetBMI;
    bmiImprovementPoints = Math.min(30, bmiDifference * 1.5); // e.g., 20 BMI difference -> 30 points
} else if (currentBMI === targetBMI && currentBMI >= 18.5 && currentBMI <= 25) {
    // Reward for maintaining healthy BMI within optimal range
    bmiImprovementPoints = 5;
} else if (targetBMI < currentBMI && targetBMI >= 18.5 && targetBMI <= 25) {
    // Additional bonus for reaching healthy BMI target
    bmiImprovementPoints = Math.min(30, (currentBMI - targetBMI) * 1.2 + 5);
}
healthScore += bmiImprovementPoints;

// 2. Health Condition Improvement Component (max 25 points)
// Lower severity means better potential improvement. (0 severity -> 25 points, 10 severity -> 0 points)
const conditionImprovementPoints = Math.min(25, (10 - healthConditionsSeverity) * 2.5);
healthScore += conditionImprovementPoints;

// 3. Dietary Plan Quality/Complexity Benefit (max 20 points)
// Higher impact factor assumed to correlate with more carefully constructed, potentially beneficial diets.
// e.g., 1.0 -> 0, 1.5 -> 7.5, 2.0 -> 15, 2.5 -> 20 (capped)
const dietQualityPoints = Math.min(20, (dietaryPreferenceImpact - 1) * 15);
healthScore += dietQualityPoints;

// 4. Supplement Optimization Component (max 10 points)
// Appropriate use of supplements contributes to health.
// e.g., 0 -> 0, 2.5 -> 5, 5 -> 10
const supplementPoints = Math.min(10, supplementNeedsScore * 2);
healthScore += supplementPoints;

// 5. Adherence/Commitment Factor (max 5 points)
// More time commitment for meal prep suggests better adherence and outcomes.
// e.g., 0 -> 0, 10 -> 5, 20 -> 5 (capped)
const adherencePoints = Math.min(5, mealPrepTimeCommitment * 0.5);
healthScore += adherencePoints;

// 6. Professional Guidance Benefit (max 10 points)
// Professional guidance significantly increases the likelihood of success and safety.
// e.g., 0 -> 0, 12 months -> 9.6, 24 months -> 10 (capped)
const guidancePoints = Math.min(10, professionalGuidanceMonths * 0.8);
healthScore += guidancePoints;

// Final Health Impact Score (capped at 100 and floored at 0)
const estimatedHealthImpactScore = Math.round(Math.min(100, Math.max(0, healthScore)));

// --- Financial Cost Calculation --- (Annual)
let annualCost = 0;

// 1. Base Food Cost (Annual) - starting point for a thoughtful diet
const BASE_ANNUAL_FOOD_COST = 3600; // $300/month average for quality ingredients
annualCost += BASE_ANNUAL_FOOD_COST;

// 2. Dietary Preference Adjustment
// More restrictive/specialized diets often cost more. (e.g., 1.0 -> 0, 2.0 -> 1500, 2.5 -> 2250)
const dietPrefCostAdjustment = (dietaryPreferenceImpact - 1) * 1500;
annualCost += dietPrefCostAdjustment;

// 3. Local Ingredient Cost Adjustment
// Multiplies the total food cost by the local index
annualCost *= localIngredientCostIndex;

// 4. Supplement Cost
// Based on supplement needs score. ($200 per point per year)
const SUPPLEMENT_COST_PER_SCORE_POINT = 200;
const supplementAnnualCost = supplementNeedsScore * SUPPLEMENT_COST_PER_SCORE_POINT;
annualCost += supplementAnnualCost;

// 5. Meal Prep Opportunity Cost / Convenience Factor
// Value assigned to time spent, or cost of convenience foods if time is low.
// Using an opportunity cost of $20/hour.
const HOURLY_VALUE_OF_TIME = 20;
const annualMealPrepCost = mealPrepTimeCommitment * 52 * HOURLY_VALUE_OF_TIME;
// Cap this to prevent excessive values, representing a maximum opportunity cost for meal prep.
annualCost += Math.min(annualMealPrepCost, 5000); 

// 6. Professional Guidance Cost
const COST_PER_MONTH_GUIDANCE = 175; // e.g., $175 per month for dietitian/nutritionist
const professionalGuidanceAnnualCost = professionalGuidanceMonths * COST_PER_MONTH_GUIDANCE;
annualCost += professionalGuidanceAnnualCost;

// Minimum and Maximum Annual Cost Caps for realism
const MIN_ANNUAL_COST = 1800; // Even basic health-focused eating costs something.
const MAX_ANNUAL_COST = 25000; // Upper limit for highly specialized, heavily supplemented, guided plans.
const estimatedAnnualFinancialCost = Math.round(Math.min(MAX_ANNUAL_COST, Math.max(MIN_ANNUAL_COST, annualCost)));

return {
    healthImpactScore: estimatedHealthImpactScore,
    estimatedAnnualCost: estimatedAnnualFinancialCost
};
