const BASE_FLUID_ML_PER_KG = 35;
const MIN_DAILY_FLUID_ML = 1500;
const MAX_DAILY_FLUID_ML = 6000;

const ACTIVITY_MULTIPLIERS = {
    'sedentary': 0.05, 
    'moderate': 0.1,
    'active': 0.15,
    'very-active': 0.2
};

const TEMP_ADJUSTMENT_ML_PER_DEGREE = 50; // ml per degree C above 20C
const HUMIDITY_ADJUSTMENT_ML_PER_PERCENT = 10; // ml per % above 50%

const BASE_SODIUM_MG = 2300;
const BASE_POTASSIUM_MG = 3500;
const BASE_MAGNESIUM_MG = 300;

const SODIUM_MG_PER_LITER_SWEAT = 600; // average
const POTASSIUM_MG_PER_LITER_SWEAT = 150; // average
const MAGNESIUM_MG_PER_LITER_SWEAT = 10; // average

const SODIUM_MAX_MG_HEALTHY = 4000;
const SODIUM_MAX_MG_SENSITIVE = 2500;

const weightKg = inputs.bodyWeight;
const activityMultiplier = ACTIVITY_MULTIPLIERS[inputs.activityLevel] || ACTIVITY_MULTIPLIERS['moderate'];
const tempC = inputs.climateTemperature;
const humidityPercent = inputs.climateHumidity;
const durationHours = inputs.durationOfActivity;
const sweatRateMlPerHour = inputs.estimatedSweatRate;
const isSodiumSensitive = inputs.sodiumSensitivity === 'yes';

// 1. Calculate Base Fluid Intake
let totalFluidMl = weightKg * BASE_FLUID_ML_PER_KG;

// 2. Adjust for Activity and Sweat Loss
const totalSweatLossMl = sweatRateMlPerHour * durationHours;
totalFluidMl += totalSweatLossMl; // Add estimated sweat loss directly

// Additional fluid for metabolic activity beyond just sweat replacement
totalFluidMl += weightKg * activityMultiplier * durationHours * 10;

// 3. Adjust for Climate
if (tempC > 20) {
    totalFluidMl += (tempC - 20) * TEMP_ADJUSTMENT_ML_PER_DEGREE;
}
if (humidityPercent > 50) {
    totalFluidMl += (humidityPercent - 50) * HUMIDITY_ADJUSTMENT_ML_PER_PERCENT;
}

// Apply minimum and maximum caps for fluid
totalFluidMl = Math.max(MIN_DAILY_FLUID_ML, totalFluidMl);
totalFluidMl = Math.min(MAX_DAILY_FLUID_ML, totalFluidMl);

// Convert total fluid to Liters
const dailyFluidIntakeL = totalFluidMl / 1000;

// 4. Calculate Electrolyte Needs
// Electrolytes from sweat loss
const totalSweatLiters = totalSweatLossMl / 1000;
const sodiumFromSweat = totalSweatLiters * SODIUM_MG_PER_LITER_SWEAT;
const potassiumFromSweat = totalSweatLiters * POTASSIUM_MG_PER_LITER_SWEAT;
const magnesiumFromSweat = totalSweatLiters * MAGNESIUM_MG_PER_LITER_SWEAT;

// Total daily electrolyte needs (base + sweat replacement)
let dailySodiumIntake = BASE_SODIUM_MG + sodiumFromSweat;
let dailyPotassiumIntake = BASE_POTASSIUM_MG + potassiumFromSweat;
let dailyMagnesiumIntake = BASE_MAGNESIUM_MG + magnesiumFromSweat;

// Apply sodium sensitivity cap
if (isSodiumSensitive) {
    dailySodiumIntake = Math.min(dailySodiumIntake, SODIUM_MAX_MG_SENSITIVE);
} else {
    dailySodiumIntake = Math.min(dailySodiumIntake, SODIUM_MAX_MG_HEALTHY);
}

return {
    dailyFluidIntake: dailyFluidIntakeL,
    dailySodiumIntake: dailySodiumIntake,
    dailyPotassiumIntake: dailyPotassiumIntake,
    dailyMagnesiumIntake: dailyMagnesiumIntake
};
