function calculate({
  current_temperature,
  wind_speed,
  breed_size,
  coat_type,
  pet_age_group,
  health_status,
  acclimatization_level,
  duration_outdoors
}) {
  let risk_level_text = '';
  let recommended_outdoor_time = 0;
  let gear_recommendations = '';
  let shelter_guidance = '';
  let safety_tips = '';
  let warning_message = '';

  // 1. Calculate Wind Chill Index (NWS formula for °F and mph)
  // If temp is above 50°F or wind is less than 3 mph, wind chill formula is not applicable.
  let effective_temp = current_temperature;
  if (current_temperature <= 50 && wind_speed >= 3) {
    effective_temp = 35.74 + (0.6215 * current_temperature) - (35.75 * Math.pow(wind_speed, 0.16)) + (0.4275 * current_temperature * Math.pow(wind_speed, 0.16));
  }
  effective_temp = Math.round(effective_temp);

  // 2. Determine Base Risk Points based on Effective Temperature
  let base_risk_points = 0;
  if (effective_temp > 32) { // Above freezing
    base_risk_points = 0; // Low
  } else if (effective_temp >= 20) { // 20-32°F
    base_risk_points = 1; // Moderate
  } else if (effective_temp >= 0) { // 0-19°F
    base_risk_points = 2; // High
  } else { // Below 0°F
    base_risk_points = 4; // Extreme
  }

  // 3. Adjust Risk based on Pet Factors
  let pet_factor_adjustment = 0;

  // Size
  if (breed_size.includes('Tiny')) pet_factor_adjustment += 3;
  else if (breed_size.includes('Small')) pet_factor_adjustment += 2;
  else if (breed_size.includes('Medium')) pet_factor_adjustment += 1;
  else if (breed_size.includes('Large')) pet_factor_adjustment -= 1;
  else if (breed_size.includes('Giant')) pet_factor_adjustment -= 2;

  // Coat Type
  if (coat_type === 'Hairless') pet_factor_adjustment += 4;
  else if (coat_type === 'Short/Thin') pet_factor_adjustment += 3;
  else if (coat_type === 'Medium/Normal') pet_factor_adjustment += 1;
  else if (coat_type === 'Thick/Double') pet_factor_adjustment -= 2;

  // Age Group
  if (pet_age_group.includes('Puppy/Kitten')) pet_factor_adjustment += 2;
  else if (pet_age_group.includes('Senior')) pet_factor_adjustment += 2;

  // Health Status
  if (health_status.includes('Mild Condition')) pet_factor_adjustment += 1;
  else if (health_status.includes('Chronic Condition')) pet_factor_adjustment += 2;
  else if (health_status.includes('Immunocompromised')) pet_factor_adjustment += 3;

  // Acclimatization Level
  if (acclimatization_level === 'Not Acclimated (e.g., new to cold climate)') pet_factor_adjustment += 2;
  else if (acclimatization_level === 'Somewhat Acclimated (e.g., spends some time outdoors)') pet_factor_adjustment += 1;
  else if (acclimatization_level === 'Well Acclimated (e.g., frequently outdoors in cold)') pet_factor_adjustment -= 1;

  // Ensure pet_factor_adjustment doesn't make risk negative from here if it was 0
  pet_factor_adjustment = Math.max(0, pet_factor_adjustment);

  let total_risk_score = base_risk_points + pet_factor_adjustment;
  // Cap total risk score for practical output mapping
  total_risk_score = Math.min(total_risk_score, 10); // Max conceptual risk for clearer mapping

  // 4. Map Adjusted Risk to Outputs
  if (total_risk_score <= 1) {
    risk_level_text = 'Low Risk';
    recommended_outdoor_time = 60; // Up to 60+ minutes with monitoring
    gear_recommendations = 'Most pets are comfortable. Consider paw protection (balm/boots) for extended walks on salt/ice.';
    shelter_guidance = 'Regular outdoor access is fine. Ensure access to warm shelter if left unsupervised outdoors.';
    safety_tips = 'Monitor for signs of discomfort. Ensure fresh water. Check paws after walks.';
  } else if (total_risk_score <= 4) {
    risk_level_text = 'Moderate Risk';
    recommended_outdoor_time = 30; // Max 30 minutes
    gear_recommendations = 'Consider a warm coat/sweater for short-haired or small breeds. Paw protection (boots) is recommended.';
    shelter_guidance = 'Limit outdoor time. Ensure pets have a warm, dry place indoors. Consider bringing outdoor pets inside.';
    safety_tips = 'Regularly check paws for ice/irritants. Avoid prolonged exposure. Be aware of slipping hazards. Ensure proper hydration.';
  } else if (total_risk_score <= 7) {
    risk_level_text = 'High Risk';
    recommended_outdoor_time = 15; // Max 10-15 minutes for potty breaks only
    gear_recommendations = 'Essential: insulated coat/sweater and protective boots. For hairless breeds, add multiple layers if possible.';
    shelter_guidance = 'Outdoor access should be for potty breaks ONLY. All pets should be kept indoors. Ensure emergency heating options.';
    safety_tips = 'Supervise closely. Watch for shivering, lethargy, lifting paws. Wipe paws immediately after coming inside. Avoid direct contact with cold surfaces.';
  } else {
    risk_level_text = 'Extreme Risk';
    recommended_outdoor_time = 5; // Potty breaks only, under 5 minutes
    gear_recommendations = 'Critical: heavy insulated coat, full paw protection (boots), and minimize skin exposure. Do not leave unattended.';
    shelter_guidance = 'ABSOLUTELY NO prolonged outdoor exposure. Pets must remain indoors in a heated environment. Have an emergency shelter plan.';
    safety_tips = 'Risk of frostbite and hypothermia is high. Monitor constantly. Never leave pet unattended. Ensure paws are completely clean and dry. Watch for signs of distress.';
  }

  // Check if planned duration exceeds recommended
  if (duration_outdoors !== undefined && duration_outdoors > recommended_outdoor_time) {
    warning_message = `Your planned outdoor duration of ${duration_outdoors} minutes exceeds the recommended maximum of ${recommended_outdoor_time} minutes for a pet with these characteristics under current conditions. Please reconsider or take extra precautions.`;
    safety_tips = warning_message + ' ' + safety_tips; // Prepend warning
  }

  return {
    cold_weather_risk_level: risk_level_text + ` (Effective Temp: ${effective_temp}°F)`,
    recommended_outdoor_time: recommended_outdoor_time,
    gear_recommendations: gear_recommendations,
    shelter_guidance: shelter_guidance,
    safety_tips: safety_tips
  };
}
