const gardenAreaSqFt = inputs.gardenAreaSqFt;
const avgPlantWidthInches = Math.max(2, inputs.avgPlantWidthInches); // Minimum 2 inches to prevent unrealistic density
const avgYieldPerPlantUnit = inputs.avgYieldPerPlantUnit;
const yieldSuccessRatePercent = Math.min(100, Math.max(0, inputs.yieldSuccessRatePercent)); // Clamp between 0 and 100
const intensiveGardeningFactor = Math.min(2.0, Math.max(0.5, inputs.intensiveGardeningFactor)); // Clamp between 0.5 and 2.0

if (gardenAreaSqFt <= 0 || avgPlantWidthInches <= 0 || avgYieldPerPlantUnit < 0) {
    return {
        numOptimalPlants: 0,
        estimatedTotalHarvest: 0,
        plantsPerSqFt: 0
    };
}

// Convert plant width to feet
const avgPlantWidthFt = avgPlantWidthInches / 12;

// Calculate area required per plant
const areaPerPlantSqFt = avgPlantWidthFt * avgPlantWidthFt;

// Calculate the base number of plants (without intensive factor)
let baseNumPlants = gardenAreaSqFt / areaPerPlantSqFt;

// Apply intensive gardening factor to get optimal plants
let numOptimalPlants = baseNumPlants * intensiveGardeningFactor;

// Cap numOptimalPlants to prevent unrealistically high numbers (e.g., if plant width is very small)
// A practical cap might be around 144 plants per sq ft (1 plant per sq inch, if possible), or less depending on plant type.
// Let's set a general cap of 200 plants/sq ft to prevent extreme values from edge cases.
const maxPlantsPerSqFtCap = 200;
numOptimalPlants = Math.min(numOptimalPlants, gardenAreaSqFt * maxPlantsPerSqFtCap);

// Calculate effective number of plants considering success rate
const effectivePlants = numOptimalPlants * (yieldSuccessRatePercent / 100);

// Calculate estimated total harvest
const estimatedTotalHarvest = effectivePlants * avgYieldPerPlantUnit;

// Calculate plants per square foot
const plantsPerSqFt = numOptimalPlants / gardenAreaSqFt;

return {
    numOptimalPlants: Math.round(numOptimalPlants), // Round to nearest whole plant
    estimatedTotalHarvest: estimatedTotalHarvest,
    plantsPerSqFt: plantsPerSqFt
};
