const gardenLength = inputs.gardenLength;
const gardenWidth = inputs.gardenWidth;
const plantSpacingInRow = inputs.plantSpacingInRow;
const rowSpacing = inputs.rowSpacing;
const yieldPerPlant = inputs.yieldPerPlant;
const survivalRate = inputs.survivalRate;
const growingCycles = inputs.growingCycles;

// Calculate total garden area in square feet
const totalGardenAreaSqFt = gardenLength * gardenWidth;

// Convert spacing from inches to feet
const plantSpacingInRowFt = plantSpacingInRow / 12;
const rowSpacingFt = rowSpacing / 12;

// Calculate the effective area required per plant in square feet
// Guard against non-positive spacing to prevent division by zero or negative area
if (plantSpacingInRowFt <= 0 || rowSpacingFt <= 0 || totalGardenAreaSqFt <= 0) {
    return {
        optimalPlants: 0,
        projectedYieldTotal: 0,
        yieldPerSquareFoot: 0
    };
}
const spacePerPlantSqFt = plantSpacingInRowFt * rowSpacingFt;

// Calculate the raw number of plants that can fit, rounded down
const rawOptimalPlants = Math.floor(totalGardenAreaSqFt / spacePerPlantSqFt);

// Apply the survival rate to get the adjusted number of plants
const adjustedOptimalPlants = Math.floor(rawOptimalPlants * (survivalRate / 100));

// Calculate projected total harvest yield, factoring in multiple cycles
const projectedYieldTotal = adjustedOptimalPlants * yieldPerPlant * growingCycles;

// Calculate yield per square foot
const yieldPerSquareFoot = totalGardenAreaSqFt > 0 ? projectedYieldTotal / totalGardenAreaSqFt : 0;

return {
    optimalPlants: adjustedOptimalPlants,
    projectedYieldTotal: parseFloat(projectedYieldTotal.toFixed(2)),
    yieldPerSquareFoot: parseFloat(yieldPerSquareFoot.toFixed(2))
};
