function calculate({
  soilType,
  soilMoistureContent,
  permafrostTemperature,
  activeLayerThickness,
  predictedWarmingRate,
  projectLifetime,
  infrastructureLoadCategory,
  drainageEfficiency,
  groundwaterTableDepth,
  designSafetyFactor
}) {
  // Input validation and normalization
  const moisture = Math.max(0, Math.min(100, soilMoistureContent));
  const pfTemp = Math.max(-20, Math.min(0, permafrostTemperature));
  const activeLayer = Math.max(0.1, Math.min(10, activeLayerThickness));
  const warmingRate = Math.max(0, predictedWarmingRate);
  const lifetime = Math.max(1, projectLifetime);
  const drainage = Math.max(0, Math.min(100, drainageEfficiency));
  const gwDepth = Math.max(0.1, groundwaterTableDepth);
  const safetyFactor = Math.max(1.0, designSafetyFactor);

  // 1. Frost Susceptibility Factor (FSF) based on soil type and moisture
  let FSF_base = 0;
  switch (soilType) {
    case 'Silt': FSF_base = 0.9; break;
    case 'Clay': FSF_base = 0.8; break;
    case 'Fine Sand': FSF_base = 0.6; break;
    case 'Coarse Sand': FSF_base = 0.4; break;
    case 'Gravel': FSF_base = 0.2; break;
    default: FSF_base = 0.5; // Default for robustness
  }
  // Amplify FSF by moisture content (higher moisture, higher susceptibility)
  const FSF = FSF_base * (1 + (moisture / 100) * 0.7);

  // 2. Permafrost Stability Factor (PSF) based on temperature, active layer, and warming
  let PSF_temp_base = 0;
  if (pfTemp > -2) PSF_temp_base = 1.0; // Permafrost close to thawing point
  else if (pfTemp > -5) PSF_temp_base = 0.8; // Moderately cold
  else PSF_temp_base = 0.6; // Very cold, more stable

  // Adjust PSF by active layer thickness (deeper active layer implies more thermal disturbance and water movement)
  const PSF_active_layer_modifier = 1 + (activeLayer / 3) * 0.5; // Max 1.5x amplification for 3m active layer

  // Adjust PSF by predicted warming over project lifetime
  const totalWarming = warmingRate / 10 * lifetime;
  const PSF_warming_modifier = 1 + (totalWarming / 3) * 1.5; // Significant amplification for warming

  const PSF = PSF_temp_base * PSF_active_layer_modifier * PSF_warming_modifier;

  // 3. Drainage Impact Factor (DIF) - reduces risk by removing water
  const DIF = 1 - (drainage / 100) * 0.8; // Up to 80% reduction for 100% efficient drainage

  // 4. Groundwater Impact Factor (GIF) - higher water table increases risk
  let GIF = 1;
  if (gwDepth < 0.5) GIF = 2.0; // Very high water table
  else if (gwDepth < 1.5) GIF = 1.5;
  else if (gwDepth < 3.0) GIF = 1.2;
  else GIF = 1.0; // Deep water table, less impact

  // 5. Raw Heave Potential Index
  // Combine factors, scaling to a base index. Clamp to avoid extreme values.
  let heaveIndex = FSF * PSF * DIF * GIF * 100; 
  heaveIndex = Math.min(Math.max(heaveIndex, 10), 1000); // Index range 10-1000

  // 6. Estimated Potential Heave Magnitude (mm)
  // A simplified, non-linear relationship: higher index leads to disproportionately higher heave
  let predictedHeaveMM = Math.round(heaveIndex * 0.5 + Math.pow(heaveIndex / 100, 2) * 50);
  predictedHeaveMM = Math.min(predictedHeaveMM, 2000); // Cap at 2000mm (2 meters) for extreme cases
  predictedHeaveMM = predictedHeaveMM * safetyFactor; // Apply safety factor
  predictedHeaveMM = Math.round(predictedHeaveMM); // Round final heave

  // 7. Thaw Settlement Risk (derived primarily from permafrost stability and warming)
  const thawRiskScore = (PSF * 0.6 + totalWarming * 0.4) / (PSF_temp_base * 1 + 1.5 * 1.5); // Normalize to ~1
  let thawSettlementRisk = "Low";
  if (thawRiskScore > 1.2) thawSettlementRisk = "Critical";
  else if (thawRiskScore > 0.8) thawSettlementRisk = "High";
  else if (thawRiskScore > 0.4) thawSettlementRisk = "Moderate";

  // 8. Overall Frost Heave Risk Level
  // Incorporate heave potential, thaw risk, and infrastructure sensitivity
  let loadFactor = 1;
  switch (infrastructureLoadCategory) {
    case 'Light': loadFactor = 1.0; break;
    case 'Moderate': loadFactor = 1.5; break;
    case 'Heavy': loadFactor = 2.5; break;
  }

  const overallRiskScore = (predictedHeaveMM / 500) * loadFactor * safetyFactor + thawRiskScore * 0.5;

  let riskLevel = "Low";
  if (overallRiskScore > 4.0) riskLevel = "Critical";
  else if (overallRiskScore > 2.0) riskLevel = "High";
  else if (overallRiskScore > 1.0) riskLevel = "Moderate";

  // 9. Estimated Mitigation Cost Range (USD)
  // Base cost per mm of heave, amplified by risk level, load category, and safety factor
  const baseCostPerMM = 2000; // Base USD per mm for remediation
  let costLoadMultiplier = 1;
  switch (infrastructureLoadCategory) {
    case 'Light': costLoadMultiplier = 0.5; break;
    case 'Moderate': costLoadMultiplier = 1.5; break;
    case 'Heavy': costLoadMultiplier = 3.0; break;
  }

  // Higher risk means higher cost amplification
  const costRiskMultiplier = 1 + (overallRiskScore / 2);

  let mitigationCostEstimate = predictedHeaveMM * baseCostPerMM * costLoadMultiplier * costRiskMultiplier;

  // Round to nearest $10,000 for realistic estimation
  mitigationCostEstimate = Math.round(mitigationCostEstimate / 10000) * 10000;
  mitigationCostEstimate = Math.max(10000, mitigationCostEstimate); // Minimum cost

  return {
    riskLevel,
    predictedHeaveMM,
    thawSettlementRisk,
    mitigationCostEstimate
  };
}
