function calculate() {
    const officialNetWorth = inputs.officialNetWorth || 0;
    const blindTrustValue = inputs.blindTrustValue || 0;
    const liquidAssetRatio = inputs.liquidAssetRatio || 0;
    const impactedDealingsValue = inputs.impactedDealingsValue || 0;
    const policyImpactCount = inputs.policyImpactCount || 0;
    const disclosureCompliance = inputs.disclosureCompliance || 0;
    const familyLobbyingInfluence = inputs.familyLobbyingInfluence || 0;
    const significantNetWorthChangePercentage = inputs.significantNetWorthChangePercentage || 0;

    // --- Step 1: Calculate raw risk components based on potential for conflict --- 
    let baseRiskScore = 0;

    // Net Worth Factor: Higher net worth offers more opportunity for conflict. Cap at 10 points for $50M+
    baseRiskScore += Math.min(officialNetWorth / 5000000, 10); 

    // Liquid Asset Factor: Highly liquid assets are easier to manipulate or benefit from policy. Cap at 15 points.
    baseRiskScore += (liquidAssetRatio / 100) * 15; 

    // Impacted Dealings Factor: Direct financial dealings with entities affected by policy. Cap at 25 points for $5M+.
    baseRiskScore += Math.min(impactedDealingsValue / 200000, 25); 

    // Policy Impact Count Factor: More policies directly benefiting personal interests means higher risk. Cap at 30 points for 10+ policies.
    baseRiskScore += Math.min(policyImpactCount, 10) * 3; 

    // Family Influence Factor: Close family members' lobbying/financial interests. Cap at 30 points for 'High'.
    // 0=None (0 pts), 1=Low (10 pts), 2=Moderate (20 pts), 3=High (30 pts)
    baseRiskScore += familyLobbyingInfluence * 10; 

    // Net Worth Change Factor: Sudden, significant increases in net worth can indicate opportunistic behavior. Cap at 15 points for 150%+ change.
    baseRiskScore += Math.min(significantNetWorthChangePercentage / 10, 15); 

    // --- Step 2: Calculate mitigation factors based on transparency and blind trusts ---
    let mitigationScore = 0;

    // Blind Trust Reduction: Significant assets in a qualified blind trust reduce direct control. Max 30 points reduction.
    // Handle division by zero for officialNetWorth
    if (officialNetWorth > 0) {
        mitigationScore += Math.min((blindTrustValue / officialNetWorth) * 30, 30);
    } else if (blindTrustValue > 0) { // If net worth is 0 but blind trust has value, it implies high proportion, though edge case.
        mitigationScore += 30; // Max reduction if all assets (hypothetically) are in trust, even if total reported net worth is 0.
    }

    // Disclosure Compliance Reduction: High level of public disclosure reduces opacity. Max 20 points reduction.
    mitigationScore += (disclosureCompliance / 100) * 20; 

    // --- Step 3: Compute final risk score and clamp it between 0 and 100 ---
    let finalRiskScore = baseRiskScore - mitigationScore;
    finalRiskScore = Math.max(0, Math.min(finalRiskScore, 100)); // Ensure score is between 0 and 100

    // --- Step 4: Determine risk level based on the final score ---
    let riskLevel;
    if (finalRiskScore <= 25) {
        riskLevel = "Low";
    } else if (finalRiskScore <= 50) {
        riskLevel = "Moderate";
    } else if (finalRiskScore <= 75) {
        riskLevel = "High";
    } else {
        riskLevel = "Severe";
    }

    return {
        riskScore: finalRiskScore,
        riskLevel: riskLevel
    };
}
