function calculate({ initialInvestment, baseAnnualReturnRate, investmentHorizon, policyAnnualSubsidy, policyCostReductionRate, policyTaxIncentiveRate, policyInitialCapExReductionRate, inflationRate }) {

    // Sanitize and default inputs
    initialInvestment = parseFloat(initialInvestment) || 0;
    baseAnnualReturnRate = parseFloat(baseAnnualReturnRate) || 0;
    investmentHorizon = parseFloat(investmentHorizon) || 0;
    policyAnnualSubsidy = parseFloat(policyAnnualSubsidy) || 0;
    policyCostReductionRate = parseFloat(policyCostReductionRate) || 0;
    policyTaxIncentiveRate = parseFloat(policyTaxIncentiveRate) || 0;
    policyInitialCapExReductionRate = parseFloat(policyInitialCapExReductionRate) || 0;
    inflationRate = parseFloat(inflationRate) || 0;

    // Handle invalid horizon or initial investment for base calculations
    if (investmentHorizon <= 0) {
        return {
            effectiveInitialInvestment: 0,
            totalReturnBeforePolicy: 0,
            totalValueBeforePolicy: initialInvestment,
            roiBeforePolicy: 0,
            totalReturnAfterPolicy: 0,
            totalValueAfterPolicy: initialInvestment,
            netPolicyImpactValue: 0,
            roiAfterPolicy: 0,
            annualizedRoiAfterPolicy: 0
        };
    }

    // 1. Calculate Effective Initial Investment
    let effectiveInitialInvestment = initialInvestment * (1 - (policyInitialCapExReductionRate / 100));
    if (effectiveInitialInvestment < 0) effectiveInitialInvestment = 0; // Cap at 0

    // 2. Calculate Base Case (Before Policy Impact)
    let totalReturnBeforePolicy = 0;
    let totalValueBeforePolicy = initialInvestment;
    let roiBeforePolicy = 0;

    if (initialInvestment > 0) {
        totalReturnBeforePolicy = initialInvestment * (Math.pow(1 + baseAnnualReturnRate / 100, investmentHorizon) - 1);
        totalValueBeforePolicy = initialInvestment + totalReturnBeforePolicy;
        roiBeforePolicy = (totalReturnBeforePolicy / initialInvestment) * 100;
    } else if (initialInvestment === 0 && baseAnnualReturnRate > 0) {
        totalReturnBeforePolicy = Infinity; // Infinite return if no initial investment but positive growth
        totalValueBeforePolicy = Infinity;
        roiBeforePolicy = Infinity;
    }

    // 3. Calculate Policy-Adjusted Annual Return Rate (Simplified Aggregation)
    let policyAdjustedNominalReturnRate = (baseAnnualReturnRate + policyCostReductionRate + policyTaxIncentiveRate) / 100;

    // 4. Calculate Future Value of Annual Subsidies
    let fvSubsidies = 0;
    if (policyAnnualSubsidy > 0) {
        if (policyAdjustedNominalReturnRate === 0) {
            fvSubsidies = policyAnnualSubsidy * investmentHorizon;
        } else {
            // Future value of an ordinary annuity
            fvSubsidies = policyAnnualSubsidy * ((Math.pow(1 + policyAdjustedNominalReturnRate, investmentHorizon) - 1) / policyAdjustedNominalReturnRate);
        }
    }

    // 5. Calculate Total Return After Policy Impact (Nominal)
    let totalReturnAfterPolicy = 0;
    let totalValueAfterPolicy = effectiveInitialInvestment;
    let roiAfterPolicy = 0;
    let annualizedRoiAfterPolicy = 0;

    if (effectiveInitialInvestment > 0) {
        totalReturnAfterPolicy = effectiveInitialInvestment * (Math.pow(1 + policyAdjustedNominalReturnRate, investmentHorizon) - 1);
        totalReturnAfterPolicy += fvSubsidies;
        totalValueAfterPolicy = effectiveInitialInvestment + totalReturnAfterPolicy;

        roiAfterPolicy = (totalReturnAfterPolicy / effectiveInitialInvestment) * 100;

        // Annualized ROI calculation requires positive final value and initial investment
        if (totalValueAfterPolicy > 0 && effectiveInitialInvestment > 0) {
            annualizedRoiAfterPolicy = (Math.pow(totalValueAfterPolicy / effectiveInitialInvestment, 1 / investmentHorizon) - 1) * 100;
        }
    } else if (effectiveInitialInvestment === 0 && (policyAdjustedNominalReturnRate > 0 || fvSubsidies > 0)) {
        // If effective initial investment is 0 due to 100%+ CapEx reduction,
        // and there's a positive return/subsidy, it implies infinite ROI.
        totalReturnAfterPolicy = Infinity;
        totalValueAfterPolicy = Infinity;
        roiAfterPolicy = Infinity;
        annualizedRoiAfterPolicy = Infinity;
    } else if (effectiveInitialInvestment === 0 && policyAdjustedNominalReturnRate <= 0 && fvSubsidies <= 0) {
        // If no effective initial investment and no returns/subsidies
        totalReturnAfterPolicy = 0;
        totalValueAfterPolicy = 0;
        roiAfterPolicy = 0;
        annualizedRoiAfterPolicy = 0;
    }

    // 6. Calculate Net Policy Impact Value
    const netPolicyImpactValue = totalReturnAfterPolicy - totalReturnBeforePolicy;

    // Optional: Real returns (not directly outputted but can be mentioned in article)
    // if (inflationRate > 0) {
    //     const realEffectiveAnnualReturnRate = (1 + policyAdjustedNominalReturnRate) / (1 + inflationRate / 100) - 1;
    //     // Further calculations for real return would follow a similar structure
    // }

    return {
        effectiveInitialInvestment: effectiveInitialInvestment,
        totalReturnBeforePolicy: totalReturnBeforePolicy,
        totalValueBeforePolicy: totalValueBeforePolicy,
        roiBeforePolicy: roiBeforePolicy,
        totalReturnAfterPolicy: totalReturnAfterPolicy,
        totalValueAfterPolicy: totalValueAfterPolicy,
        netPolicyImpactValue: netPolicyImpactValue,
        roiAfterPolicy: roiAfterPolicy,
        annualizedRoiAfterPolicy: annualizedRoiAfterPolicy
    };
}
