function calculate({
    currentPortfolioValue,
    currentDiversificationScore,
    expectedPolicyImpactOnVulnerableAssets,
    percentageVulnerableAssets,
    policyShiftDurationYears,
    riskAversionLevel,
    opportunitySectorGrowthPotential,
    reallocationConstraintPercentage,
    taxEfficiencyOptimizationFactor
}) {
    const vulnerableAssetsValue = currentPortfolioValue * (percentageVulnerableAssets / 100);

    // Step 1: Quantify Initial Policy Risk and Potential Impact
    const initialPotentialLossOrGain = vulnerableAssetsValue * (expectedPolicyImpactOnVulnerableAssets / 100);

    // Initial risk score (higher score indicates higher risk)
    // Factors: percentage of vulnerable assets, magnitude of expected impact, inverse of diversification
    let initialPolicyRiskScore = 0;
    if (vulnerableAssetsValue > 0) {
        initialPolicyRiskScore = Math.min(10, Math.max(1, 
            (percentageVulnerableAssets / 100) * 
            (Math.abs(expectedPolicyImpactOnVulnerableAssets) / 10) * 
            ((11 - currentDiversificationScore) / 10) * 10
        ));
    } else {
        // If no vulnerable assets, initial risk is low.
        initialPolicyRiskScore = Math.max(1, currentDiversificationScore / 10 * 2); // Base risk based on diversification
    }

    // Step 2: Determine Optimal Reallocation Strategy
    const maxReallocationAmount = currentPortfolioValue * (reallocationConstraintPercentage / 100);

    // Determine a reallocation factor based on risk aversion and initial policy risk
    const riskDrivenReallocationFactor = Math.min(1, Math.max(0, 
        (riskAversionLevel / 5) * (initialPolicyRiskScore / 10) * (Math.abs(expectedPolicyImpactOnVulnerableAssets) > 0 ? 1 : 0.5) // Less reallocation if no direct impact
    ));

    // The proposed amount to reallocate from vulnerable assets
    let optimizedReallocationAmount = Math.min(vulnerableAssetsValue, maxReallocationAmount * riskDrivenReallocationFactor);

    // Apply a cap to ensure reallocation doesn't exceed available vulnerable assets
    optimizedReallocationAmount = Math.min(optimizedReallocationAmount, vulnerableAssetsValue);

    // Ensure reallocation is non-negative
    optimizedReallocationAmount = Math.max(0, optimizedReallocationAmount);

    // Calculate potential gain from reallocated funds into opportunity sectors
    const gainFromOpportunity = optimizedReallocationAmount * (opportunitySectorGrowthPotential / 100) * policyShiftDurationYears;

    // Factor in tax efficiency for the overall impact calculation, especially for mitigation.
    // A higher factor means less drag from taxes on gains or losses.
    const taxAdjustedGainFromOpportunity = gainFromOpportunity * (1 - (1 - taxEfficiencyOptimizationFactor) * 0.5); // Simplified impact

    // Step 3: Project Post-Adjustment Portfolio Performance and Risk
    // Net impact considers initial policy impact on remaining vulnerable assets and gains from reallocated assets
    const remainingVulnerableValue = vulnerableAssetsValue - optimizedReallocationAmount;
    const actualPolicyImpactOnRemainingVulnerable = remainingVulnerableValue * (expectedPolicyImpactOnVulnerableAssets / 100);

    const totalNetImpact = actualPolicyImpactOnRemainingVulnerable + taxAdjustedGainFromOpportunity;

    const projectedPortfolioValuePostShift = currentPortfolioValue + totalNetImpact;

    // Calculate adjusted risk score
    // Risk reduces with reallocation, but increases if risk aversion is low and opportunity sectors are volatile.
    let adjustedRiskScore = Math.max(1, Math.min(10, 
        initialPolicyRiskScore * (1 - (optimizedReallocationAmount / Math.max(1, vulnerableAssetsValue))) + 
        (optimizedReallocationAmount / Math.max(1, currentPortfolioValue)) * (riskAversionLevel / 5 * 5) // Higher reallocation to opportunity adds some risk
    ));
    // Further adjustment for risk aversion: higher aversion means we aim for lower adjusted risk.
    adjustedRiskScore = adjustedRiskScore * (1 - (riskAversionLevel / 5 - 0.5) * 0.1); // Slightly reduce risk if aversion is high
    adjustedRiskScore = Math.max(1, Math.min(10, adjustedRiskScore));

    // Projected annualized return change
    let projectedReturnChange = (totalNetImpact / currentPortfolioValue) * (100 / policyShiftDurationYears);
    // Cap return change to prevent unrealistic spikes or drops
    projectedReturnChange = Math.max(-50, Math.min(50, projectedReturnChange));

    // Policy mitigation effectiveness
    let policyMitigationEffectiveness = 0;
    if (vulnerableAssetsValue > 0) {
        // How much of the initial negative impact was avoided or offset?
        const initialNegativeImpact = Math.min(0, initialPotentialLossOrGain);
        const mitigatedImpact = (initialPotentialLossOrGain - actualPolicyImpactOnRemainingVulnerable) + (taxAdjustedGainFromOpportunity > 0 ? taxAdjustedGainFromOpportunity : 0); // Gain always mitigates

        if (initialNegativeImpact < 0) {
            policyMitigationEffectiveness = Math.min(100, Math.max(0, (mitigatedImpact / Math.abs(initialNegativeImpact)) * 100));
        } else if (initialNegativeImpact === 0 && optimizedReallocationAmount > 0) {
            policyMitigationEffectiveness = Math.min(100, (optimizedReallocationAmount / vulnerableAssetsValue) * 50); // Some mitigation if proactive
        } else {
            policyMitigationEffectiveness = 0;
        }
    } else {
        policyMitigationEffectiveness = 100; // No vulnerable assets, risk is fully mitigated from policy shift
    }

    // Edge cases for outputs
    if (currentPortfolioValue <= 0) {
        optimizedReallocationAmount = 0;
        projectedPortfolioValuePostShift = 0;
        adjustedRiskScore = 1; 
        projectedReturnChange = 0;
        policyMitigationEffectiveness = 100;
    }
    if (policyShiftDurationYears <= 0) {
        projectedReturnChange = (totalNetImpact / currentPortfolioValue) * 100; // Total change if duration is instant
    }

    return {
        optimizedReallocationAmount,
        projectedPortfolioValuePostShift,
        adjustedRiskScore,
        projectedReturnChange,
        policyMitigationEffectiveness
    };
}
