function calculate({
  totalCampaignBudget,
  targetVoterPoolSize,
  digitalAdSpendAllocation,
  traditionalMediaSpendAllocation,
  grassrootsOutreachSpendAllocation,
  averageEngagementRate,
  avgCostPerDigitalImpression,
  avgCostPerTraditionalReach,
  avgCostPerGrassrootsContact
}) {
  const EPSILON = 0.001; // For float comparison

  // Input validation and normalization
  if (totalCampaignBudget <= 0 || targetVoterPoolSize <= 0) {
    return {
      totalCampaignReach: 0,
      totalEngagedVoters: 0,
      costPerEngagedVoter: 0,
      influenceEffectivenessScore: 0,
      roiRatio: 0
    };
  }

  let sumAllocations = digitalAdSpendAllocation + traditionalMediaSpendAllocation + grassrootsOutreachSpendAllocation;

  // Normalize allocations if they don't sum precisely to 100 due to float arithmetic or user input
  let normalizedDigitalAdSpend = digitalAdSpendAllocation;
  let normalizedTraditionalMediaSpend = traditionalMediaSpendAllocation;
  let normalizedGrassrootsOutreachSpend = grassrootsOutreachSpendAllocation;

  if (Math.abs(sumAllocations - 100) > EPSILON) {
    const factor = 100 / sumAllocations;
    normalizedDigitalAdSpend *= factor;
    normalizedTraditionalMediaSpend *= factor;
    normalizedGrassrootsOutreachSpend *= factor;
  }

  // Calculate allocated budgets
  const digitalBudget = totalCampaignBudget * (normalizedDigitalAdSpend / 100);
  const traditionalBudget = totalCampaignBudget * (normalizedTraditionalMediaSpend / 100);
  const grassrootsBudget = totalCampaignBudget * (normalizedGrassrootsOutreachSpend / 100);

  // Calculate potential reach for each channel, handling zero costs and capping at targetVoterPoolSize
  let digitalReach = 0;
  if (avgCostPerDigitalImpression > 0) {
    digitalReach = Math.min(digitalBudget / avgCostPerDigitalImpression, targetVoterPoolSize * 5); // Cap at 5x target pool to prevent absurd numbers
  }

  let traditionalReach = 0;
  if (avgCostPerTraditionalReach > 0) {
    traditionalReach = Math.min(traditionalBudget / avgCostPerTraditionalReach, targetVoterPoolSize * 3); // Cap at 3x target pool
  }

  let grassrootsReach = 0;
  if (avgCostPerGrassrootsContact > 0) {
    grassrootsReach = Math.min(grassrootsBudget / avgCostPerGrassrootsContact, targetVoterPoolSize);
  }

  // Total potential campaign reach (sum of unique contacts/impressions, acknowledging potential overlap conceptually)
  const totalCampaignReach = digitalReach + traditionalReach + grassrootsReach; // This is a sum of unique channel reaches, not necessarily unique individuals

  // Calculate engaged voters per channel
  const engagementRateFactor = averageEngagementRate / 100;
  const digitalEngaged = digitalReach * engagementRateFactor;
  const traditionalEngaged = traditionalReach * engagementRateFactor;
  const grassrootsEngaged = grassrootsReach * engagementRateFactor;

  // Total estimated engaged voters, capped by the target voter pool size
  let totalEngagedVoters = Math.min(digitalEngaged + traditionalEngaged + grassrootsEngaged, targetVoterPoolSize);

  // Calculate Cost Per Engaged Voter
  let costPerEngagedVoter = 0;
  if (totalEngagedVoters > 0) {
    costPerEngagedVoter = totalCampaignBudget / totalEngagedVoters;
  }

  // Calculate Influence Effectiveness Score (percentage of target voters engaged)
  let influenceEffectivenessScore = 0;
  if (targetVoterPoolSize > 0) {
    influenceEffectivenessScore = (totalEngagedVoters / targetVoterPoolSize) * 100;
  }

  // Calculate ROI Ratio (engaged voters per $1000 spent)
  let roiRatio = 0;
  if (totalCampaignBudget > 0) {
    roiRatio = (totalEngagedVoters / totalCampaignBudget) * 1000;
  }

  return {
    totalCampaignReach: Math.round(totalCampaignReach),
    totalEngagedVoters: Math.round(totalEngagedVoters),
    costPerEngagedVoter: parseFloat(costPerEngagedVoter.toFixed(2)),
    influenceEffectivenessScore: parseFloat(influenceEffectivenessScore.toFixed(2)),
    roiRatio: parseFloat(roiRatio.toFixed(2))
  };
}
