function calculate({
    eventType,
    eventImpactScope,
    affectedSectorExposure,
    historicalMarketVolatility,
    policyClarityIndex,
    investorSentimentImpact,
    countryPoliticalStabilityIndex,
    durationOfImpact
}) {
    let volatilityScore = 0;

    // Step 1: Establish Base Event Volatility
    let baseEventVolatility = 0;
    switch (eventType) {
        case "National Election":
            baseEventVolatility = 20;
            break;
        case "Major Policy Shift":
            baseEventVolatility = 25;
            break;
        case "Geopolitical Conflict":
            baseEventVolatility = 40;
            break;
        case "Referendum":
            baseEventVolatility = 15;
            break;
        case "Leadership Transition":
            baseEventVolatility = 18;
            break;
        case "Regulatory Overhaul":
            baseEventVolatility = 22;
            break;
        case "International Sanctions":
            baseEventVolatility = 30;
            break;
        case "Other":
            baseEventVolatility = 10;
            break;
    }
    volatilityScore += baseEventVolatility;

    // Step 2: Adjust for Event Scope and Economic Exposure
    // Scope factor: 1 (local) to 5 (global) -> multiplier 0.2 to 1.0
    let scopeFactor = eventImpactScope / 5;
    // Exposure factor: 0% to 100% -> multiplier 0 to 1.0
    let exposureFactor = affectedSectorExposure / 100;

    // Combine scope and exposure impact additively to the score
    // Max additional score for this could be 40 points (0.8 * 50)
    volatilityScore += (scopeFactor * exposureFactor * 50);

    // Step 3: Integrate Historical Market Volatility
    // Scale historical volatility (e.g., VIX 10-50 mapped to +/-10-25 points)
    // Assuming 15 is a 'neutral' VIX, adjust by 0.7 per point deviation
    volatilityScore += (historicalMarketVolatility - 15) * 0.7;

    // Step 4: Account for Policy Clarity and Political Stability
    // Policy Clarity: 1 (high uncertainty) to 5 (high clarity)
    // Less clarity increases volatility. Max impact for 1 is 4 * 8 = 32 points
    volatilityScore += (5 - policyClarityIndex) * 8;

    // Political Stability: 0 (unstable) to 100 (stable)
    // Less stability increases volatility. Max impact for 0 is 100/5 = 20 points
    volatilityScore += (100 - countryPoliticalStabilityIndex) / 5;

    // Step 5: Incorporate Investor Sentiment
    // Sentiment: -2 (very negative) to +2 (very positive)
    // Negative sentiment increases volatility. Positive decreases.
    volatilityScore += (investorSentimentImpact * -1) * 12; // Each 1 point of negative sentiment adds 12 points

    // Step 6: Factor in Duration of Impact
    // Longer duration implies more sustained volatility.
    let durationMultiplier = 1;
    if (durationOfImpact > 12) {
        durationMultiplier = 1.3;
    } else if (durationOfImpact > 6) {
        durationMultiplier = 1.15;
    }
    volatilityScore *= durationMultiplier;

    // Edge case handling and capping
    volatilityScore = Math.max(0, volatilityScore); // Volatility score cannot be negative
    volatilityScore = Math.min(250, volatilityScore); // Cap max score to 250 for realism

    // Map volatility score to a projected market impact range (as a magnitude)
    // A score of 0-250 roughly maps to 0.5% - 25% potential market movement (e.g., daily/weekly average)
    let marketImpactEstimate = 0.5 + (volatilityScore / 250) * 24.5; // Min 0.5%, Max 25%
    marketImpactEstimate = parseFloat(marketImpactEstimate.toFixed(2));

    return {
        volatilityScore: parseFloat(volatilityScore.toFixed(1)),
        marketImpactEstimate: marketImpactEstimate
    };
}
