const clamp = (num, min, max) => Math.min(Math.max(num, min), max);

const messageWordCount = inputs.messageWordCount;
const technicalJargonRatio = inputs.technicalJargonRatio; 
const passiveVoiceRatio = inputs.passiveVoiceRatio;    
const sentimentPolarityScore = inputs.sentimentPolarityScore;
const ambiguityIndicatorsCount = inputs.ambiguityIndicatorsCount;
const averageSentenceLength = inputs.averageSentenceLength;
const readabilityIndexTarget = inputs.readabilityIndexTarget;
const userPerceivedComplexity = inputs.userPerceivedComplexity;

// --- Calculate Clarity Score ---
let clarity = 100; 

// Deductions for factors reducing clarity
clarity -= clamp(technicalJargonRatio * 0.8, 0, 40); // Max 40 points deduction for 50% jargon
clarity -= clamp(passiveVoiceRatio * 0.5, 0, 25);     // Max 25 points deduction for 50% passive voice
clarity -= clamp(ambiguityIndicatorsCount * 5, 0, 30); // Max 30 points deduction for 6 indicators

if (averageSentenceLength > 20) {
    clarity -= clamp((averageSentenceLength - 20) * 1, 0, 20); // Each word above 20 reduces by 1, max 20 points
}

if (messageWordCount > 200) {
    clarity -= clamp((messageWordCount - 200) * 0.05, 0, 15); // Over 200 words, deduct 0.05 per word, max 15 points
}

clarity -= clamp((userPerceivedComplexity - 1) * 3, 0, 27); // User perceived complexity: (1-10) -> (0-9) * 3 = max 27 points

clarity = clamp(clarity, 0, 100);

// --- Calculate Misunderstanding Potential ---
let misunderstanding = 0;

// Additions for factors increasing misunderstanding
misunderstanding += clamp(technicalJargonRatio * 0.8, 0, 40);
misunderstanding += clamp(passiveVoiceRatio * 0.5, 0, 25);
misunderstanding += clamp(ambiguityIndicatorsCount * 5, 0, 30);

if (averageSentenceLength > 20) {
    misunderstanding += clamp((averageSentenceLength - 20) * 1, 0, 20);
}

misunderstanding += clamp(sentimentPolarityScore * 0.3, 0, 30); // Higher extremity adds up to 30 points

// Simplified estimated readability for comparison: Flesch-Kincaid Grade Level approximation
// (0.39 * averageSentenceLength) + (11.8 * averageSyllablesPerWord) - 15.59 
// We don't have syllables, so a simpler heuristic: ASL * 0.5 + 6 is a very rough estimate.
const estimatedReadability = clamp((averageSentenceLength * 0.5) + 6, 1, 18); 
const readabilityDeviation = Math.abs(estimatedReadability - readabilityIndexTarget);
misunderstanding += clamp(readabilityDeviation * 2, 0, 20); // Each grade level deviation adds 2 points, max 20

misunderstanding += clamp((userPerceivedComplexity - 1) * 3, 0, 27);

misunderstanding = clamp(misunderstanding, 0, 100);

// --- Calculate Educational Impact Score ---
let impact = 0;

// High clarity strongly contributes to educational impact.
impact += clarity * 0.6; // Max 60 points

// Low misunderstanding potential boosts impact.
impact += (100 - misunderstanding) * 0.4; // Max 40 points (inverse relationship)

// Moderate sentiment polarity (30-70) can enhance engagement and thus impact, if clear.
let sentimentInfluence = 0;
if (sentimentPolarityScore >= 30 && sentimentPolarityScore <= 70) {
    sentimentInfluence = (50 - Math.abs(sentimentPolarityScore - 50)) * 0.2; // Max 10 points at 50
}
impact += clamp(sentimentInfluence, 0, 10);

// Alignment with readability target boosts educational impact.
impact += (10 - clamp(readabilityDeviation * 0.5, 0, 10)); // Max 10 points if deviation is 0

impact = clamp(impact, 0, 100);

return {
    clarityScore: parseFloat(clarity.toFixed(1)),
    misunderstandingPotential: parseFloat(misunderstanding.toFixed(1)),
    educationalImpactScore: parseFloat(impact.toFixed(1))
};
