function calculateRisk(inputs) {
  const {
    politicalStabilityIndex,
    policyPredictabilityIndex,
    regulatoryEnvironmentScore,
    ruleOfLawIndex,
    geopoliticalRiskExposure,
    economicVulnerabilityPercentage,
    sectorPolicySensitivity
  } = inputs;

  // Input validation and default to min/max if out of bounds
  const psi = Math.max(1, Math.min(10, politicalStabilityIndex));
  const ppi = Math.max(1, Math.min(10, policyPredictabilityIndex));
  const res = Math.max(1, Math.min(10, regulatoryEnvironmentScore));
  const rli = Math.max(1, Math.min(10, ruleOfLawIndex));
  const gre = Math.max(1, Math.min(10, geopoliticalRiskExposure));
  const evp = Math.max(0, Math.min(100, economicVulnerabilityPercentage));
  const sps = Math.max(1, Math.min(10, sectorPolicySensitivity));

  // Step 1: Normalize 'good is high' scores to a 'risk contribution' (0-1 scale)
  // Higher score (e.g., 10) means lower risk, so (10 - score) / 9 gives lower risk contribution.
  const normPoliticalStabilityRisk = (10 - psi) / 9;
  const normPolicyPredictabilityRisk = (10 - ppi) / 9;
  const normRegulatoryEnvironmentRisk = (10 - res) / 9;
  const normRuleOfLawRisk = (10 - rli) / 9;

  // Step 2: Normalize 'risk is high' scores to a 'risk contribution' (0-1 scale)
  // Higher score (e.g., 10) means higher risk, so (score - 1) / 9 gives higher risk contribution.
  const normGeopoliticalRisk = (gre - 1) / 9;

  // Step 3: Normalize economic vulnerability percentage to a risk contribution (0-1 scale)
  const normEconomicVulnerabilityRisk = evp / 100;

  // Step 4: Define weights for core risk factors (sum should be 1.0)
  const weights = {
    politicalStability: 0.20,
    policyPredictability: 0.25,
    regulatoryEnvironment: 0.15,
    ruleOfLaw: 0.15,
    geopoliticalRisk: 0.10,
    economicVulnerability: 0.15
  };

  // Step 5: Calculate weighted core risk score
  const weightedCoreRisk = 
    (normPoliticalStabilityRisk * weights.politicalStability) +
    (normPolicyPredictabilityRisk * weights.policyPredictability) +
    (normRegulatoryEnvironmentRisk * weights.regulatoryEnvironment) +
    (normRuleOfLawRisk * weights.ruleOfLaw) +
    (normGeopoliticalRisk * weights.geopoliticalRisk) +
    (normEconomicVulnerabilityRisk * weights.economicVulnerability);

  // Step 6: Calculate sector sensitivity multiplier
  // A score of 1 means 0 sensitivity (multiplier of 0), a score of 10 means max sensitivity (multiplier of 1)
  const sensitivityMultiplier = (sps - 1) / 9; // Range 0 to 1

  // Step 7: Apply sector sensitivity to the core risk
  // We add 1 to the multiplier to ensure the core risk is always present, even for min sensitivity.
  // Max rawRiskScore: 1.0 (weightedCoreRisk) * (1 + 1.0 (sensitivityMultiplier)) = 2.0
  const rawRiskScore = weightedCoreRisk * (1 + sensitivityMultiplier);

  // Step 8: Scale the raw risk score to a 0-100 range
  // Max possible rawRiskScore is 2.0, so multiply by 50 to get a max of 100.
  let investmentRiskScore = Math.min(100, Math.max(0, rawRiskScore * 50));

  // Round to one decimal place
  investmentRiskScore = parseFloat(investmentRiskScore.toFixed(1));

  // Step 9: Determine risk category and recommendation
  let riskCategory;
  let recommendation;

  if (investmentRiskScore <= 20) {
    riskCategory = "Low Risk";
    recommendation = "The political and policy environment appears stable and predictable. While no investment is without risk, this context suggests a relatively favorable outlook. Continue due diligence on specific project risks.";
  } else if (investmentRiskScore <= 45) {
    riskCategory = "Moderate Risk";
    recommendation = "The political and policy landscape presents moderate challenges. Investors should monitor policy shifts, regulatory changes, and potential geopolitical tensions closely. Diversification and contingency planning are advisable.";
  } else if (investmentRiskScore <= 75) {
    riskCategory = "High Risk";
    recommendation = "Significant political and policy uncertainties exist, posing substantial investment risks. Thorough risk mitigation strategies, including political risk insurance and careful local partnership selection, are critical. High volatility is to be expected.";
  } else {
    riskCategory = "Very High Risk";
    recommendation = "The environment is highly volatile with considerable political and policy-driven investment risks. Extreme caution is advised. Only investors with a high-risk tolerance and comprehensive strategies for navigating instability should consider engagement.";
  }

  return {
    investmentRiskScore,
    riskCategory,
    recommendation
  };
}
