const inputs = data;

const poolVolume = parseFloat(inputs.poolVolume);
const volumeUnit = inputs.volumeUnit || 'Gallons';
let desiredTurnoverHours = parseFloat(inputs.desiredTurnoverHours);
const isSpa = inputs.isSpa === true || inputs.isSpa === 'true' || inputs.isSpa === 1;
const isCommercial = inputs.isCommercial === true || inputs.isCommercial === 'true' || inputs.isCommercial === 1;
const filterType = inputs.filterType ? inputs.filterType.toLowerCase() : 'sand';
const safetyFactor = parseFloat(inputs.safetyFactor || 0);

// --- Input Validation and Defaulting ---
if (isNaN(poolVolume) || poolVolume <= 0) {
    throw new Error("Pool/Spa Volume must be a positive number.");
}
if (safetyFactor < 0 || safetyFactor > 100) {
    throw new Error("Operational Safety Factor must be between 0 and 100%.");
}

let effectivePoolVolumeGallons = poolVolume;
if (volumeUnit === 'Liters') {
    effectivePoolVolumeGallons = poolVolume * 0.264172; // Convert Liters to Gallons
}

// Default desired turnover hours if not provided or invalid
if (isNaN(desiredTurnoverHours) || desiredTurnoverHours <= 0) {
    if (isSpa) {
        desiredTurnoverHours = isCommercial ? 0.125 : 0.5; // Commercial Spa: 7.5 mins, Res Spa: 30 mins
    } else {
        desiredTurnoverHours = isCommercial ? 6 : 8; // Commercial Pool: 6 hrs, Res Pool: 8 hrs (PHTA/APSP standards vary, often 6 for commercial)
    }
}

// --- Core Calculations ---
// 1. Required Flow Rate (GPM)
let requiredFlowRateGPM = effectivePoolVolumeGallons / desiredTurnoverHours / 60; // GPM

// Apply safety factor (e.g., for future needs, minor inefficiencies)
requiredFlowRateGPM *= (1 + safetyFactor / 100);

// 2. Turnover Per Day
const turnoverPerDay = 24 / desiredTurnoverHours;

// 3. Filter Sizing
let recommendedFilterAreaSqFt = 0;
let cartridgeFilterGPMCapacity = 0; // Output for cartridge filters

// Define typical design filtration rates (GPM/SqFt) based on PHTA/APSP guidelines
const filtrationRates = {
    sand: {
        residential: { pool: 15, spa: 20 }, // GPM/SqFt. Spas often allow higher rates due to smaller particles
        commercial: { pool: 10, spa: 15 }   // Commercial generally has lower rates for better filtration given high bather load
    },
    de: {
        residential: { pool: 2.0, spa: 2.5 }, // GPM/SqFt
        commercial: { pool: 1.5, spa: 2.0 }   // GPM/SqFt
    },
    cartridge: {
        residential: { pool: 0.375, spa: 0.375 }, // GPM/SqFt. Cartridge filters usually operate at lower rates.
        commercial: { pool: 0.25, spa: 0.25 }     // GPM/SqFt
    }
};

let filterRateCategory = isCommercial ? 'commercial' : 'residential';
let filterRateType = isSpa ? 'spa' : 'pool';
let effectiveFiltrationRate = 0;

if (filterType === 'sand' || filterType === 'de') {
    if (filtrationRates[filterType] && filtrationRates[filterType][filterRateCategory] && filtrationRates[filterType][filterRateCategory][filterRateType]) {
        effectiveFiltrationRate = filtrationRates[filterType][filterRateCategory][filterRateType];
    } else {
        effectiveFiltrationRate = filtrationRates.sand.residential.pool; // Fallback
    }
    recommendedFilterAreaSqFt = requiredFlowRateGPM / effectiveFiltrationRate;
} else if (filterType === 'cartridge') {
    // For cartridge filters, we recommend a GPM capacity based on required flow, often oversized by 50% for longevity/efficiency.
    cartridgeFilterGPMCapacity = requiredFlowRateGPM * 1.5; 

    // Also calculate a theoretical area based on typical GPM/SqFt for comparison or systems that still use it for sizing.
    if (filtrationRates.cartridge && filtrationRates.cartridge[filterRateCategory] && filtrationRates.cartridge[filterRateCategory][filterRateType]) {
        effectiveFiltrationRate = filtrationRates.cartridge[filterRateCategory][filterRateType];
    } else {
        effectiveFiltrationRate = filtrationRates.cartridge.residential.pool; // Fallback
    }
    recommendedFilterAreaSqFt = requiredFlowRateGPM / effectiveFiltrationRate;
} else {
    throw new Error("Unsupported filter type. Please choose Sand, DE, or Cartridge.");
}

// 4. Approximate Pump HP (Very rough estimate, heavily dependent on Total Dynamic Head (TDH))
// This is a highly generalized estimate for guidance, actual pump sizing requires TDH calculations.
// A very rough heuristic: 1 HP can typically manage 40-70 GPM in common residential setups.
let approxPumpHP = requiredFlowRateGPM / 55; // Using an average GPM/HP for a generalized estimate

// Ensure a practical minimum/maximum for general estimation
if (approxPumpHP < 0.25) approxPumpHP = 0.25; 
if (approxPumpHP > 7.5) approxPumpHP = 7.5; // Cap for general pool/spa estimation, commercial can go much higher.


// Prepare outputs in both Imperial and Metric where applicable
return {
    requiredFlowRate: {
        imperial: requiredFlowRateGPM,
        metric: requiredFlowRateGPM * 3.78541 // GPM to LPM
    },
    turnoverPerDay: turnoverPerDay,
    recommendedFilterAreaSandDE: {
        imperial: (filterType === 'sand' || filterType === 'de') ? recommendedFilterAreaSqFt : 0,
        metric: (filterType === 'sand' || filterType === 'de') ? recommendedFilterAreaSqFt * 0.092903 : 0 // Sq Ft to Sq M
    },
    cartridgeFilterCapacity: {
        imperial: (filterType === 'cartridge') ? cartridgeFilterGPMCapacity : 0,
        metric: (filterType === 'cartridge') ? cartridgeFilterGPMCapacity * 3.78541 : 0 // GPM to LPM
    },
    approxPumpHP: approxPumpHP
};
