const totalCurrentValue = inputs.current_total_portfolio_value;
const targetStocksPct = inputs.target_stocks_percentage / 100;
const targetBondsPct = inputs.target_bonds_percentage / 100;
const targetCashPct = inputs.target_cash_percentage / 100;
const transactionCostRate = inputs.transaction_cost_percentage ? inputs.transaction_cost_percentage / 100 : 0;
const rebalanceThresholdPct = inputs.rebalance_threshold_percentage ? inputs.rebalance_threshold_percentage / 100 : 0.01; // Default to 1% threshold if not provided

const targetStocksValue = totalCurrentValue * targetStocksPct;
const targetBondsValue = totalCurrentValue * targetBondsPct;
const targetCashValue = totalCurrentValue * targetCashPct;

const currentStocksPct = inputs.current_stocks_value / totalCurrentValue;
const currentBondsPct = inputs.current_bonds_value / totalCurrentValue;
const currentCashPct = inputs.current_cash_value / totalCurrentValue;

let stocksAction = 'Hold';
let stocksAmount = 0;
let bondsAction = 'Hold';
let bondsAmount = 0;
let cashAction = 'Hold';
let cashAmount = 0;

let estimatedTransactionCost = 0;
let totalRebalancedValue = 0; // Sum of absolute amounts transacted

// Stocks rebalance logic
if (Math.abs(currentStocksPct - targetStocksPct) > rebalanceThresholdPct) {
    stocksAmount = Math.abs(targetStocksValue - inputs.current_stocks_value);
    stocksAction = (targetStocksValue > inputs.current_stocks_value) ? 'Buy' : 'Sell';
    estimatedTransactionCost += stocksAmount * transactionCostRate;
    totalRebalancedValue += stocksAmount;
}

// Bonds rebalance logic
if (Math.abs(currentBondsPct - targetBondsPct) > rebalanceThresholdPct) {
    bondsAmount = Math.abs(targetBondsValue - inputs.current_bonds_value);
    bondsAction = (targetBondsValue > inputs.current_bonds_value) ? 'Buy' : 'Sell';
    estimatedTransactionCost += bondsAmount * transactionCostRate;
    totalRebalancedValue += bondsAmount;
}

// Cash rebalance logic
// Note: Cash rebalance often occurs as a result of stock/bond rebalancing, but we calculate its direct deviation.
if (Math.abs(currentCashPct - targetCashPct) > rebalanceThresholdPct) {
    cashAmount = Math.abs(targetCashValue - inputs.current_cash_value);
    cashAction = (targetCashValue > inputs.current_cash_value) ? 'Increase Holding' : 'Decrease Holding'; // Use more descriptive terms for cash
    // Usually no direct transaction cost for moving cash, unless it's conversion, but we track the movement.
    totalRebalancedValue += cashAmount;
}

const totalPortfolioValueAfterCosts = totalCurrentValue - estimatedTransactionCost;

return {
    rebalance_stocks_action: stocksAction,
    rebalance_stocks_amount: stocksAmount,
    rebalance_bonds_action: bondsAction,
    rebalance_bonds_amount: bondsAmount,
    rebalance_cash_action: cashAction,
    rebalance_cash_amount: cashAmount,
    estimated_transaction_cost: estimatedTransactionCost,
    total_rebalanced_value: totalRebalancedValue,
    total_portfolio_value_after_costs: totalPortfolioValueAfterCosts
};
