function formula(inputs) {
    const portfolioAnnualReturn = inputs.portfolioAnnualReturn / 100;
    const portfolioAnnualStdDev = inputs.portfolioAnnualStdDev / 100;
    const portfolioAnnualDownsideDeviation = inputs.portfolioAnnualDownsideDeviation / 100;
    const riskFreeRate = inputs.riskFreeRate / 100;
    const minimumAcceptableReturn = inputs.minimumAcceptableReturn / 100;
    const benchmarkReturn = inputs.benchmarkReturn / 100;
    const portfolioBeta = inputs.portfolioBeta;

    let sharpeRatio = NaN;
    let sortinoRatio = NaN;
    let treynorRatio = NaN;
    let jensensAlpha = NaN;

    const excessReturnPortfolio = portfolioAnnualReturn - riskFreeRate;

    // Sharpe Ratio calculation
    if (portfolioAnnualStdDev > 0) {
        sharpeRatio = excessReturnPortfolio / portfolioAnnualStdDev;
    } else {
        if (excessReturnPortfolio > 0) sharpeRatio = Infinity;
        else if (excessReturnPortfolio < 0) sharpeRatio = -Infinity;
        else sharpeRatio = 0; 
    }

    // Sortino Ratio calculation
    const excessReturnAboveMAR = portfolioAnnualReturn - minimumAcceptableReturn;
    if (portfolioAnnualDownsideDeviation > 0) {
        sortinoRatio = excessReturnAboveMAR / portfolioAnnualDownsideDeviation;
    } else {
        if (excessReturnAboveMAR > 0) sortinoRatio = Infinity;
        else if (excessReturnAboveMAR < 0) sortinoRatio = -Infinity;
        else sortinoRatio = 0; 
    }

    // Treynor Ratio calculation
    if (portfolioBeta !== 0) {
        treynorRatio = excessReturnPortfolio / portfolioBeta;
    } else {
        treynorRatio = NaN;
    }

    // Jensen's Alpha calculation
    const benchmarkExcessReturn = benchmarkReturn - riskFreeRate;
    const expectedReturnUsingCAPM = riskFreeRate + portfolioBeta * benchmarkExcessReturn;
    jensensAlpha = (portfolioAnnualReturn - expectedReturnUsingCAPM) * 100; 

    return {
        sharpeRatio: sharpeRatio,
        sortinoRatio: sortinoRatio,
        treynorRatio: treynorRatio,
        jensensAlpha: jensensAlpha
    };
}
