const WEEKS_PER_MONTH = 4.33; 

const adaptiveEquipment = inputs.adaptiveEquipmentCost || 0;
const homeModification = inputs.homeModificationCost || 0;
const contingencyPercentage = (inputs.contingencyFundPercentage || 0) / 100; 

const durationMonths = Math.max(1, inputs.rehabDurationMonths);
const durationWeeks = durationMonths * WEEKS_PER_MONTH;

// 1. Calculate Medical & Therapy Out-of-Pocket Cost
const totalTherapyCost = inputs.weeklyTherapyCost * durationWeeks;
const medicalTherapyTotal = inputs.initialMedicalBills + totalTherapyCost;

// 2. Calculate Lost Wages
const lostIncomePercentage = inputs.rehabImpactOnIncomePercentage / 100;
const weeklyLostIncome = inputs.preInjuryWeeklyIncome * lostIncomePercentage;
const totalLostWages = weeklyLostIncome * durationWeeks;

// 3. Calculate Equipment and Modification Costs
const equipmentModTotal = adaptiveEquipment + homeModification;

// 4. Sum base costs
const baseTotalCost = medicalTherapyTotal + totalLostWages + equipmentModTotal;

// 5. Calculate Contingency Fund
const contingencyAllocation = baseTotalCost * contingencyPercentage;

// 6. Calculate Final Total Estimated Cost
const totalEstimatedCost = baseTotalCost + contingencyAllocation;

return {
    totalEstimatedCost: totalEstimatedCost,
    medicalTherapyCost: medicalTherapyTotal,
    lostWagesTotal: totalLostWages,
    equipmentModCost: equipmentModTotal,
    contingencyCost: contingencyAllocation
};
