function calculate({ initialPopulation, casualtiesDirect, healthInfrastructureDamage, preConflictHealthPersonnelPer1000, conflictDurationMonths, humanitarianAidIndex, preExistingVulnerabilityIndex }) {

  // Constants and Base Values
  const BASE_RECOVERY_TIME_YEARS_PER_MILLION = 5; // Base years per million population
  const HEALTH_INFRA_RECONSTRUCTION_COST_PER_CAPITA_USD = 100; // Base cost per capita for moderate damage
  const TARGET_HEALTH_PERSONNEL_PER_1000 = 4.5; // WHO recommended minimum for essential services, adjust for post-conflict goal
  const PERSONNEL_TRAINING_COST_PER_PERSON_USD = 15000; // Average cost for training/recruitment
  const BASE_DISEASE_BURDEN_FACTOR = 1.5; // Baseline for post-conflict disease risk

  // Input Validation and Edge Cases
  if (initialPopulation <= 0 || casualtiesDirect < 0 || healthInfrastructureDamage < 0 || healthInfrastructureDamage > 100 || preConflictHealthPersonnelPer1000 < 0 || conflictDurationMonths <= 0 || humanitarianAidIndex < 1 || humanitarianAidIndex > 5 || preExistingVulnerabilityIndex < 1 || preExistingVulnerabilityIndex > 5) {
    return {
      estimatedRecoveryYears: NaN,
      requiredFundingMillionsUSD: NaN,
      additionalHealthPersonnelNeeded: NaN,
      postConflictDiseaseBurdenFactor: NaN
    };
  }

  // 1. Calculate Base Recovery Time
  let estimatedRecoveryYears = (initialPopulation / 1000000) * BASE_RECOVERY_TIME_YEARS_PER_MILLION;

  // 2. Adjust for Casualties
  // Each 1% of population lost (direct deaths) adds an equivalent percentage to recovery time
  const casualtyImpactFactor = Math.min(casualtiesDirect / initialPopulation, 0.2); // Cap at 20% of population loss impact
  estimatedRecoveryYears *= (1 + casualtyImpactFactor * 5); // 5x multiplier for casualty impact on time

  // 3. Adjust for Health Infrastructure Damage
  // Damage directly increases recovery time and funding
  estimatedRecoveryYears *= (1 + (healthInfrastructureDamage / 100) * 1.5); // 1.5x multiplier for damage impact on time

  // 4. Adjust for Conflict Duration
  // Longer conflicts exacerbate issues, even post-conflict
  estimatedRecoveryYears *= (1 + (conflictDurationMonths / 12) * 0.1); // 10% increase per year of conflict

  // 5. Adjust for Pre-Existing Vulnerability
  // Higher vulnerability index means slower recovery
  estimatedRecoveryYears *= (1 + (preExistingVulnerabilityIndex - 1) * 0.15); // 15% increase per vulnerability point

  // 6. Mitigate with Humanitarian Aid
  // Higher aid index means faster recovery (negative multiplier)
  const aidMitigationFactor = (6 - humanitarianAidIndex) / 5; // 1 for minimal aid, 0.2 for extensive aid
  estimatedRecoveryYears *= aidMitigationFactor;

  // Cap minimum recovery time to prevent unrealistically low figures
  estimatedRecoveryYears = Math.max(estimatedRecoveryYears, 2); 

  // --- Funding Calculation ---
  let requiredFundingUSD = 0;

  // Infrastructure Reconstruction Cost
  requiredFundingUSD += initialPopulation * HEALTH_INFRA_RECONSTRUCTION_COST_PER_CAPITA_USD * (healthInfrastructureDamage / 100) * 1.2; // 20% buffer

  // Personnel Cost (Recruitment & Training)
  const currentHealthPersonnel = (initialPopulation / 1000) * preConflictHealthPersonnelPer1000;
  const targetHealthPersonnel = (initialPopulation / 1000) * TARGET_HEALTH_PERSONNEL_PER_1000;
  let additionalHealthPersonnelNeeded = Math.max(0, targetHealthPersonnel - currentHealthPersonnel);
  // Account for potential loss of existing personnel due to conflict (assume 2x casualty rate for health personnel if high damage)
  if (healthInfrastructureDamage > 50) {
      additionalHealthPersonnelNeeded += (casualtiesDirect / initialPopulation) * currentHealthPersonnel * 2; // Rough estimate of lost personnel
  }
  additionalHealthPersonnelNeeded = Math.ceil(additionalHealthPersonnelNeeded); // Round up to full person

  requiredFundingUSD += additionalHealthPersonnelNeeded * PERSONNEL_TRAINING_COST_PER_PERSON_USD;

  // Operational & Programmatic Costs (proportional to recovery time and population)
  requiredFundingUSD += (initialPopulation / 1000000) * estimatedRecoveryYears * 50000000; // 50M USD per million pop per recovery year

  // Adjust funding for vulnerability (increases cost) and aid (reduces direct burden, but model calculates 'needed')
  requiredFundingUSD *= (1 + (preExistingVulnerabilityIndex - 1) * 0.1);
  // Aid might reduce the *funded* portion, but not the *required* portion. We are estimating what's *needed*.

  // --- Disease Burden Factor Calculation ---
  let postConflictDiseaseBurdenFactor = BASE_DISEASE_BURDEN_FACTOR;
  postConflictDiseaseBurdenFactor += (casualtiesDirect / initialPopulation) * 10; // High casualties, higher burden
  postConflictDiseaseBurdenFactor += (healthInfrastructureDamage / 100) * 2; // Damage, higher burden
  postConflictDiseaseBurdenFactor += (conflictDurationMonths / 12) * 0.5; // Longer conflict, higher burden
  postConflictDiseaseBurdenFactor += (preExistingVulnerabilityIndex - 1) * 0.8; // Vulnerability amplifies burden
  postConflictDiseaseBurdenFactor -= (humanitarianAidIndex - 1) * 0.3; // Aid mitigates burden

  // Cap and floor for disease burden factor
  postConflictDiseaseBurdenFactor = Math.max(1.0, Math.min(postConflictDiseaseBurdenFactor, 5.0));

  // Final Output Formatting
  const requiredFundingMillionsUSD = requiredFundingUSD / 1000000;

  return {
    estimatedRecoveryYears: parseFloat(estimatedRecoveryYears.toFixed(1)),
    requiredFundingMillionsUSD: parseFloat(requiredFundingMillionsUSD.toFixed(2)),
    additionalHealthPersonnelNeeded: Math.round(additionalHealthPersonnelNeeded),
    postConflictDiseaseBurdenFactor: parseFloat(postConflictDiseaseBurdenFactor.toFixed(1))
  };
}
