function calculate(inputs) {
    const {
        project_scope_sqm,
        infrastructure_complexity_factor,
        material_cost_index,
        local_labor_cost_per_day_usd,
        security_risk_level,
        logistics_challenge_level,
        project_management_overhead_percent,
        contingency_reserve_percent,
        long_term_maintenance_years
    } = inputs;

    // Input validation and default values for robustness
    const scope = Math.max(0, project_scope_sqm || 0);
    const complexity = Math.max(1, Math.min(5, infrastructure_complexity_factor || 1));
    const matIndex = Math.max(0.5, Math.min(2.0, material_cost_index || 1.0));
    const laborCostDay = Math.max(0, local_labor_cost_per_day_usd || 0);
    const securityLevel = Math.max(1, Math.min(5, security_risk_level || 1));
    const logisticsLevel = Math.max(1, Math.min(3, logistics_challenge_level || 1));
    const pmOverhead = Math.max(0, project_management_overhead_percent || 0);
    const contingency = Math.max(0, contingency_reserve_percent || 0);
    const maintenanceYears = Math.max(0, long_term_maintenance_years || 0);

    // Constants for baseline estimations (can be adjusted for regional context)
    const BASE_MATERIAL_COST_PER_SQM = 400; // USD per square meter for baseline infrastructure
    const BASE_LABOR_HOURS_PER_SQM = 1.5;   // Hours per square meter for baseline infrastructure
    const HOURS_PER_LABOR_DAY = 8;
    const ANNUAL_MAINTENANCE_RATE_PERCENT = 0.02; // 2% of initial total cost annually

    // Step 1: Calculate Base Direct Construction Costs (Materials + Labor)
    const baseMaterialCost = scope * BASE_MATERIAL_COST_PER_SQM * matIndex;
    // Labor cost is adjusted by complexity and converted from daily rate to hourly
    const baseLaborCost = scope * complexity * BASE_LABOR_HOURS_PER_SQM * (laborCostDay / HOURS_PER_LABOR_DAY);
    const base_direct_cost_usd = baseMaterialCost + baseLaborCost;

    // Step 2: Apply Security Risk Premium
    let securityMultiplier;
    switch (securityLevel) {
        case 1: securityMultiplier = 0.00; break; // Low Risk
        case 2: securityMultiplier = 0.10; break; // Moderate Risk
        case 3: securityMultiplier = 0.25; break; // High Risk
        case 4: securityMultiplier = 0.50; break; // Very High Risk
        case 5: securityMultiplier = 1.00; break; // Extreme Risk (doubles the base direct cost)
        default: securityMultiplier = 0.00;
    }
    const security_risk_premium_usd = base_direct_cost_usd * securityMultiplier;

    // Step 3: Apply Logistics Challenge Premium
    let logisticsMultiplier;
    switch (logisticsLevel) {
        case 1: logisticsMultiplier = 0.05; break; // Minor challenges
        case 2: logisticsMultiplier = 0.15; break; // Moderate disruptions
        case 3: logisticsMultiplier = 0.30; break; // Severe disruptions, damaged infrastructure
        default: logisticsMultiplier = 0.00;
    }
    const logistics_premium_usd = base_direct_cost_usd * logisticsMultiplier;

    // Step 4: Calculate Subtotal for Direct and Risk-Adjusted Costs
    const subtotal1_cost = base_direct_cost_usd + security_risk_premium_usd + logistics_premium_usd;

    // Step 5: Add Project Management and Administrative Overhead
    const project_management_cost_usd = subtotal1_cost * (pmOverhead / 100);
    const subtotal2_cost = subtotal1_cost + project_management_cost_usd;

    // Step 6: Add Contingency Reserve
    const contingency_reserve_usd = subtotal2_cost * (contingency / 100);

    // Step 7: Calculate Total Estimated Rebuilding Cost
    const total_estimated_cost_usd = subtotal2_cost + contingency_reserve_usd;

    // Step 8: Estimate Long-Term Maintenance Costs
    const annualMaintenanceCost = total_estimated_cost_usd * ANNUAL_MAINTENANCE_RATE_PERCENT;
    const total_maintenance_cost_usd = annualMaintenanceCost * maintenanceYears;

    return {
        total_estimated_cost_usd,
        base_direct_cost_usd,
        security_risk_premium_usd,
        logistics_premium_usd,
        project_management_cost_usd,
        contingency_reserve_usd,
        total_maintenance_cost_usd
    };
}
