const zoneAreaSqM = inputs.zoneAreaSqM;
const damagePercentage = Math.max(0, Math.min(100, inputs.damagePercentage));
const baseConstructionCostPerSqM = inputs.baseConstructionCostPerSqM;
const localMaterialAvailabilityFactor = Math.max(0.1, Math.min(1.0, inputs.localMaterialAvailabilityFactor));
const skilledLaborAvailabilityFactor = Math.max(0.1, Math.min(1.0, inputs.skilledLaborAvailabilityFactor));
const securityPremiumFactor = Math.max(1.0, Math.min(3.0, inputs.securityPremiumFactor));
const logisticsDifficultyFactor = Math.max(1.0, Math.min(2.5, inputs.logisticsDifficultyFactor));
const projectManagementOverheadPercentage = Math.max(0, Math.min(50, inputs.projectManagementOverheadPercentage));
const contingencyPercentage = Math.max(0, Math.min(40, inputs.contingencyPercentage));

// Input validation: Ensure core numeric inputs are positive
if ([zoneAreaSqM, baseConstructionCostPerSqM].some(val => val < 0)) {
    return {
        totalEstimatedCostUSD: 0,
        materialsCostUSD: 0,
        laborCostUSD: 0,
        securityLogisticsCostUSD: 0,
        projectManagementContingencyCostUSD: 0,
        costPerSqMReconstructionUSD: 0
    };
}

// Step 1: Calculate the base cost for the damaged portion.
// Assuming a 40% material, 60% labor split for standard base construction.
const effectiveDamagedAreaSqM = zoneAreaSqM * (damagePercentage / 100);
const baseReconstructionCost = effectiveDamagedAreaSqM * baseConstructionCostPerSqM;

let materialsCost = baseReconstructionCost * 0.4; // Initial material cost assumption
let laborCost = baseReconstructionCost * 0.6;   // Initial labor cost assumption

// Step 2: Adjust Material and Labor Costs based on local availability factors.
// A higher unavailability (1 - factor) leads to a premium for sourcing/importing.
// Premium for non-local materials: (1 - availability) * 70% premium multiplier.
materialsCost *= (1 + (1 - localMaterialAvailabilityFactor) * 0.7);

// Premium for scarce skilled labor: (1 - availability) * 100% premium multiplier.
laborCost *= (1 + (1 - skilledLaborAvailabilityFactor) * 1.0);

const constructionSubtotal = materialsCost + laborCost;

// Step 3: Calculate Security and Logistics Premiums.
// Premiums are added as a percentage uplift on the construction subtotal.
const securityPremiumAmount = constructionSubtotal * (securityPremiumFactor - 1);
const logisticsPremiumAmount = constructionSubtotal * (logisticsDifficultyFactor - 1);
const securityLogisticsCost = securityPremiumAmount + logisticsPremiumAmount;

const subtotalAfterSecurityLogistics = constructionSubtotal + securityLogisticsCost;

// Step 4: Calculate Project Management Overhead and Contingency.
const projectManagementCost = subtotalAfterSecurityLogistics * (projectManagementOverheadPercentage / 100);
const subtotalAfterPM = subtotalAfterSecurityLogistics + projectManagementCost;

const contingencyCost = subtotalAfterPM * (contingencyPercentage / 100);

// Step 5: Calculate Total Estimated Cost.
const totalEstimatedCostUSD = subtotalAfterPM + contingencyCost;

// Calculate average cost per square meter of the *total zone area*.
const costPerSqMReconstructionUSD = zoneAreaSqM > 0 ? totalEstimatedCostUSD / zoneAreaSqM : 0;

return {
    totalEstimatedCostUSD: totalEstimatedCostUSD,
    materialsCostUSD: materialsCost,
    laborCostUSD: laborCost,
    securityLogisticsCostUSD: securityLogisticsCost,
    projectManagementContingencyCostUSD: projectManagementCost + contingencyCost,
    costPerSqMReconstructionUSD: costPerSqMReconstructionUSD
};
