const { plant_capacity_mw, plant_lifetime_years, capacity_factor_percent, capital_cost_per_kw, fixed_om_cost_per_kw_year, variable_om_cost_per_mwh, fuel_cost_per_mwh, decommissioning_cost_percent, carbon_intensity_tco2_per_mwh, discount_rate_percent } = inputs;

// Convert percentages to decimals
const capacityFactorDecimal = capacity_factor_percent / 100;
const decommissioningCostDecimal = decommissioning_cost_percent / 100;
const discountRateDecimal = discount_rate_percent / 100;

// Constants
const HOURS_PER_YEAR = 8760;
const KW_PER_MW = 1000;

// --- Step 1: Calculate Annual and Total Electricity Generation --- 
const annualElectricityGenerationMWh = plant_capacity_mw * KW_PER_MW * capacityFactorDecimal * HOURS_PER_YEAR / KW_PER_MW;
const totalElectricityGenerationMWh = annualElectricityGenerationMWh * plant_lifetime_years;

// Handle potential division by zero later if no generation or lifetime
if (totalElectricityGenerationMWh <= 0 || plant_lifetime_years <= 0) {
    return {
        total_lifecycle_cost: 0,
        levelized_cost_of_electricity: Infinity,
        total_lifecycle_emissions: 0,
        levelized_carbon_intensity: Infinity,
        annual_electricity_generation: 0,
        total_electricity_generation: 0
    };
}

// --- Step 2: Calculate Undiscounted Costs and Emissions --- 
const totalCapitalCost = plant_capacity_mw * KW_PER_MW * capital_cost_per_kw;
const totalFixedOMCostUndiscounted = plant_capacity_mw * KW_PER_MW * fixed_om_cost_per_kw_year * plant_lifetime_years;
const totalVariableOMCostUndiscounted = variable_om_cost_per_mwh * totalElectricityGenerationMWh;
const totalFuelCostUndiscounted = fuel_cost_per_mwh * totalElectricityGenerationMWh;
const totalDecommissioningCostUndiscounted = totalCapitalCost * decommissioningCostDecimal;

const totalUndiscountedLifecycleCost = totalCapitalCost +
    totalFixedOMCostUndiscounted +
    totalVariableOMCostUndiscounted +
    totalFuelCostUndiscounted +
    totalDecommissioningCostUndiscounted;

const totalUndiscountedLifecycleEmissions = carbon_intensity_tco2_per_mwh * totalElectricityGenerationMWh;

// --- Step 3: Calculate Net Present Value (NPV) of Costs and Generation for LCOE --- 
let npvOfOperatingCosts = 0;
let npvOfGeneration = 0;

// Annual operating expenses (fixed, variable, fuel)
const annualFixedOMCost = plant_capacity_mw * KW_PER_MW * fixed_om_cost_per_kw_year;
const annualVariableOMCost = variable_om_cost_per_mwh * annualElectricityGenerationMWh;
const annualFuelCost = fuel_cost_per_mwh * annualElectricityGenerationMWh;

// Loop through each year to calculate NPV. Capital cost is already at year 0 (present value).
// Decommissioning cost is typically incurred at the very end of the project's life.
if (discountRateDecimal > 0) {
    for (let i = 1; i <= plant_lifetime_years; i++) {
        const discountFactor = 1 / Math.pow(1 + discountRateDecimal, i);
        npvOfOperatingCosts += (annualFixedOMCost + annualVariableOMCost + annualFuelCost) * discountFactor;
        npvOfGeneration += annualElectricityGenerationMWh * discountFactor;
    }
    npvOfOperatingCosts += totalDecommissioningCostUndiscounted / Math.pow(1 + discountRateDecimal, plant_lifetime_years); // Decommissioning at end of life
} else { // Discount rate is 0, so NPV is just the sum of undiscounted values over the lifetime
    npvOfOperatingCosts = totalFixedOMCostUndiscounted + totalVariableOMCostUndiscounted + totalFuelCostUndiscounted + totalDecommissioningCostUndiscounted;
    npvOfGeneration = totalElectricityGenerationMWh;
}

const totalNpvOfCosts = totalCapitalCost + npvOfOperatingCosts;

// --- Step 4: Calculate LCOE and Levelized Carbon Intensity --- 
let levelizedCostOfElectricity;
if (npvOfGeneration > 0) {
    levelizedCostOfElectricity = totalNpvOfCosts / npvOfGeneration;
} else {
    levelizedCostOfElectricity = Infinity; 
}

let levelizedCarbonIntensity;
if (totalElectricityGenerationMWh > 0) {
    levelizedCarbonIntensity = totalUndiscountedLifecycleEmissions / totalElectricityGenerationMWh;
} else {
    levelizedCarbonIntensity = Infinity;
}

return {
    total_lifecycle_cost: totalUndiscountedLifecycleCost,
    levelized_cost_of_electricity: levelizedCostOfElectricity,
    total_lifecycle_emissions: totalUndiscountedLifecycleEmissions,
    levelized_carbon_intensity: levelizedCarbonIntensity,
    annual_electricity_generation: annualElectricityGenerationMWh,
    total_electricity_generation: totalElectricityGenerationMWh
};
