function calculate({ initialProbabilityEstimate, marketImpliedProbability, informationEdgeScore, marketLiquidityScore, betAmount, eventSensitivityScore, sourceCompromiseRisk }) {
    // Convert percentages to decimals
    const p_user = initialProbabilityEstimate / 100;
    const p_market = marketImpliedProbability / 100;

    // --- 1. Calculate Expected Financial Return (USD) ---
    let expectedReturn = 0;
    // Edge case: if market probability is too close to 0 or 1, adjust slightly to avoid division by zero or extreme payouts
    const p_market_adjusted_for_calc = Math.max(0.001, Math.min(0.999, p_market));

    if (p_user > p_market_adjusted_for_calc) {
        // User believes event is more likely than the market implies (betting 'Yes')
        // Payout for 'Yes' bet: betAmount / p_market
        const profitIfYes = (betAmount / p_market_adjusted_for_calc) - betAmount;
        const lossIfNo = betAmount; // Lose full bet amount if it doesn't happen
        expectedReturn = (p_user * profitIfYes) - ((1 - p_user) * lossIfNo);
    } else if (p_user < p_market_adjusted_for_calc) {
        // User believes event is less likely than the market implies (betting 'No')
        // Payout for 'No' bet: betAmount / (1 - p_market)
        const profitIfNo = (betAmount / (1 - p_market_adjusted_for_calc)) - betAmount;
        const lossIfYes = betAmount; // Lose full bet amount if it happens
        expectedReturn = ((1 - p_user) * profitIfNo) - (p_user * lossIfYes);
    }
    // If p_user == p_market, expected return remains 0.

    // Cap expected return to prevent extremely high values from tiny bet amounts / extreme probabilities
    expectedReturn = Math.max(-betAmount, Math.min(betAmount * 10, expectedReturn)); // Max 10x betAmount profit, min -betAmount loss

    // --- 2. Calculate Regulatory and Ethical Risk Scores (0-10) ---
    // Regulatory Risk: Primarily driven by event sensitivity
    let regulatoryRiskScore = eventSensitivityScore; // Direct mapping, can be refined

    // Ethical Risk: Driven by event sensitivity and the risk of source compromise
    let ethicalRiskScore = (eventSensitivityScore * 0.7 + sourceCompromiseRisk * 0.3); // Weighted average
    ethicalRiskScore = Math.min(10, Math.max(0, ethicalRiskScore)); // Ensure within 0-10 range

    // --- 3. Calculate Risk-Adjusted Return Percentage ---
    let rawReturnPercentage = (betAmount > 0) ? (expectedReturn / betAmount) * 100 : 0;

    // Risk factors (higher means more penalty)
    const normalizedRegulatoryRisk = regulatoryRiskScore / 10; // Scale 0-1
    const normalizedEthicalRisk = ethicalRiskScore / 10;     // Scale 0-1
    const normalizedSourceCompromiseRisk = sourceCompromiseRisk / 10; // Scale 0-1

    // Liquidity factor (higher means more boost)
    const normalizedLiquidity = marketLiquidityScore / 10;   // Scale 0-1

    // Combined risk penalty factor: higher risk reduces this
    const combinedRiskPenalty = (normalizedRegulatoryRisk * 0.3 + normalizedEthicalRisk * 0.5 + normalizedSourceCompromiseRisk * 0.2); // Ethical risk weighted higher
    const riskReductionFactor = Math.max(0, 1 - combinedRiskPenalty); // Cap at 0 to avoid negative risk multiplier

    // Liquidity boost: higher liquidity slightly boosts effective return
    const liquidityBoost = normalizedLiquidity * 0.2; // Max 20% boost for max liquidity

    let riskAdjustedReturnPercentage = rawReturnPercentage * riskReductionFactor * (1 + liquidityBoost);

    // Cap risk-adjusted return percentage
    riskAdjustedReturnPercentage = Math.max(-100, Math.min(1000, riskAdjustedReturnPercentage)); // Capped between -100% and 1000%

    // --- 4. Calculate Overall Opportunity Score (0-10) ---
    // Combines risk-adjusted return and information edge
    // Scale risk-adjusted return (e.g., max 100% is 10 score for raw return portion)
    const scaledRiskAdjustedReturn = Math.max(-10, Math.min(10, riskAdjustedReturnPercentage / 10)); // From -10 to +10
    const opportunityFromReturn = scaledRiskAdjustedReturn * 0.5; // Max contribution 5
    const opportunityFromEdge = informationEdgeScore * 0.5; // Max contribution 5

    let overallOpportunityScore = opportunityFromReturn + opportunityFromEdge;
    overallOpportunityScore = Math.min(10, Math.max(0, overallOpportunityScore)); // Cap between 0 and 10

    // --- 5. Calculate Overall Risk Score (0-10) ---
    // Average of relevant risk components, giving ethical/source compromise slightly more weight
    let overallRiskScore = (regulatoryRiskScore * 0.2 + ethicalRiskScore * 0.4 + sourceCompromiseRisk * 0.4);
    overallRiskScore = Math.min(10, Math.max(0, overallRiskScore)); // Cap between 0 and 10

    return {
        expectedReturn,
        riskAdjustedReturnPercentage,
        regulatoryRiskScore,
        ethicalRiskScore,
        overallOpportunityScore,
        overallRiskScore
    };
}
