function calculateRisk(inputs) {
    const {
        staffing_ratio_deviation,
        staff_turnover_rate,
        protocol_adherence_score,
        incident_reporting_rate,
        ehr_utilization_score,
        tech_integration_score,
        patient_feedback_sentiment,
        complaint_resolution_time
    } = inputs;

    let totalWeightedRisk = 0;

    // --- Normalization and Risk Factor Calculation (0-1 scale, 1 is highest risk) ---

    // 1. Staffing Ratio Deviation (0-30%, ideal 0%. Cap at 30% for risk calculation)
    // Max risk at 30% deviation. e.g., 30/30 = 1
    let risk_srd = Math.min(1, Math.max(0, staffing_ratio_deviation / 30));
    totalWeightedRisk += risk_srd * 0.15;

    // 2. Staff Turnover Rate (5-50%, ideal 5%. Values below 5% are treated as 5% for risk baseline)
    // Max risk at 50% turnover. (50-5)/45 = 1
    let risk_str = Math.min(1, Math.max(0, (staff_turnover_rate - 5) / 45));
    totalWeightedRisk += risk_str * 0.10;

    // 3. Protocol Adherence Score (50-100%, ideal 100%. Values above 100% are treated as 100%)
    // Max risk at 50% adherence. (100-50)/50 = 1
    let risk_pas = Math.min(1, Math.max(0, (100 - protocol_adherence_score) / 50));
    totalWeightedRisk += risk_pas * 0.20;

    // 4. Incident Reporting Rate (per 1000 patient days)
    // Optimal range 5-10. Risk increases outside this range. Values <0 are treated as 0, >20 as 20.
    let risk_irr = 0;
    const clamped_irr = Math.min(20, Math.max(0, incident_reporting_rate));
    if (clamped_irr < 5) {
        risk_irr = Math.min(1, (5 - clamped_irr) / 5); // max 1 at 0
    } else if (clamped_irr > 10) {
        risk_irr = Math.min(1, (clamped_irr - 10) / 10); // max 1 at 20
    }
    totalWeightedRisk += risk_irr * 0.15;

    // 5. EHR Utilization Score (50-100%, ideal 100%. Values above 100% are treated as 100%)
    // Max risk at 50% utilization. (100-50)/50 = 1
    let risk_eus = Math.min(1, Math.max(0, (100 - ehr_utilization_score) / 50));
    totalWeightedRisk += risk_eus * 0.10;

    // 6. Technology Integration Score (50-100%, ideal 100%. Values above 100% are treated as 100%)
    // Max risk at 50% integration. (100-50)/50 = 1
    let risk_tis = Math.min(1, Math.max(0, (100 - tech_integration_score) / 50));
    totalWeightedRisk += risk_tis * 0.10;

    // 7. Patient Feedback Sentiment (1-5, ideal 5. Values <1 are treated as 1, >5 as 5)
    // Max risk at 1. (5-1)/4 = 1
    let risk_pfs = Math.min(1, Math.max(0, (5 - patient_feedback_sentiment) / 4));
    totalWeightedRisk += risk_pfs * 0.10;

    // 8. Complaint Resolution Time (1-30 days, ideal 1-3 days. Values <1 are treated as 1, >30 as 30)
    // Max risk at 30 days. (30-3)/27 = 1
    let risk_crt = Math.min(1, Math.max(0, (complaint_resolution_time - 3) / 27));
    totalWeightedRisk += risk_crt * 0.10;

    // --- Final Risk Index Score (0-100) ---
    let risk_index_score = Math.round(totalWeightedRisk * 100);

    // Ensure score is within 0-100 range due to rounding/capping
    risk_index_score = Math.min(100, Math.max(0, risk_index_score));

    // --- Risk Level Categorization ---
    let risk_level;
    if (risk_index_score <= 25) {
        risk_level = "Low Risk";
    } else if (risk_index_score <= 50) {
        risk_level = "Moderate Risk";
    } else if (risk_index_score <= 75) {
        risk_level = "High Risk";
    } else {
        risk_level = "Critical Risk";
    }

    return {
        risk_index_score: risk_index_score,
        risk_level: risk_level
    };
}
