function calculateCriticalPath(inputs) {
  const pathDurations = [
    inputs.path1Duration || 0,
    inputs.path2Duration || 0,
    inputs.path3Duration || 0,
    inputs.path4Duration || 0
  ].filter(d => d >= 0);

  if (pathDurations.length === 0 || pathDurations.every(d => d === 0)) {
    return {
      criticalPathDuration: 0,
      criticalPathIdentified: 'N/A',
      totalProjectDuration: 0
    };
  }

  const maxDuration = Math.max(...pathDurations);

  let criticalPathName = '';
  if (maxDuration === inputs.path1Duration) criticalPathName = 'Path 1';
  else if (maxDuration === inputs.path2Duration) criticalPathName = 'Path 2';
  else if (maxDuration === inputs.path3Duration) criticalPathName = 'Path 3';
  else if (inputs.path4Duration !== null && inputs.path4Duration !== undefined && maxDuration === inputs.path4Duration) criticalPathName = 'Path 4';
  else criticalPathName = 'Multiple Paths (or N/A)'; // Fallback if multiple paths have max duration

  // Ensure contingency is treated as a percentage and is non-negative
  const contingencyFactor = 1 + (Math.max(0, inputs.contingencyPercentage) / 100);

  // Cap contingency factor to avoid excessively large numbers from user input errors, e.g., max 200% buffer
  const cappedContingencyFactor = Math.min(contingencyFactor, 3.0); 

  const totalProjectDuration = maxDuration * cappedContingencyFactor;

  return {
    criticalPathDuration: maxDuration,
    criticalPathIdentified: criticalPathName,
    totalProjectDuration: totalProjectDuration
  };
}
