function calculate(inputs) {
    const weights = {
        governmentStabilityRisk: 20,
        publicSentimentRisk: 15,
        stakeholderControversyRisk: 15,
        internationalCooperationRisk: 10,
        environmentalSocialRisk: 15,
        mediaScrutinyIntensity: 10,
        geopoliticalLocationSensitivity: 10,
        projectVisibility: 5
    };

    let rawScore = 0;
    for (const key in inputs) {
        const value = Number(inputs[key]);
        // Ensure inputs are within 1-5 range for calculation robustness
        const validatedValue = Math.max(1, Math.min(5, value)); 
        if (weights[key]) {
            rawScore += validatedValue * weights[key];
        }
    }

    // Min possible raw score (all inputs are 1) = 1 * sum_of_weights = 1 * 100 = 100
    const minRawScore = 100;
    // Max possible raw score (all inputs are 5) = 5 * sum_of_weights = 5 * 100 = 500
    const maxRawScore = 500;

    // Normalize to 0-100 scale
    const totalGeopoliticalReputationalRiskScore = ((rawScore - minRawScore) / (maxRawScore - minRawScore)) * 100;

    let riskCategory;
    if (totalGeopoliticalReputationalRiskScore <= 25) {
        riskCategory = "Low Risk";
    } else if (totalGeopoliticalReputationalRiskScore <= 50) {
        riskCategory = "Moderate Risk";
    } else if (totalGeopoliticalReputationalRiskScore <= 75) {
        riskCategory = "High Risk";
    } else {
        riskCategory = "Critical Risk";
    }

    return {
        totalGeopoliticalReputationalRiskScore: Math.round(totalGeopoliticalReputationalRiskScore),
        riskCategory: riskCategory
    };
}
