const elevation_above_floodplain_m = parseFloat(inputs.elevation_above_floodplain);
const proximity_to_major_water_body_m = parseFloat(inputs.proximity_to_major_water_body);
const historical_flood_events_count = parseFloat(inputs.historical_flood_events);
const local_rainfall_increase_projection_percent = parseFloat(inputs.local_rainfall_increase_projection);
const local_drainage_quality_scale = parseFloat(inputs.local_drainage_quality); // 1-5
const soil_permeability_index_scale = parseFloat(inputs.soil_permeability_index); // 1-5

let riskScore = 0;

// Component 1: Elevation (lower is higher risk)
// Max impact at or below 0m, decreasing risk up to 10m. Capped at 10m influence.
const elevationFactor = Math.max(0, 10 - elevation_above_floodplain_m); 
riskScore += elevationFactor * 8; // Max 80 (if elevation <= 0)

// Component 2: Proximity to Water (closer is higher risk)
// Significant impact if within 200m, diminishing thereafter. Min effective proximity 1m to avoid division by zero.
const effectiveProximity = Math.max(1, proximity_to_major_water_body_m);
const proximityFactor = Math.min(100, (200 / effectiveProximity) * 5); // Max 100 (if 1m), min ~5 (if 200m)
riskScore += proximityFactor * 0.8; // Max 80

// Component 3: Historical Flood Events (more events is higher risk)
// Each event adds risk. Capped at 10 events to prevent extreme scores from very high counts.
const cappedHistoricalEvents = Math.min(10, historical_flood_events_count);
riskScore += cappedHistoricalEvents * 10; // Max 100

// Component 4: Climate Change Projection (higher % increase is higher risk)
// Direct correlation. Capped at 30% to represent plausible extreme projections.
const cappedRainfallIncrease = Math.min(30, local_rainfall_increase_projection_percent);
riskScore += cappedRainfallIncrease * 3; // Max 90

// Component 5: Drainage Infrastructure Quality (lower quality is higher risk)
// Scale 1-5, transformed to risk factor (6 - value, so 1 -> 5, 5 -> 1). Lower quality yields higher factor.
const drainageFactor = (6 - local_drainage_quality_scale);
riskScore += drainageFactor * 12; // Max 60 (if quality is 1)

// Component 6: Soil Permeability (lower permeability is higher risk, indicative of runoff/mudslide risk)
// Scale 1-5, transformed to risk factor (6 - value, so 1 -> 5, 5 -> 1). Lower permeability yields higher factor.
const soilFactor = (6 - soil_permeability_index_scale);
riskScore += soilFactor * 10; // Max 50 (if permeability is 1)

// Total potential raw score: 80 (elevation) + 80 (proximity) + 100 (historical) + 90 (climate) + 60 (drainage) + 50 (soil) = 460
// Normalize the raw risk score to a 0-100 scale for the final Flood Risk Score.
const maxPossibleRawScore = 460;
let finalFloodRiskScore = Math.min(100, Math.round((riskScore / maxPossibleRawScore) * 100));

// Ensure the score is not negative
finalFloodRiskScore = Math.max(0, finalFloodRiskScore);

let riskCategory;
if (finalFloodRiskScore >= 80) {
    riskCategory = "Extreme";
} else if (finalFloodRiskScore >= 60) {
    riskCategory = "High";
} else if (finalFloodRiskScore >= 40) {
    riskCategory = "Moderate";
} else if (finalFloodRiskScore >= 20) {
    riskCategory = "Low";
} else {
    riskCategory = "Very Low";
}

return {
    flood_risk_score: finalFloodRiskScore,
    flood_risk_category: riskCategory
};
