const minTemperatureForecast = inputs.minTemperatureForecast; 
const freezeDurationHours = inputs.freezeDurationHours; 
const exteriorWallRValue = inputs.exteriorWallRValue; 
const atticRValue = inputs.atticRValue; 
const crawlSpaceInsulated = inputs.crawlSpaceInsulated === 'Yes'; 
const exposedPipeLength = inputs.exposedPipeLength; 
const thermostatSettingDuringFreeze = inputs.thermostatSettingDuringFreeze; 
const propertySquareFootage = inputs.propertySquareFootage; 
const hasSmartMonitoring = inputs.hasSmartMonitoring === 'Yes'; 

const CRITICAL_FREEZE_TEMP = 28; 
const SEVERE_FREEZE_TEMP = 15; 
const EXTREME_FREEZE_TEMP = 0; 

const DURATION_WEIGHT = 0.005; 
const SEVERE_DURATION_MULTIPLIER = 1.8; 
const EXTREME_DURATION_MULTIPLIER = 3.0; 

const R_VALUE_BASE_EFFECT = 0.005; 
const PIPE_EXPOSURE_BASE_RISK_PER_FOOT = 0.8; 
const THERMOSTAT_CRITICAL_TEMP = 35; 
const THERMOSTAT_LOW_TEMP = 50; 
const THERMOSTAT_RISK_MULTIPLIER_CRITICAL = 2.5; 
const THERMOSTAT_RISK_MULTIPLIER_LOW = 1.5; 

const CRAWL_SPACE_PENALTY = 20; 
const SMART_MONITORING_REDUCTION_FACTOR = 0.25; 

const BASE_BURST_PROBABILITY_OFFSET = 5; 

const MIN_REPAIR_COST_FLOOD = 5000; 
const MAX_REPAIR_COST_FLOOD = 50000; 
const COST_PER_SQFT_BASE = 5; 
const COST_PER_SQFT_SEVERE = 25; 
const PIPE_REPAIR_COST_PER_FOOT_LOW = 50; 
const PIPE_REPAIR_COST_PER_FOOT_HIGH = 150; 

let coldStressScore = 0; 
if (minTemperatureForecast < CRITICAL_FREEZE_TEMP && freezeDurationHours > 0) { 
    let effectiveDuration = freezeDurationHours; 
    if (minTemperatureForecast < EXTREME_FREEZE_TEMP) { 
        effectiveDuration *= EXTREME_DURATION_MULTIPLIER; 
    } else if (minTemperatureForecast < SEVERE_FREEZE_TEMP) { 
        effectiveDuration *= SEVERE_DURATION_MULTIPLIER; 
    } 
    coldStressScore = (CRITICAL_FREEZE_TEMP - minTemperatureForecast) * effectiveDuration * DURATION_WEIGHT; 
} 
coldStressScore = Math.min(coldStressScore, 100); 

let insulationRiskReduction = 0; 
insulationRiskReduction += Math.max(0, (exteriorWallRValue - 10) * R_VALUE_BASE_EFFECT); 
insulationRiskReduction += Math.max(0, (atticRValue - 20) * R_VALUE_BASE_EFFECT); 

if (!crawlSpaceInsulated) { 
    coldStressScore += CRAWL_SPACE_PENALTY; 
} 

coldStressScore = Math.max(0, coldStressScore - insulationRiskReduction); 

coldStressScore += exposedPipeLength * PIPE_EXPOSURE_BASE_RISK_PER_FOOT; 

if (thermostatSettingDuringFreeze < THERMOSTAT_CRITICAL_TEMP) { 
    coldStressScore *= THERMOSTAT_RISK_MULTIPLIER_CRITICAL; 
} else if (thermostatSettingDuringFreeze < THERMOSTAT_LOW_TEMP) { 
    coldStressScore *= THERMOSTAT_RISK_MULTIPLIER_LOW; 
} 

if (hasSmartMonitoring) { 
    coldStressScore *= (1 - SMART_MONITORING_REDUCTION_FACTOR); 
} 

coldStressScore = Math.min(Math.max(0, coldStressScore), 250); 

let burstPipeProbability = BASE_BURST_PROBABILITY_OFFSET; 
if (coldStressScore > 0) { 
    burstPipeProbability += (100 - BASE_BURST_PROBABILITY_OFFSET) / (1 + Math.exp(-0.05 * (coldStressScore - 50))); 
} 
burstPipeProbability = Math.min(Math.max(0, burstPipeProbability), 99.9); 

let riskLevel = 'Low'; 
if (burstPipeProbability >= 70) { 
    riskLevel = 'Severe'; 
} else if (burstPipeProbability >= 40) { 
    riskLevel = 'High'; 
} else if (burstPipeProbability >= 15) { 
    riskLevel = 'Moderate'; 
} 

let estimatedCostLow = 0; 
let estimatedCostHigh = 0; 

if (burstPipeProbability > 0) { 
    estimatedCostLow = MIN_REPAIR_COST_FLOOD * (burstPipeProbability / 100); 
    estimatedCostHigh = MAX_REPAIR_COST_FLOOD * (burstPipeProbability / 100); 

    estimatedCostLow += propertySquareFootage * COST_PER_SQFT_BASE * (burstPipeProbability / 100); 
    estimatedCostHigh += propertySquareFootage * COST_PER_SQFT_SEVERE * (burstPipeProbability / 100); 

    estimatedCostLow += exposedPipeLength * PIPE_REPAIR_COST_PER_FOOT_LOW * (burstPipeProbability / 100); 
    estimatedCostHigh += exposedPipeLength * PIPE_REPAIR_COST_PER_FOOT_HIGH * (burstPipeProbability / 100) * (riskLevel === 'Severe' ? 1.5 : 1); 

    if (riskLevel === 'Moderate') { 
        estimatedCostLow = Math.max(estimatedCostLow, 3000); 
        estimatedCostHigh = Math.max(estimatedCostHigh, 15000); 
    } else if (riskLevel === 'High') { 
        estimatedCostLow = Math.max(estimatedCostLow, 8000); 
        estimatedCostHigh = Math.max(estimatedCostHigh, 35000); 
    } else if (riskLevel === 'Severe') { 
        estimatedCostLow = Math.max(estimatedCostLow, 15000); 
        estimatedCostHigh = Math.max(estimatedCostHigh, 60000); 
    } 

    if (burstPipeProbability > BASE_BURST_PROBABILITY_OFFSET && estimatedCostLow < 1000) { 
        estimatedCostLow = 1000; 
        estimatedCostHigh = 3000; 
    } 
} 

estimatedCostLow = Math.round(estimatedCostLow / 100) * 100; 
estimatedCostHigh = Math.round(estimatedCostHigh / 100) * 100; 

let estimatedCostRange = `$${estimatedCostLow.toLocaleString()} - $${estimatedCostHigh.toLocaleString()}`; 
if (estimatedCostLow === 0 && estimatedCostHigh === 0) {
    estimatedCostRange = "Minimal / No Cost Expected";
} else if (estimatedCostLow === estimatedCostHigh) {
    estimatedCostRange = `$${estimatedCostLow.toLocaleString()}`;
}

return { 
    riskLevel: riskLevel, 
    burstPipeProbability: burstPipeProbability, 
    estimatedCostRange: estimatedCostRange 
};
